import React, { useMemo } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  ChartOptions,
  TooltipItem
} from 'chart.js';
import { ProcessedArmor3Stat, ProcessedBenefit } from '../types/armor3-types';
import styles from './Armor3Chart.m.scss';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

interface Props {
  stat: ProcessedArmor3Stat;
  showBreakpoints: boolean;
  showPvP: boolean;
  currentStatValue?: number;
  onPointClick?: (point: any) => void;
}

// Expanded color palette with unique distinct colors for each benefit
const distinctColors = [
  '#00E676', // Bright Green
  '#2196F3', // Blue  
  '#FF5722', // Deep Orange
  '#9C27B0', // Purple
  '#FFC107', // Amber
  '#00BCD4', // Cyan
  '#4CAF50', // Green
  '#FF9800', // Orange
  '#E91E63', // Pink
  '#3F51B5', // Indigo
  '#FFEB3B', // Yellow
  '#795548', // Brown
  '#607D8B', // Blue Grey
  '#F44336', // Red
  '#8BC34A', // Light Green
  '#673AB7', // Deep Purple
  '#FF6F00', // Dark Orange
  '#1976D2', // Dark Blue
  '#D32F2F', // Dark Red
  '#388E3C'  // Dark Green
];

export default function Armor3Chart({
  stat,
  showBreakpoints,
  showPvP,
  currentStatValue,
  onPointClick
}: Props) {
  const chartData = useMemo(() => {
    const labels = Array.from({ length: 201 }, (_, i) => i.toString());
    const datasets: any[] = [];
    
    // Helper to create dataset for a benefit
    const createBenefitDataset = (benefit: ProcessedBenefit, statName: string, color: string, isDashed = false) => {
      // Filter values based on PvE/PvP selection
      const values = showPvP && benefit.pvpValues ? benefit.pvpValues : benefit.values;
      
      return {
        label: benefit.name, // Simplified label since legend is disabled
        data: values,
        borderColor: color,
        backgroundColor: `${color  }20`,
        borderWidth: 2,
        borderDash: isDashed ? [5, 5] : [],
        pointRadius: 0,
        pointHoverRadius: 6,
        tension: 0.1
      };
    };
    
    let colorIndex = 0;
    
    // Add primary effects datasets (always show all)
    stat.primaryEffects.forEach((benefit) => {
      datasets.push(createBenefitDataset(
        benefit, 
        stat.name, 
        distinctColors[colorIndex % distinctColors.length]
      ));
      colorIndex++;
    });
    
    // Add enhanced effects datasets (always show all)
    stat.enhancedEffects.forEach((benefit) => {
      datasets.push(createBenefitDataset(
        benefit, 
        stat.name, 
        distinctColors[colorIndex % distinctColors.length]
      ));
      colorIndex++;
    });
    
    
    return { labels, datasets };
  }, [stat, showPvP]);
  
  const chartOptions: ChartOptions<'line'> = {
    responsive: true,
    maintainAspectRatio: false,
    interaction: {
      mode: 'index',
      intersect: false,
    },
    onClick: (event, elements) => {
      if (elements.length > 0 && onPointClick) {
        const element = elements[0];
        const statValue = parseInt(chartData.labels[element.index]);
        
        // Find breakpoint at this stat value
        const breakpoint = stat.breakpoints?.find(bp => bp.statValue === statValue);
        if (breakpoint) {
          onPointClick({
            statValue,
            benefitName: breakpoint.description,
            description: breakpoint.description
          });
        }
      }
    },
    plugins: {
      legend: {
        display: false // Disable Chart.js built-in legend since we have our own below
      },
      title: {
        display: false
      },
      tooltip: {
        backgroundColor: 'rgba(0, 0, 0, 0.9)',
        titleColor: '#fff',
        bodyColor: '#fff',
        borderColor: '#333',
        borderWidth: 1,
        padding: 12,
        displayColors: true,
        callbacks: {
          title: (items: TooltipItem<'line'>[]) => {
            const statValue = parseInt(items[0].label || '0');
            return `Stat Value: ${statValue}`;
          },
          afterTitle: (items: TooltipItem<'line'>[]) => {
            const statValue = parseInt(items[0].label || '0');
            const activeBreakpoints = stat.breakpoints ? stat.breakpoints.filter(bp => bp.statValue === statValue) : [];
            if (activeBreakpoints.length > 0) {
              return `\nBreakpoint: ${  activeBreakpoints.map(bp => bp.description).join('\n')}`;
            }
            return '';
          }
        }
      }
    },
    scales: {
      x: {
        min: 0,
        max: 200,
        grid: {
          display: true,
          drawOnChartArea: true,
          color: (context) => {
            const value = context.tick.value;
            if (currentStatValue !== undefined && value === currentStatValue) {
              return 'rgba(79, 195, 247, 0.9)'; // Blue for current stat
            }
            if (value === 100) {
              return 'rgba(255, 193, 7, 0.9)'; // Gold color for tier boundary  
            }
            return 'rgba(255, 255, 255, 0.15)';
          },
          lineWidth: (context) => {
            const value = context.tick.value;
            if (currentStatValue !== undefined && value === currentStatValue) {
              return 3; // Current stat position
            }
            if (value === 100) {
              return 4; // Strong threshold between primary and enhanced
            }
            return value % 20 === 0 ? 1.5 : 0.8;
          }
        },
        ticks: {
          // Generate custom ticks that include current stat value
          callback: function(value: any, index: any, ticks: any) {
            const num = parseInt(value);
            // Always show current stat value and tier boundary
            if (currentStatValue !== undefined && num === currentStatValue) {return num;}
            if (num === 100) {return num;}
            // Show major intervals
            if (num % 20 === 0) {return num;}
            return '';
          },
          color: (context) => {
            const value = context.tick.value;
            if (currentStatValue !== undefined && value === currentStatValue) {
              return '#4FC3F7'; // Blue for current stat
            }
            if (value === 100) {
              return '#FFC107'; // Gold for tier boundary
            }
            return '#aaa';
          }
        },
        // Force specific tick values to be included
        afterBuildTicks: function(scale: any) {
          const ticks = [];
          const ticksToInclude = new Set();
          
          // Always include these key values
          ticksToInclude.add(0);
          ticksToInclude.add(100);
          ticksToInclude.add(200);
          
          // Include current stat value if defined
          if (currentStatValue !== undefined) {
            ticksToInclude.add(currentStatValue);
          }
          
          // Include major intervals
          for (let i = 0; i <= 200; i += 20) {
            ticksToInclude.add(i);
          }
          
          // Include breakpoints if they exist
          if (showBreakpoints && stat.breakpoints) {
            stat.breakpoints.forEach(bp => ticksToInclude.add(bp.statValue));
          }
          
          // Convert to sorted array and create tick objects
          const sortedValues = Array.from(ticksToInclude).sort((a, b) => a - b);
          scale.ticks = sortedValues.map(value => ({ value }));
        }
      },
      y: {
        grid: {
          color: 'rgba(255, 255, 255, 0.1)'
        },
        ticks: {
          color: '#aaa',
          callback: function(value: any) {
            // Format based on the first visible dataset's unit
            const visibleDataset = chartData.datasets.find(d => !d.hidden);
            if (visibleDataset) {
              const benefit = [...stat.primaryEffects, ...stat.enhancedEffects]
                .find(b => visibleDataset.label?.includes(b.name));
              if (benefit?.unit === '%') {
                return `${value  }%`;
              }
            }
            return value;
          }
        }
      }
    },
    elements: {
      line: {
        borderWidth: 2
      }
    }
  };
  
  
  return (
    <div className={styles.chartContainer}>
      <div className={styles.chartHeader}>
        <h3 className={styles.chartTitle}>
          {stat.name} Stat Benefits
          {showPvP && <span className={styles.pvpIndicator}>PvP Values</span>}
        </h3>
      </div>
      <div className={styles.chart}>
        <Line data={chartData} options={chartOptions} />
      </div>
      
      {/* Vertical Legend List */}
      <div className={styles.legendList}>
        {/* Show each benefit with its unique color and current value */}
        {stat.primaryEffects.map((benefit, idx) => {
          const currentBenefitValue = currentStatValue !== undefined ? benefit.values[currentStatValue] || 0 : 0;
          const formattedValue = benefit.unit === '%' 
            ? `${currentBenefitValue.toFixed(1)}%`
            : `${currentBenefitValue.toFixed(1)}${benefit.unit || ''}`;
          
          return (
            <div key={`primary-${benefit.name}`} className={styles.legendItem}>
              <span 
                className={styles.legendDot} 
                style={{ backgroundColor: distinctColors[idx % distinctColors.length] }}
              />
              <span className={styles.benefitName}>{benefit.name}</span>
              <span className={styles.benefitValue}>{formattedValue}</span>
            </div>
          );
        })}
        {stat.enhancedEffects.map((benefit, idx) => {
          const currentBenefitValue = currentStatValue !== undefined ? benefit.values[currentStatValue] || 0 : 0;
          const formattedValue = benefit.unit === '%' 
            ? `${currentBenefitValue.toFixed(1)}%`
            : `${currentBenefitValue.toFixed(1)}${benefit.unit || ''}`;
          
          return (
            <div key={`enhanced-${benefit.name}`} className={styles.legendItem}>
              <span 
                className={styles.legendDot} 
                style={{ backgroundColor: distinctColors[(stat.primaryEffects.length + idx) % distinctColors.length] }}
              />
              <span className={styles.benefitName}>{benefit.name}</span>
              <span className={styles.benefitValue}>{formattedValue}</span>
            </div>
          );
        })}
      </div>
      
    </div>
  );
}

import React from 'react';
import { ProcessedArmor3Stat, ProcessedBenefit } from '../types/armor3-types';
import styles from './Armor3StatDisplay.m.scss';

interface Props {
  stat: ProcessedArmor3Stat;
  currentValue: number;
  showPvP: boolean;
  highlightBreakpoints: boolean;
  compareValue?: number;
}

export default function Armor3StatDisplay({ 
  stat, 
  currentValue, 
  showPvP, 
  highlightBreakpoints,
  compareValue 
}: Props) {
  // Find active breakpoints
  const activeBreakpoints = stat.breakpoints.filter(bp => bp.statValue <= currentValue);
  const nextBreakpoint = stat.breakpoints.find(bp => bp.statValue > currentValue);
  
  // Determine which effects are active
  const isPrimaryActive = currentValue > 0 && currentValue <= 100;
  const isEnhancedActive = currentValue > 100;
  
  return (
    <div className={styles.statDisplay}>
      <div className={styles.header}>
        <h3 className={styles.statName}>{stat.name}</h3>
        <div className={styles.currentValue}>
          <span className={styles.value}>{currentValue}</span>
          {compareValue !== undefined && compareValue !== currentValue && (
            <span className={styles.compareValue}>
              ({compareValue > currentValue ? '+' : ''}{compareValue - currentValue})
            </span>
          )}
        </div>
      </div>
      
      <p className={styles.description}>{stat.description}</p>
      
      {/* Primary Effects Section */}
      <div className={`${styles.effectSection} ${isPrimaryActive ? styles.active : ''}`}>
        <h4 className={styles.sectionTitle}>
          Primary Effects (0-100)
          {currentValue > 100 && <span className={styles.maxed}>MAXED</span>}
        </h4>
        <div className={styles.benefits}>
          {stat.primaryEffects.map((benefit, idx) => (
            <BenefitDisplay
              key={idx}
              benefit={benefit}
              currentValue={Math.min(currentValue, 100)}
              showPvP={showPvP}
              isActive={isPrimaryActive || currentValue > 100}
            />
          ))}
        </div>
      </div>
      
      {/* Enhanced Effects Section */}
      <div className={`${styles.effectSection} ${isEnhancedActive ? styles.active : styles.locked}`}>
        <h4 className={styles.sectionTitle}>
          Enhanced Effects (101-200)
          {currentValue <= 100 && <span className={styles.locked}>LOCKED</span>}
        </h4>
        <div className={styles.benefits}>
          {stat.enhancedEffects.map((benefit, idx) => (
            <BenefitDisplay
              key={idx}
              benefit={benefit}
              currentValue={currentValue}
              showPvP={showPvP}
              isActive={isEnhancedActive}
            />
          ))}
        </div>
      </div>
      
      {/* Breakpoints Section */}
      {highlightBreakpoints && (
        <div className={styles.breakpoints}>
          <h4 className={styles.sectionTitle}>Key Breakpoints</h4>
          <div className={styles.breakpointList}>
            {activeBreakpoints.map((bp, idx) => (
              <div key={idx} className={`${styles.breakpoint} ${styles[bp.importance]}`}>
                <span className={styles.bpValue}>{bp.statValue}</span>
                <span className={styles.bpDesc}>{bp.description}</span>
              </div>
            ))}
            {nextBreakpoint && (
              <div className={`${styles.breakpoint} ${styles.next}`}>
                <span className={styles.bpValue}>{nextBreakpoint.statValue}</span>
                <span className={styles.bpDesc}>{nextBreakpoint.description}</span>
                <span className={styles.pointsNeeded}>
                  (+{nextBreakpoint.statValue - currentValue} needed)
                </span>
              </div>
            )}
          </div>
        </div>
      )}
      
      {/* Recommendations */}
      {stat.recommendations.length > 0 && (
        <div className={styles.recommendations}>
          <h4 className={styles.sectionTitle}>Build Recommendations</h4>
          {stat.recommendations
            .filter(rec => currentValue >= rec.minValue - 10 && currentValue <= rec.maxValue + 10)
            .map((rec, idx) => (
              <div key={idx} className={`${styles.recommendation} ${styles[rec.priority]}`}>
                <span className={styles.buildType}>{rec.buildType}</span>
                <span className={styles.range}>{rec.minValue}-{rec.maxValue}</span>
                <p className={styles.reasoning}>{rec.reasoning}</p>
              </div>
            ))}
        </div>
      )}
    </div>
  );
}

// Sub-component for individual benefit display
function BenefitDisplay({ 
  benefit, 
  currentValue, 
  showPvP, 
  isActive 
}: { 
  benefit: ProcessedBenefit;
  currentValue: number;
  showPvP: boolean;
  isActive: boolean;
}) {
  // Calculate the actual value based on current stat
  const effectiveValue = currentValue >= benefit.range[0] && currentValue <= benefit.range[1]
    ? benefit.values[currentValue]
    : currentValue > benefit.range[1] 
      ? benefit.values[benefit.range[1]]
      : 0;
  
  const showBenefit = (!benefit.isPvE || !showPvP) || (benefit.isPvE && !benefit.isPvP);
  
  if (!showBenefit && !showPvP) {return null;}
  
  return (
    <div className={`${styles.benefit} ${!isActive ? styles.inactive : ''}`}>
      <div className={styles.benefitHeader}>
        <span className={styles.benefitName}>{benefit.name}</span>
        {benefit.isPvE && benefit.isPvP && (
          <span className={styles.pveTags}>PvE/PvP</span>
        )}
        {benefit.isPvE && !benefit.isPvP && (
          <span className={styles.pveTag}>PvE</span>
        )}
        {!benefit.isPvE && benefit.isPvP && (
          <span className={styles.pvpTag}>PvP</span>
        )}
      </div>
      <div className={styles.benefitValue}>
        <span className={styles.current}>
          {effectiveValue > 0 ? '+' : ''}{effectiveValue.toFixed(1)}{benefit.unit}
        </span>
        <span className={styles.max}>
          / {benefit.maxValue}{benefit.unit}
        </span>
      </div>
      {benefit.description && (
        <p className={styles.benefitNote}>{benefit.description}</p>
      )}
      <div className={styles.progressBar}>
        <div 
          className={styles.progress}
          style={{ 
            width: `${(effectiveValue / benefit.maxValue) * 100}%` 
          }}
        />
      </div>
    </div>
  );
}

import React, { useState, useMemo } from 'react';
import { ProcessedArmor3Stat, BuildConstraints } from '../types/armor3-types';
import { analyzeStatDistribution } from '../utils/armor3-parser';
import { calculateEfficiencyScore } from '../utils/armor3-calculations';
import styles from './BuildOptimizer.m.scss';

interface Props {
  stats: ProcessedArmor3Stat[];
  currentValues: { [statId: string]: number };
  onStatChange: (statId: string, value: number) => void;
}

export default function BuildOptimizer({ stats, currentValues, onStatChange }: Props) {
  const [buildType, setBuildType] = useState<BuildConstraints['buildType']>('pve-dps');
  const [totalPoints, setTotalPoints] = useState(550);
  
  // Analyze current distribution
  const analysis = useMemo(() => 
    analyzeStatDistribution(currentValues), 
    [currentValues]
  );
  
  // Build type presets
  const buildPresets = {
    'pve-dps': {
      name: 'PvE DPS',
      description: 'Maximize damage output for raids and dungeons',
      priorities: ['weapon', 'grenade', 'super'],
      breakpoints: [
        { statId: 'weapon', minValue: 100 },
        { statId: 'grenade', minValue: 70 },
        { statId: 'super', minValue: 30 }
      ]
    },
    'pve-survivability': {
      name: 'PvE Tank',
      description: 'Maximum survivability for difficult content',
      priorities: ['health', 'class', 'melee'],
      breakpoints: [
        { statId: 'health', minValue: 100 },
        { statId: 'class', minValue: 100 }
      ]
    },
    'pvp-aggressive': {
      name: 'PvP Aggressive',
      description: 'Fast-paced aggressive PvP playstyle',
      priorities: ['melee', 'class', 'weapon'],
      breakpoints: [
        { statId: 'melee', minValue: 70 },
        { statId: 'class', minValue: 70 }
      ]
    },
    'pvp-defensive': {
      name: 'PvP Defensive',
      description: 'Defensive PvP build with high survivability',
      priorities: ['health', 'class', 'grenade'],
      breakpoints: [
        { statId: 'health', minValue: 60 },
        { statId: 'class', minValue: 100 }
      ]
    },
    'hybrid': {
      name: 'Balanced',
      description: 'Versatile build for all activities',
      priorities: ['health', 'weapon', 'grenade'],
      breakpoints: [
        { statId: 'health', minValue: 50 },
        { statId: 'weapon', minValue: 50 },
        { statId: 'grenade', minValue: 70 }
      ]
    }
  };
  
  const currentPreset = buildPresets[buildType];
  
  // Optimize distribution
  const optimizeDistribution = () => {
    const result: { [statId: string]: number } = {};
    let remainingPoints = totalPoints;
    
    // First, meet all breakpoint requirements
    for (const breakpoint of currentPreset.breakpoints) {
      result[breakpoint.statId] = breakpoint.minValue;
      remainingPoints -= breakpoint.minValue;
    }
    
    // Initialize other stats to 0
    for (const stat of stats) {
      if (!result[stat.id]) {
        result[stat.id] = 0;
      }
    }
    
    // Distribute remaining points based on priorities
    while (remainingPoints > 0 && currentPreset.priorities.length > 0) {
      for (const priorityStat of currentPreset.priorities) {
        if (remainingPoints <= 0) {break;}
        
        const currentValue = result[priorityStat] || 0;
        
        // Find next valuable breakpoint for this stat
        const stat = stats.find(s => s.id === priorityStat);
        if (!stat) {continue;}
        
        // Determine optimal investment
        let targetValue = currentValue;
        
        // Key thresholds to aim for
        const thresholds = [30, 70, 80, 100, 150, 200];
        for (const threshold of thresholds) {
          if (threshold > currentValue && threshold <= currentValue + remainingPoints) {
            targetValue = threshold;
            break;
          }
        }
        
        // If no threshold found, add up to 20 points
        if (targetValue === currentValue && currentValue < 200) {
          targetValue = Math.min(200, currentValue + Math.min(20, remainingPoints));
        }
        
        const pointsToAdd = targetValue - currentValue;
        if (pointsToAdd > 0) {
          result[priorityStat] = targetValue;
          remainingPoints -= pointsToAdd;
        }
      }
    }
    
    // Update the stat values
    for (const [statId, value] of Object.entries(result)) {
      onStatChange(statId, value);
    }
  };
  
  // Calculate efficiency for each stat
  const statEfficiencies = useMemo(() => {
    const efficiencies: { [statId: string]: number } = {};
    for (const stat of stats) {
      const benefits = [...stat.primaryEffects, ...stat.enhancedEffects];
      efficiencies[stat.id] = calculateEfficiencyScore(currentValues[stat.id], benefits.map(b => ({
        name: b.name,
        range: b.range[0] === 0 ? '0-100' : '101-200',
        scaling_model: b.scalingType as any,
        max_value: b.maxValue,
        unit: b.unit,
        pve_only: b.isPvE && !b.isPvP
      })));
    }
    return efficiencies;
  }, [stats, currentValues]);
  
  return (
    <div className={styles.buildOptimizer}>
      <div className={styles.header}>
        <h2>Build Optimizer</h2>
        <p>Optimize your stat distribution for specific playstyles</p>
      </div>
      
      {/* Build Type Selector */}
      <div className={styles.buildTypeSection}>
        <h3>Select Build Type</h3>
        <div className={styles.buildTypes}>
          {Object.entries(buildPresets).map(([key, preset]) => (
            <div
              key={key}
              className={`${styles.buildType} ${buildType === key ? styles.selected : ''}`}
              onClick={() => setBuildType(key as BuildConstraints['buildType'])}
            >
              <h4>{preset.name}</h4>
              <p>{preset.description}</p>
            </div>
          ))}
        </div>
      </div>
      
      {/* Total Points Control */}
      <div className={styles.pointsControl}>
        <label>
          <span>Total Stat Points Available</span>
          <input
            type="number"
            min="300"
            max="700"
            value={totalPoints}
            onChange={(e) => setTotalPoints(parseInt(e.target.value) || 550)}
          />
          <span className={styles.hint}>Typical range: 550-600 with mods</span>
        </label>
      </div>
      
      {/* Current Distribution */}
      <div className={styles.currentDistribution}>
        <h3>Current Distribution</h3>
        <div className={styles.statGrid}>
          {stats.map(stat => (
            <div key={stat.id} className={styles.statRow}>
              <div className={styles.statInfo}>
                <span className={styles.statName}>{stat.name}</span>
                <span className={styles.efficiency}>
                  Efficiency: {statEfficiencies[stat.id]?.toFixed(0)}%
                </span>
              </div>
              <div className={styles.statControl}>
                <input
                  type="range"
                  min="0"
                  max="200"
                  value={currentValues[stat.id]}
                  onChange={(e) => onStatChange(stat.id, parseInt(e.target.value))}
                  className={styles.statSlider}
                />
                <input
                  type="number"
                  min="0"
                  max="200"
                  value={currentValues[stat.id]}
                  onChange={(e) => onStatChange(stat.id, parseInt(e.target.value) || 0)}
                  className={styles.statInput}
                />
              </div>
              {/* Breakpoint indicators */}
              <div className={styles.breakpoints}>
                {stat.breakpoints
                  .filter(bp => bp.importance === 'critical' || bp.importance === 'major')
                  .map((bp, idx) => (
                    <span
                      key={idx}
                      className={`${styles.breakpoint} ${
                        currentValues[stat.id] >= bp.statValue ? styles.reached : ''
                      }`}
                      title={bp.description}
                    >
                      {bp.statValue}
                    </span>
                  ))}
              </div>
            </div>
          ))}
        </div>
        
        {/* Total and Analysis */}
        <div className={styles.analysisSection}>
          <div className={styles.totalDisplay}>
            <span>Total Points Used:</span>
            <span className={`${styles.totalValue} ${
              analysis.totalPoints > totalPoints ? styles.exceeded : ''
            }`}>
              {analysis.totalPoints} / {totalPoints}
            </span>
          </div>
          
          {/* Warnings */}
          {analysis.warnings.length > 0 && (
            <div className={styles.warnings}>
              <h4>Warnings</h4>
              {analysis.warnings.map((warning, idx) => (
                <div key={idx} className={styles.warning}>
                  <span className="fa fa-warning" />
                  {warning}
                </div>
              ))}
            </div>
          )}
          
          {/* Suggestions */}
          {analysis.suggestions.length > 0 && (
            <div className={styles.suggestions}>
              <h4>Suggestions</h4>
              {analysis.suggestions.map((suggestion, idx) => (
                <div key={idx} className={styles.suggestion}>
                  <span className="fa fa-lightbulb-o" />
                  {suggestion}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
      
      {/* Optimization Actions */}
      <div className={styles.actions}>
        <button
          className={styles.optimizeButton}
          onClick={optimizeDistribution}
        >
          <span className="fa fa-magic" />
          Optimize for {currentPreset.name}
        </button>
        
        <button
          className={styles.resetButton}
          onClick={() => {
            for (const stat of stats) {
              onStatChange(stat.id, 50);
            }
          }}
        >
          <span className="fa fa-refresh" />
          Reset All
        </button>
      </div>
      
      {/* Build Requirements */}
      <div className={styles.requirements}>
        <h3>Build Requirements</h3>
        <div className={styles.requirementsList}>
          <div className={styles.priorities}>
            <h4>Priority Stats</h4>
            <ol>
              {currentPreset.priorities.map(statId => (
                <li key={statId}>
                  {stats.find(s => s.id === statId)?.name || statId}
                </li>
              ))}
            </ol>
          </div>
          
          <div className={styles.minimums}>
            <h4>Minimum Breakpoints</h4>
            {currentPreset.breakpoints.map((bp, idx) => (
              <div key={idx} className={styles.requirement}>
                <span>{stats.find(s => s.id === bp.statId)?.name}: </span>
                <span className={styles.reqValue}>{bp.minValue}+</span>
                {currentValues[bp.statId] >= bp.minValue ? (
                  <span className={styles.met}>âœ“</span>
                ) : (
                  <span className={styles.notMet}>âœ—</span>
                )}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

import { t } from 'app/i18next-t';
import { ChartOptions } from '../types/armor-types';
import styles from './ChartControls.m.scss';

interface Props {
  options: ChartOptions;
  onChange: (options: Partial<ChartOptions>) => void;
  maxValue: number;
}

export default function ChartControls({ options, onChange, maxValue }: Props) {
  const handleRangeChange = (field: 'startValue' | 'endValue', value: number) => {
    if (field === 'startValue' && value < options.endValue) {
      onChange({ [field]: value });
    } else if (field === 'endValue' && value > options.startValue) {
      onChange({ [field]: value });
    }
  };

  return (
    <div className={styles.chartControls}>
      <div className={styles.controlGroup}>
        <label>{(t as any)('ArmorAnalysis.DisplayOptions', 'Display Options')}</label>

        <div className={styles.toggles}>
          <button
            className={`${styles.toggle} ${options.showOnlyChangedRange ? styles.active : ''}`}
            onClick={() => onChange({ showOnlyChangedRange: !options.showOnlyChangedRange })}
            title={(t as any)('ArmorAnalysis.ShowActiveRange', 'Show only active range')}
          >
            <span className="fa fa-compress" />
            <span>{(t as any)('ArmorAnalysis.ActiveRange', 'Active Range')}</span>
          </button>

          <button
            className={`${styles.toggle} ${options.showDifferences ? styles.active : ''}`}
            onClick={() => onChange({ showDifferences: !options.showDifferences })}
            title={(t as any)('ArmorAnalysis.ShowDifferences', 'Show tier-to-tier differences')}
          >
            <span className="fa fa-chart-line" />
            <span>{(t as any)('ArmorAnalysis.Differences', 'Differences')}</span>
          </button>

          <button
            className={`${styles.toggle} ${options.showPlateau ? styles.active : ''}`}
            onClick={() => onChange({ showPlateau: !options.showPlateau })}
            title={(t as any)('ArmorAnalysis.ShowPlateau', 'Highlight plateau region')}
          >
            <span className="fa fa-chart-area" />
            <span>{(t as any)('ArmorAnalysis.Plateau', 'Plateau')}</span>
          </button>

          <button
            className={`${styles.toggle} ${options.compareMode ? styles.active : ''}`}
            onClick={() => onChange({ compareMode: !options.compareMode })}
            title={(t as any)('ArmorAnalysis.CompareMode', 'Compare multiple stats')}
          >
            <span className="fa fa-code-branch" />
            <span>{(t as any)('ArmorAnalysis.Compare', 'Compare')}</span>
          </button>
        </div>
      </div>

      {!options.showOnlyChangedRange && (
        <div className={styles.rangeControls}>
          <div className={styles.rangeGroup}>
            <label htmlFor="startRange">
              {(t as any)('ArmorAnalysis.StartValue', 'Start')}
            </label>
            <input
              id="startRange"
              type="range"
              min={0}
              max={maxValue - 1}
              value={options.startValue}
              onChange={(e) => handleRangeChange('startValue', parseInt(e.target.value))}
              className={styles.rangeSlider}
            />
            <span className={styles.rangeValue}>{options.startValue}</span>
          </div>

          <div className={styles.rangeGroup}>
            <label htmlFor="endRange">
              {(t as any)('ArmorAnalysis.EndValue', 'End')}
            </label>
            <input
              id="endRange"
              type="range"
              min={1}
              max={maxValue}
              value={options.endValue}
              onChange={(e) => handleRangeChange('endValue', parseInt(e.target.value))}
              className={styles.rangeSlider}
            />
            <span className={styles.rangeValue}>{options.endValue}</span>
          </div>
        </div>
      )}
    </div>
  );
}

import { t } from 'app/i18next-t';
import clsx from 'clsx';
import { FilterOptions } from '../types/armor-types';
import styles from './FilterPanel.m.scss';

interface Props {
  options: FilterOptions;
  onChange: (options: Partial<FilterOptions>) => void;
  onClose: () => void;
  categories: string[];
}

export default function FilterPanel({ options, onChange, onClose, categories }: Props) {
  const handleCategoryToggle = (category: string) => {
    const newCategories = options.categories.includes(category)
      ? options.categories.filter(c => c !== category)
      : [...options.categories, category];
    onChange({ categories: newCategories });
  };

  const handleClearFilters = () => {
    onChange({
      categories: [],
      searchQuery: '',
      showPercentagesOnly: false,
      showAbsoluteOnly: false,
      minValue: 0,
      maxValue: 200
    });
  };

  return (
    <div className={styles.filterPanel}>
      <div className={styles.panelHeader}>
        <h3>{(t as any)('ArmorAnalysis.Filters', 'Filters')}</h3>
        <button className={styles.closeButton} onClick={onClose}>
          <span className="fa fa-times" />
        </button>
      </div>

      <div className={styles.filterSection}>
        <label>{(t as any)('ArmorAnalysis.SearchStats', 'Search Stats')}</label>
        <input
          type="text"
          placeholder={(t as any)('ArmorAnalysis.SearchPlaceholder', 'Type to search...')}
          value={options.searchQuery}
          onChange={(e) => onChange({ searchQuery: e.target.value })}
          className={styles.searchInput}
        />
      </div>

      <div className={styles.filterSection}>
        <label>{(t as any)('ArmorAnalysis.Categories', 'Categories')}</label>
        <div className={styles.categoryButtons}>
          {categories.map(category => (
            <button
              key={category}
              className={clsx(styles.categoryButton, {
                [styles.active]: options.categories.includes(category)
              })}
              onClick={() => handleCategoryToggle(category)}
            >
              {category}
            </button>
          ))}
        </div>
      </div>

      <div className={styles.filterSection}>
        <label>{(t as any)('ArmorAnalysis.ValueType', 'Value Type')}</label>
        <div className={styles.typeToggles}>
          <button
            className={clsx(styles.typeToggle, {
              [styles.active]: options.showPercentagesOnly
            })}
            onClick={() => onChange({
              showPercentagesOnly: !options.showPercentagesOnly,
              showAbsoluteOnly: false
            })}
          >
            {(t as any)('ArmorAnalysis.PercentagesOnly', 'Percentages Only')}
          </button>
          <button
            className={clsx(styles.typeToggle, {
              [styles.active]: options.showAbsoluteOnly
            })}
            onClick={() => onChange({
              showAbsoluteOnly: !options.showAbsoluteOnly,
              showPercentagesOnly: false
            })}
          >
            {(t as any)('ArmorAnalysis.AbsoluteOnly', 'Absolute Values Only')}
          </button>
        </div>
      </div>

      <div className={styles.panelFooter}>
        <button className={styles.clearButton} onClick={handleClearFilters}>
          {(t as any)('ArmorAnalysis.ClearFilters', 'Clear All')}
        </button>
      </div>
    </div>
  );
}

import { t } from 'app/i18next-t';
import { ProcessedStatData } from '../types/armor-types';
import styles from './StatCards.m.scss';

interface Props {
  stats: ProcessedStatData[];
  viewMode: 'compact' | 'expanded';
}

export default function StatCards({ stats, viewMode }: Props) {
  if (stats.length === 0) {return null;}

  const formatValue = (value: number, stat: ProcessedStatData) => {
    if (stat.isPercentage) {
      return `${value.toFixed(2)  }%`;
    } else if (stat.unit) {
      return `${value.toFixed(1)  } ${  stat.unit}`;
    }
    return value.toFixed(1);
  };

  const getStatCards = (stat: ProcessedStatData) => {
    const totalIncrease = stat.maxValue - stat.values[0];
    const avgIncrease = stat.differences.reduce((sum, d) => sum + Math.abs(d), 0) / (stat.differences.length || 1);

    const cards = [
      {
        label: (t as any)('ArmorAnalysis.MaxBenefitTier', 'Max Benefit Change Tier'),
        value: String(stat.maxDifferenceIndex),
        highlight: true,
      },
      {
        label: (t as any)('ArmorAnalysis.MaxBenefitValue', 'Max Tier Change'),
        value: `+${  formatValue(stat.maxDifference, stat)}`,
        highlight: true,
      },
      {
        label: (t as any)('ArmorAnalysis.TotalIncrease', 'Total Possible Increase'),
        value: formatValue(totalIncrease, stat),
      },
      {
        label: (t as any)('ArmorAnalysis.AveragePerTier', 'Average per Tier'),
        value: `+${  formatValue(avgIncrease, stat)}`,
      },
    ];

    if (stat.firstNonZeroIndex > 0) {
      cards.push({
        label: (t as any)('ArmorAnalysis.FirstActiveTier', 'First Active Tier'),
        value: String(stat.firstNonZeroIndex),
      });
    }

    if (stat.plateauIndex !== -1) {
      cards.push({
        label: (t as any)('ArmorAnalysis.PlateauReached', 'Max Value Reached'),
        value: String(stat.plateauIndex),
        highlight: true,
      });
    }

    return cards;
  };

  if (viewMode === 'compact' && stats.length === 1) {
    const stat = stats[0];
    const cards = getStatCards(stat);

    return (
      <div className={styles.compactCards}>
        {cards.slice(0, 4).map((card, idx) => (
          <div key={idx} className={`${styles.compactCard} ${card.highlight ? styles.highlight : ''}`}>
            <div className={styles.cardValue}>{card.value}</div>
            <div className={styles.cardLabel}>{card.label}</div>
          </div>
        ))}
      </div>
    );
  }

  return (
    <div className={styles.statsContainer}>
      {stats.map((stat) => (
        <div key={stat.name} className={styles.statSection}>
          {stats.length > 1 && (
            <div className={styles.statHeader}>
              <h3 className={styles.statTitle}>{stat.label}</h3>
              <div className={styles.statSource}>
                {stat.sourceName} ({stat.sourceType})
              </div>
            </div>
          )}
          <div className={styles.statsGrid}>
            {getStatCards(stat).map((card, idx) => (
              <div
                key={idx}
                className={`${styles.statCard} ${card.highlight ? styles.highlight : ''}`}
              >
                <div className={styles.cardLabel}>{card.label}</div>
                <div className={styles.cardValue}>{card.value}</div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}

import React from 'react';
import { ProcessedArmor3Stat } from '../types/armor3-types';
import styles from './StatComparison.m.scss';

interface Props {
  stats: ProcessedArmor3Stat[];
  currentValues: { [statId: string]: number };
  compareStats: ProcessedArmor3Stat[];
  onAddCompareStat: (stat: ProcessedArmor3Stat) => void;
  onRemoveCompareStat: (stat: ProcessedArmor3Stat) => void;
}

export default function StatComparison({
  stats,
  currentValues,
  compareStats,
  onAddCompareStat,
  onRemoveCompareStat
}: Props) {
  // Calculate benefit values at specific stat points for comparison
  const comparisonPoints = [30, 50, 70, 80, 100, 120, 150, 200];
  
  const selectedStats = compareStats.length > 0 ? compareStats : stats;
  
  return (
    <div className={styles.statComparison}>
      <div className={styles.header}>
        <h2>Stat Comparison</h2>
        <p>Compare benefits across different stat values and breakpoints</p>
      </div>
      
      {/* Stat Selector */}
      <div className={styles.statSelector}>
        <h3>Select Stats to Compare</h3>
        <div className={styles.statButtons}>
          {stats.map(stat => {
            const isSelected = compareStats.some(s => s.id === stat.id);
            return (
              <button
                key={stat.id}
                className={`${styles.statButton} ${isSelected ? styles.selected : ''}`}
                onClick={() => {
                  if (isSelected) {
                    onRemoveCompareStat(stat);
                  } else {
                    onAddCompareStat(stat);
                  }
                }}
              >
                {stat.name}
                {isSelected && <span className={styles.removeIcon}>Ã—</span>}
              </button>
            );
          })}
        </div>
      </div>
      
      {/* Comparison Table */}
      <div className={styles.comparisonTable}>
        <h3>Benefit Comparison at Key Breakpoints</h3>
        
        {selectedStats.map(stat => (
          <div key={stat.id} className={styles.statSection}>
            <h4>{stat.name}</h4>
            
            <div className={styles.tableWrapper}>
              <table className={styles.table}>
                <thead>
                  <tr>
                    <th>Stat Value</th>
                    {stat.primaryEffects.map(benefit => (
                      <th key={benefit.name} className={styles.primary}>
                        {benefit.name}
                        <span className={styles.unit}>({benefit.unit})</span>
                      </th>
                    ))}
                    {stat.enhancedEffects.map(benefit => (
                      <th key={benefit.name} className={styles.enhanced}>
                        {benefit.name}
                        <span className={styles.unit}>({benefit.unit})</span>
                      </th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {comparisonPoints.map(point => {
                    const isCurrentValue = currentValues[stat.id] === point;
                    const isBreakpoint = stat.breakpoints.some(bp => bp.statValue === point);
                    
                    return (
                      <tr 
                        key={point} 
                        className={`
                          ${isCurrentValue ? styles.current : ''}
                          ${isBreakpoint ? styles.breakpoint : ''}
                        `}
                      >
                        <td className={styles.statValue}>
                          {point}
                          {isBreakpoint && (
                            <span className={styles.breakpointIcon} title={
                              stat.breakpoints.find(bp => bp.statValue === point)?.description
                            }>
                              â˜…
                            </span>
                          )}
                        </td>
                        {stat.primaryEffects.map(benefit => {
                          const value = point <= 100 ? benefit.values[point] : benefit.values[100];
                          const isMaxed = point >= 100 && benefit.range[1] === 100;
                          
                          return (
                            <td key={benefit.name} className={`${styles.primary} ${isMaxed ? styles.maxed : ''}`}>
                              {value > 0 ? `+${value.toFixed(1)}` : value.toFixed(1)}
                              {isMaxed && <span className={styles.maxIndicator}>MAX</span>}
                            </td>
                          );
                        })}
                        {stat.enhancedEffects.map(benefit => {
                          const value = point > 100 ? benefit.values[point] : 0;
                          const isActive = point > 100;
                          
                          return (
                            <td key={benefit.name} className={`${styles.enhanced} ${!isActive ? styles.locked : ''}`}>
                              {isActive ? `+${value.toFixed(1)}` : '-'}
                            </td>
                          );
                        })}
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
            
            {/* Key Insights */}
            <div className={styles.insights}>
              <h5>Key Insights</h5>
              <ul>
                {stat.breakpoints
                  .filter(bp => bp.importance === 'critical' || bp.importance === 'major')
                  .map((bp, idx) => (
                    <li key={idx}>
                      <strong>{bp.statValue}:</strong> {bp.description}
                    </li>
                  ))}
                <li>
                  <strong>100:</strong> Primary effects maxed, enhanced effects unlock
                </li>
                {stat.id === 'grenade' && (
                  <li>
                    <strong>200:</strong> Maximum +65% PvE damage - highest stat damage bonus
                  </li>
                )}
                {stat.id === 'weapon' && (
                  <li>
                    <strong>200:</strong> Guaranteed double ammo from bricks in PvE
                  </li>
                )}
              </ul>
            </div>
          </div>
        ))}
      </div>
      
      {/* Efficiency Analysis */}
      <div className={styles.efficiencyAnalysis}>
        <h3>Investment Efficiency Analysis</h3>
        <div className={styles.efficiencyGrid}>
          {selectedStats.map(stat => (
            <div key={stat.id} className={styles.efficiencyCard}>
              <h4>{stat.name}</h4>
              
              <div className={styles.ranges}>
                <div className={styles.range}>
                  <span className={styles.rangeLabel}>0-70:</span>
                  <span className={styles.rangeValue}>High Efficiency</span>
                  <p>Maximum returns for ability cooldowns</p>
                </div>
                
                <div className={styles.range}>
                  <span className={styles.rangeLabel}>70-100:</span>
                  <span className={styles.rangeValue}>Diminishing Returns</span>
                  <p>Limited benefit, consider other stats</p>
                </div>
                
                <div className={styles.range}>
                  <span className={styles.rangeLabel}>101-200:</span>
                  <span className={styles.rangeValue}>Specialization</span>
                  <p>Powerful enhanced effects for focused builds</p>
                </div>
              </div>
              
              {/* Recommendations based on current value */}
              <div className={styles.recommendation}>
                <strong>Current: {currentValues[stat.id]}</strong>
                {currentValues[stat.id] < 70 && stat.id !== 'weapon' && (
                  <p>Consider investing to 70 for efficient ability uptime</p>
                )}
                {currentValues[stat.id] >= 70 && currentValues[stat.id] < 100 && (
                  <p>Near soft cap - evaluate if 100+ is worth it for enhanced effects</p>
                )}
                {currentValues[stat.id] >= 100 && (
                  <p>Enhanced effects active - deep specialization territory</p>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

import { t } from 'app/i18next-t';
import clsx from 'clsx';
import { useMemo } from 'react';
import { ProcessedStatData } from '../types/armor-types';
import styles from './StatSelector.m.scss';

interface Props {
  stats: ProcessedStatData[];
  selectedStat: ProcessedStatData | null;
  compareStats: ProcessedStatData[];
  onSelect: (stat: ProcessedStatData | null) => void;
  compareMode: boolean;
}

export default function StatSelector({
  stats,
  selectedStat,
  compareStats,
  onSelect,
  compareMode
}: Props) {
  const groupedStats = useMemo(() => {
    const groups: Record<string, ProcessedStatData[]> = {};
    stats.forEach(stat => {
      if (!groups[stat.category]) {
        groups[stat.category] = [];
      }
      groups[stat.category].push(stat);
    });
    return groups;
  }, [stats]);

  const handleSelect = (stat: ProcessedStatData) => {
    if (compareMode) {
      onSelect(stat);
    } else {
      onSelect(stat.name === selectedStat?.name ? null : stat);
    }
  };

  const isSelected = (stat: ProcessedStatData) => {
    if (compareMode) {
      return compareStats.some(s => s.name === stat.name);
    }
    return selectedStat?.name === stat.name;
  };

  return (
    <div className={styles.statSelector}>
      <div className={styles.selectorHeader}>
        <label>{(t as any)('ArmorAnalysis.SelectStat', 'Select Stat')}</label>
        {compareMode && (
          <span className={styles.compareIndicator}>
            {(t as any)('ArmorAnalysis.CompareMode', 'Compare Mode')} ({compareStats.length})
          </span>
        )}
      </div>

      <div className={styles.statsGrid}>
        {Object.entries(groupedStats).map(([category, categoryStats]) => (
          <div key={category} className={styles.categoryGroup}>
            <div className={styles.categoryHeader}>{category}</div>
            <div className={styles.statButtons}>
              {categoryStats.map(stat => (
                <button
                  key={stat.name}
                  className={clsx(styles.statButton, {
                    [styles.selected]: isSelected(stat),
                    [styles.percentage]: stat.isPercentage,
                    [styles.absolute]: !stat.isPercentage
                  })}
                  onClick={() => handleSelect(stat)}
                  title={stat.name}
                >
                  <div className={styles.statInfo}>
                    <span className={styles.statName}>{stat.label}</span>
                    <span className={styles.statSource}>
                      {stat.sourceName} ({stat.sourceType})
                    </span>
                  </div>
                  <div className={styles.statMeta}>
                    {stat.unit && (
                      <span className={styles.statUnit}>{stat.unit}</span>
                    )}
                    {stat.plateauIndex !== -1 && (
                      <span className={styles.plateauBadge} title={(t as any)('ArmorAnalysis.HasPlateau', 'Has plateau')}>
                        P
                      </span>
                    )}
                  </div>
                </button>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

import { t } from 'app/i18next-t';
import {
  CategoryScale,
  Chart as ChartJS,
  Filler,
  Legend,
  LinearScale,
  LineElement,
  PointElement,
  Title,
  Tooltip,
} from 'chart.js';
import { useRef, useState } from 'react';
import { Line } from 'react-chartjs-2';
import { ChartOptions, ProcessedStatData } from '../types/armor-types';
import { getActiveRange } from '../utils/data-parser';
import styles from './TierChart.m.scss';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Filler,
  Legend
);

interface Props {
  stats: ProcessedStatData[];
  options: ChartOptions;
  viewMode: 'compact' | 'expanded';
}

export default function TierChart({ stats, options }: Props) {
  const chartRef = useRef<ChartJS<'line'>>(null);
  const [hoveredIndex, setHoveredIndex] = useState<number | null>(null);

  const getChartData = () => {
    let labels: string[] = [];
    let startIdx = options.startValue;
    let endIdx = options.endValue;

    // Determine range based on options
    if (options.showOnlyChangedRange && stats.length > 0) {
      const ranges = stats.map(stat => getActiveRange(stat));
      startIdx = Math.min(...ranges.map(r => r[0]));
      endIdx = Math.max(...ranges.map(r => r[1]));
    }

    // Create labels for x-axis
    labels = Array.from(
      { length: endIdx - startIdx + 1 },
      (_, i) => String(startIdx + i)
    );

    const datasets = stats.map((stat, idx) => {
      const dataToShow = options.showDifferences ? stat.differences : stat.values;
      const slicedData = dataToShow.slice(startIdx, endIdx + 1);

      // High contrast colors for dark background
      const colors = [
        '#00ffff', // cyan
        '#ff6b6b', // coral red
        '#4ecdc4', // turquoise
        '#ffd93d', // gold
        '#95e1d3', // mint
        '#ff9ff3', // pink
      ];

      const color = colors[idx % colors.length];

      return {
        label: stat.label,
        data: slicedData,
        borderColor: color,
        backgroundColor: `${color}15`,
        tension: 0.3,
        fill: true,
        pointRadius: slicedData.map((_, i) => {
          const actualIdx = startIdx + i;
          // Highlight critical points
          if (actualIdx === stat.maxDifferenceIndex) {return 10;}
          if (actualIdx === stat.plateauIndex) {return 8;}
          if (actualIdx === stat.firstNonZeroIndex) {return 6;}
          // Show points every 10 for clarity
          if (actualIdx % 10 === 0) {return 3;}
          return 0;
        }),
        pointBackgroundColor: slicedData.map((_, i) => {
          const actualIdx = startIdx + i;
          if (actualIdx === stat.maxDifferenceIndex) {return '#ff00ff';} // Magenta for max change
          if (actualIdx === stat.plateauIndex) {return '#ffff00';} // Yellow for plateau
          if (actualIdx === stat.firstNonZeroIndex) {return '#00ff00';} // Green for start
          return color;
        }),
        pointBorderColor: slicedData.map((_, i) => {
          const actualIdx = startIdx + i;
          if (actualIdx === stat.maxDifferenceIndex) {return '#ff00ff';}
          if (actualIdx === stat.plateauIndex) {return '#ffff00';}
          if (actualIdx === stat.firstNonZeroIndex) {return '#00ff00';}
          return color;
        }),
        pointBorderWidth: 2,
        pointHoverRadius: 10,
        borderWidth: 3,
      };
    });

    return {
      labels,
      datasets,
    };
  };

  const chartOptions: any = {
    responsive: true,
    maintainAspectRatio: false,
    interaction: {
      mode: 'index' as const,
      intersect: false,
    },
    plugins: {
      legend: {
        display: stats.length > 1,
        position: 'top' as const,
        labels: {
          color: '#ffffff',
          padding: 15,
          font: {
            size: 13,
            weight: '500',
          },
        },
      },
      tooltip: {
        callbacks: {
          label: (context: any) => {
            const stat = stats[context.datasetIndex];
            if (!stat) {return '';}

            let label = context.dataset.label || '';
            const actualIdx = options.startValue + context.dataIndex;

            if (label) {
              label += ': ';
            }

            const value = context.parsed.y;
            if (stat.isPercentage && !options.showDifferences) {
              label += `${value.toFixed(2)  }%`;
            } else if (stat.unit && !options.showDifferences) {
              label += `${value.toFixed(2)  } ${  stat.unit}`;
            } else {
              label += value.toFixed(2);
            }

            // Add change indicator
            if (context.dataIndex > 0 && !options.showDifferences) {
              const prevValue = context.dataset.data[context.dataIndex - 1];
              const diff = value - prevValue;
              if (diff !== 0) {
                label += ` (${  diff > 0 ? '+' : ''  }${diff.toFixed(2)  })`;
              }
            }

            // Add special markers
            if (actualIdx === stat.maxDifferenceIndex) {
              label += ` ðŸ”¥ ${  (t as any)('ArmorAnalysis.MaxIncrease', 'MAX INCREASE')}`;
            }
            if (actualIdx === stat.plateauIndex) {
              label += ` ðŸ“Š ${  (t as any)('ArmorAnalysis.Plateau', 'PLATEAU')}`;
            }

            return label;
          },
          title: (tooltipItems: any) => (t as any)('ArmorAnalysis.StatValue', {
              value: tooltipItems[0].label
            })
        },
        backgroundColor: 'rgba(0, 0, 0, 0.9)',
        titleColor: '#fff',
        bodyColor: '#fff',
        borderColor: '#667eea',
        borderWidth: 1,
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: options.showDifferences
            ? 'Change per Stat Point'
            : 'Benefit Value',
          color: '#ffffff',
          font: {
            size: 14,
            weight: '600',
          },
        },
        ticks: {
          color: '#ffffff',
          font: {
            size: 12,
            weight: '500',
          },
          callback: function(value: any) {
            if (stats.length > 0 && stats[0].isPercentage && !options.showDifferences) {
              return `${value.toFixed(1)  }%`;
            }
            return value.toFixed(1);
          },
        },
        grid: {
          color: 'rgba(255, 255, 255, 0.2)',
          lineWidth: 1,
        },
      },
      x: {
        title: {
          display: true,
          text: stats.length > 0 ? stats[0].axisLabel : 'Stat Value (0-200)',
          color: '#ffffff',
          font: {
            size: 14,
            weight: '600',
          },
        },
        ticks: {
          color: '#ffffff',
          font: {
            size: 12,
            weight: '500',
          },
          stepSize: 10,
          min: 0,
          max: 200,
          callback: function(value: any) {
            // Show major ticks every 10 points
            if (value % 10 === 0) {
              return value.toString();
            }
            return null;
          },
        },
        grid: {
          color: (context: any) => {
            // Major grid lines every 10 points
            if (context.tick && context.tick.value % 10 === 0) {
              return 'rgba(255, 255, 255, 0.25)';
            }
            return 'rgba(255, 255, 255, 0.05)';
          },
          lineWidth: (context: any) => {
            if (context.tick && context.tick.value % 10 === 0) {
              return 1.5;
            }
            return 0.5;
          },
        },
      },
    },
    onHover: (_: any, activeElements: any[]) => {
      if (activeElements.length > 0) {
        setHoveredIndex(activeElements[0].index);
      } else {
        setHoveredIndex(null);
      }
    },
  };

  // Add annotations for plateau regions if enabled
  if (options.showPlateau && stats.length === 1 && stats[0].plateauIndex !== -1) {
    const stat = stats[0];
    const startIdx = options.showOnlyChangedRange ? stat.firstNonZeroIndex : options.startValue;
    const adjustedPlateauIdx = stat.plateauIndex - startIdx;

    if (adjustedPlateauIdx >= 0) {
      chartOptions.plugins.annotation = {
        annotations: {
          plateau: {
            type: 'box',
            xMin: adjustedPlateauIdx,
            xMax: options.endValue - startIdx,
            backgroundColor: 'rgba(245, 220, 86, 0.1)',
            borderColor: 'rgba(245, 220, 86, 0.5)',
            borderWidth: 1,
            label: {
              content: (t as any)('ArmorAnalysis.PlateauRegion', 'Plateau Region'),
              enabled: true,
              position: 'center',
              color: 'rgba(245, 220, 86, 0.8)',
            },
          },
        },
      };
    }
  }

  return (
    <div className={styles.chartWrapper}>
      <Line ref={chartRef} data={getChartData()} options={chartOptions} />
      {hoveredIndex !== null && stats.length > 0 && (
        <div className={styles.hoverInfo}>
          {(t as any)('ArmorAnalysis.ValueAt', {
            value: options.startValue + hoveredIndex,
            benefit: stats[0].values[options.startValue + hoveredIndex]?.toFixed(2) || '0'
          })}
        </div>
      )}
    </div>
  );
}

import { useEffect, useState } from 'react';
import { ProcessedArmor3Stat } from '../types/armor3-types';
import { parseArmor3Data } from '../utils/armor3-parser';

interface UseArmor3DataReturn {
  data: ProcessedArmor3Stat[] | null;
  loading: boolean;
  error: Error | null;
  reload: () => void;
}

export default function useArmor3Data(): UseArmor3DataReturn {
  const [data, setData] = useState<ProcessedArmor3Stat[] | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<Error | null>(null);

  const loadData = async (abortController?: AbortController) => {
    try {
      setLoading(true);
      setError(null);
      
      // Load the new Armor 3.0 data
      const response = await fetch('/D2ArmorAnalysis/armor3-data.json', {
        signal: abortController?.signal
      });
      if (!response.ok) {
        throw new Error(`Failed to load Armor 3.0 data: ${response.statusText}`);
      }
      
      const rawData = await response.json();
      const processedData = parseArmor3Data(rawData);
      
      setData(processedData);
    } catch (err) {
      if (err.name === 'AbortError') {
        console.log('Armor 3.0 data fetch aborted');
        return;
      }
      console.error('Error loading Armor 3.0 analysis data:', err);
      setError(err instanceof Error ? err : new Error('Unknown error loading data'));
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const abortController = new AbortController();
    loadData(abortController);
    
    return () => {
      console.log('useArmor3Data - aborting fetch');
      abortController.abort();
    };
  }, []);

  return { data, loading, error, reload: () => loadData() };
}

import { useEffect, useState } from 'react';
