const categoryStyles: LookupTable<D2BucketCategory, string> = {
  Weapons: styles.categoryWeapons,
  Armor: styles.categoryArmor,
  General: styles.categoryGeneral,
};

export default function LoadoutItemCategorySection({
  category,
  subclass,
  store,
  items,
  allMods,
  modsByBucket,
  loadout,
  hideOptimizeArmor,
}: {
  category: D2BucketCategory;
  subclass?: ResolvedLoadoutItem;
  store: DimStore;
  items?: ResolvedLoadoutItem[];
  allMods: PluggableInventoryItemDefinition[];
  modsByBucket: {
    [bucketHash: number]: number[];
  };
  loadout: Loadout;
  hideOptimizeArmor?: boolean;
}) {
  const defs = useD2Definitions()!;
  const buckets = useSelector(bucketsSelector)!;
  const analysis = useAnalyzeLoadout(loadout, store, /* active */ !hideOptimizeArmor);
  const itemsByBucket = Map.groupBy(items ?? [], (li) => li.item.bucket.hash);
  const isPhonePortrait = useIsPhonePortrait();
  const bucketOrder =
    category === 'Weapons' || category === 'Armor'
      ? buckets.byCategory[category]
      : Array.from(itemsByBucket.keys(), (bucketHash) => buckets.byHash[bucketHash]).sort(
          compareBy((bucket) =>
            buckets.byCategory[category].findIndex((b) => b.hash === bucket.hash),
          ),
        );
  const equippedItems =
    items?.filter((li) => li.loadoutItem.equip && !li.missing).map((li) => li.item) ?? [];

  const isArmor = category === 'Armor';
  const hasFashion = isArmor && !isEmpty(modsByBucket);

  const [optimizeLoadout, constraints]: [Loadout, ResolvedStatConstraint[] | undefined] =
    useMemo(() => {
      if (
        analysis?.result.armorResults?.tag === 'done' &&
        analysis.result.armorResults.betterStatsAvailable
      ) {
        return [
          clearBucketCategory(
            defs,
            'Armor',
          )(setLoadoutParameters(analysis.result.armorResults.loadoutParameters)(loadout)),
          analysis.result.armorResults.strictUpgradeStatConstraints,
        ];
      } else {
        return [loadout, undefined];
      }
    }, [defs, analysis?.result.armorResults, loadout]);

  if (isPhonePortrait && !items && !hasFashion) {
    return null;
  }

  return (
    <div key={category} className={clsx(styles.itemCategory, categoryStyles[category])}>
      {items || hasFashion ? (
        <div className={styles.itemsInCategory}>
          {bucketOrder.map((bucket) => (
            <ItemBucket
              key={bucket.hash}
              storeId={store.id}
              bucketHash={bucket.hash}
              items={itemsByBucket.get(bucket.hash) ?? emptyArray()}
              modsForBucket={modsByBucket[bucket.hash] ?? emptyArray()}
            />
          ))}
        </div>
      ) : (
        <>
          <div className={clsx(styles.placeholder, `category-${category}`)}>
            {(t as any)(`Bucket.${category}`, { metadata: { keys: 'buckets' } })}
          </div>
        </>
      )}
      {items && isArmor && (
        <>
          {equippedItems.length === 5 && (
            <div className="stat-bars">
              <LoadoutCharacterStats
                loadout={loadout}
                subclass={subclass}
                allMods={allMods}
                items={items}
              />
            </div>
          )}
          {loadout.parameters && <LoadoutParametersDisplay params={loadout.parameters} />}
          {!hideOptimizeArmor && (
            <OptimizerButton
              loadout={optimizeLoadout}
              storeId={store.id}
              missingArmor={armorItemsMissing(items)}
              strictUpgradeStatConstraints={constraints}
            />
          )}
        </>
      )}
    </div>
  );
}

function ItemBucket({
  bucketHash,
  storeId,
  items,
  modsForBucket,
}: {
  bucketHash: number;
  storeId?: string;
  items: ResolvedLoadoutItem[];
  modsForBucket: number[];
}) {
  const [equipped, unequipped] = partition(items, (li) => li.loadoutItem.equip);

  const showFashion = ArmorBucketHashes.includes(bucketHash);

  // TODO: should these be draggable? so you can drag them into other loadouts?

  return (
    <div className={styles.itemBucket}>
      {[equipped, unequipped].map((items, index) =>
        items.length > 0 ? (
          <div
            className={clsx(styles.items, index === 0 ? styles.equipped : styles.unequipped)}
            key={index}
          >
            {items.map(({ item, loadoutItem, missing }) => (
              <DraggableInventoryItem item={item} key={item.id}>
                <ItemPopupTrigger
                  item={item}
                  extraData={{ socketOverrides: loadoutItem.socketOverrides }}
                >
                  {(ref, onClick) => (
                    <div
                      className={clsx({
                        [styles.missingItem]: missing,
                      })}
                    >
                      <ConnectedInventoryItem item={item} ref={ref} onClick={onClick} />
                    </div>
                  )}
                </ItemPopupTrigger>
              </DraggableInventoryItem>
            ))}
            {index === 0 && showFashion && (
              <FashionMods modsForBucket={modsForBucket} storeId={storeId} />
            )}
          </div>
        ) : (
          index === 0 && (
            <div
              className={clsx(styles.items, index === 0 ? styles.equipped : styles.unequipped)}
              key={index}
            >
              <BucketPlaceholder bucketHash={bucketHash} />
              {/* TODO: show empty placeholder for bucket type? */}
              {showFashion && <FashionMods modsForBucket={modsForBucket} storeId={storeId} />}
            </div>
          )
        ),
      )}
    </div>
  );
}

import { TileGridTile } from 'app/d2l-ui/TileGrid';
import RichDestinyText from 'app/d2l-ui/destiny-symbols/RichDestinyText';
import { t } from 'app/i18next-t';
import { DefItemIcon } from 'app/inventory/ItemIcon';
import { PluggableInventoryItemDefinition } from 'app/inventory/item-types';
import { PlugStats } from 'app/item-popup/PlugTooltip';
import { banIcon, minusIcon, plusIcon } from 'app/shell/icons';
import AppIcon from 'app/shell/icons/AppIcon';
import { getPlugDefStats, usePlugDescriptions } from 'app/utils/plug-descriptions';
import { DestinyClass } from 'bungie-api-ts/destiny2';
import { useCallback, useMemo } from 'react';
import PlugStackableIcon from './PlugStackableIcon';
import styles from './SelectablePlug.m.scss';
import { PlugSelectionType } from './types';

/**
 * A single selectable plug in the PlugDrawer component. This shows the details of the plug along
 * with whether it's selected and whether it *can* be selected.
 */
export default function SelectablePlug({
  plug,
  classType,
  selected,
  selectable,
  selectionType,
  stack,
  removable,
  onPlugSelected,
  onPlugRemoved,
}: {
  plug: PluggableInventoryItemDefinition;
  /** How many of this specific mod have been selected? (only for stackable mods) */
  stack: number;
  classType: DestinyClass;
  selected: boolean;
  selectable: boolean;
  selectionType: PlugSelectionType;
  removable: boolean;
  onPlugSelected: (plug: PluggableInventoryItemDefinition) => void;
  onPlugRemoved: (plug: PluggableInventoryItemDefinition) => void;
}) {
  const canRemoveOnClick =
    selectionType !== PlugSelectionType.Single && selected && removable && stack <= 1;

  const handleClick = useCallback(() => {
    selectable ? onPlugSelected(plug) : canRemoveOnClick && onPlugRemoved(plug);
  }, [selectable, onPlugSelected, plug, canRemoveOnClick, onPlugRemoved]);

  // Memoize the meat of the component as it doesn't need to re-render every time
  const plugDetails = useMemo(
    () => <SelectablePlugDetails plug={plug} classType={classType} />,
    [plug, classType],
  );

  const stackable = stack > 1 || (stack === 1 && selectable);

  const handleIncrease =
    stackable && selectable
      ? (e: React.MouseEvent) => {
          e.stopPropagation();
          onPlugSelected(plug);
        }
      : undefined;

  const handleDecrease =
    stackable && removable
      ? (e: React.MouseEvent) => {
          e.stopPropagation();
          onPlugRemoved(plug);
        }
      : undefined;

  const corner =
    handleIncrease || handleDecrease || stackable ? (
      <div className={styles.buttons}>
        {stackable && <div className={styles.stack}>{stack}Ã—</div>}
        {(handleIncrease || handleDecrease) && (
          <div className={styles.volumeRocker}>
            <button
              type="button"
              className="d2l-button"
              onClick={handleIncrease}
              disabled={!handleIncrease}
              title={t('LB.AddStack')}
            >
              <AppIcon icon={plusIcon} />
            </button>
            <button
              type="button"
              className="d2l-button"
              onClick={handleDecrease}
              disabled={!handleDecrease}
              title={t('LB.RemoveStack')}
            >
              <AppIcon icon={stack === 1 ? banIcon : minusIcon} />
            </button>
          </div>
        )}
      </div>
    ) : undefined;

  return (
    <TileGridTile
      selected={selected}
      disabled={
        !(
          (stackable && (selectable || removable)) ||
          selectable ||
          canRemoveOnClick ||
          (selectionType === PlugSelectionType.Single && selected)
        )
      }
      title={plug.displayProperties.name}
      icon={
        <div className="item" title={plug.displayProperties.name}>
          <DefItemIcon itemDef={plug} />
        </div>
      }
      onClick={handleClick}
      corner={corner}
    >
      {plugDetails}
    </TileGridTile>
  );
}

function SelectablePlugDetails({
  plug,
  classType,
}: {
  plug: PluggableInventoryItemDefinition;
  classType: DestinyClass;
}) {
  const stats = getPlugDefStats(plug, classType);

  const plugDescriptions = usePlugDescriptions(plug, stats);

  return (
    <>
      {plugDescriptions.perks.map(
        (perkDesc) =>
          perkDesc.description && (
            <RichDestinyText key={perkDesc.perkHash} text={perkDesc.description} />
          ),
      )}
      <PlugStackableIcon descriptions={plugDescriptions} hash={plug.hash} />
      {stats.length > 0 && <PlugStats stats={stats} />}
    </>
  );
}

import { t } from 'app/i18next-t';
import { userGuideUrl } from 'app/shell/links';
import { useThunkDispatch } from 'app/store/thunk-dispatch';
