function PerksCell({
  item,
  sockets,
  onPlugClicked,
}: {
  item: DimItem;
  sockets: DimSocket[];
  onPlugClicked?: (value: { item: DimItem; socket: DimSocket; plugHash: number }) => void;
}) {
  if (!sockets.length) {
    return null;
  }

  return (
    <>
      {sockets.map((socket) => (
        <div
          key={socket.socketIndex}
          className={clsx(styles.modPerks, {
            [styles.isPerk]: socket.isPerk && socket.plugOptions.length > 1,
          })}
        >
          {socket.plugOptions.map((p) => (
            p && // Add null check for p
            <PressTip key={p.plugDef.hash} tooltip={() => <DimPlugTooltip item={item} plug={p} />}>
              <div
                className={clsx(styles.modPerk, {
                  [styles.perkSelected]:
                    socket.isPerk && socket.plugOptions.length > 1 && p === socket.plugged,
                  [styles.perkSelectable]: socket.plugOptions.length > 1,
                })}
                data-filter-value={p.plugDef.displayProperties.name}
                onClick={
                  onPlugClicked && socket.plugOptions.length > 1
                    ? (e: React.MouseEvent) => {
                        if (!e.shiftKey) {
                          e.stopPropagation();
                          onPlugClicked({ item, socket, plugHash: p.plugDef.hash });
                        }
                      }
                    : undefined
                }
              >
                <div className={styles.miniPerkContainer}>
                  <DefItemIcon itemDef={p.plugDef} borderless={true} />
                </div>
                <span
                  className={clsx({
                    [styles.enhancedArrow]: isEnhancedPerk(p.plugDef),
                  })}
                >
                  {p.plugDef.displayProperties.name}
                </span>
              </div>
            </PressTip>
          ))}
        </div>
      ))}
    </>
  );
}

function D1PerksCell({ item }: { item: D1Item }) {
  if (!isD1Item(item) || !item.talentGrid) {
    return null;
  }
  const sockets = Object.values(
    Object.groupBy(
      item.talentGrid.nodes.filter((n) => n.column > 0),
      (n) => n.column,
    ),
  );

  if (!sockets.length) {
    return null;
  }
  return (
    <>
      {sockets.map((socket) => (
        socket && // Add null check for socket
        <div
          key={socket[0].column}
          className={clsx(styles.modPerks, {
            [styles.isPerk]: socket.length > 1 && socket[0].exclusiveInColumn,
          })}
        >
          {socket.map(
            (p) =>
              p && // Add null check for p
              isD1Item(item) && (
                <PressTip
                  key={p.hash}
                  tooltip={
                    <>
                      <Tooltip.Header text={p.name} />
                      <div>{p.description}</div>
                    </>
                  }
                >
                  <div className={styles.modPerk} data-filter-value={p.name}>
                    <div className={styles.miniPerkContainer}>
                      <BungieImage src={p.icon} />
                    </div>{' '}
                    {p.name}
                    {p.xpRequired > 0 && (!p.unlocked || p.xp < p.xpRequired) && (
                      <> ({percent(p.xp / p.xpRequired)})</>
                    )}
                  </div>
                </PressTip>
              ),
          )}
        </div>
      ))}
    </>
  );
}

function StoreLocation({ storeId }: { storeId: string }) {
  const store = useSelector((state: RootState) => getStore(storesSelector(state), storeId)!);

  return (
    <div className={styles.locationCell}>
      <StoreIcon store={store} /> {store.className}
    </div>
  );
}

export function perkString(sockets: DimSocket[]): string | undefined {
  if (!sockets.length) {
    return undefined;
  }

  // TODO: filter out empty sockets, maybe sort?
  return sockets
    .flatMap((socket) => socket.plugOptions.map((p) => p.plugDef.displayProperties.name))
    .filter(Boolean)
    .join(',');
}

export function getIntrinsicSockets(item: DimItem) {
  const intrinsicSocket = getIntrinsicArmorPerkSocket(item);
  const extraIntrinsicSockets = getExtraIntrinsicPerkSockets(item);
  return intrinsicSocket &&
    // artifice already shows up in the "modslot" column
    !isArtificeSocket(intrinsicSocket)
    ? [intrinsicSocket, ...extraIntrinsicSockets]
    : extraIntrinsicSockets;
}

/**
 * This builds stat infos for all the stats that are relevant to a particular category of items.
 * It will return the same result for the same category, since all items in a category share stats.
 */
export function buildStatInfo(items: DimItem[]): DimStat[] {
  if (!items.length) {
    return emptyArray<DimStat>();
  }

  const statHashes: { [statHash: number]: DimStat } = {};
  for (const item of items) {
    if (item.stats) {
      for (const stat of item.stats) {
        // Just use the first item's stats as the source of truth.
        statHashes[stat.statHash] ??= stat;
      }
    }
  }
  return Object.values(statHashes);
}

// ignore raid & calus sources in favor of more detailed sources
const sourceKeys = Object.keys(D2Sources).filter((k) => !['raid', 'calus'].includes(k));
function source(item: DimItem) {
  if (item.destinyVersion === 2) {
    return (
      sourceKeys.find(
        (src) =>
          (item.source && D2Sources[src].sourceHashes?.includes(item.source)) ||
          D2Sources[src].itemHashes?.includes(item.hash),
      ) || ''
    );
  }
}

import { useD2Definitions } from 'app/manifest/selectors';
import { METRICS_ACCOUNT_NODE } from 'app/search/d2-known-values';
import { DestinyObjectiveProgress } from 'bungie-api-ts/destiny2';
import clsx from 'clsx';
import masterworkOverlay from 'images/masterwork-metric.png';
import BungieImage from '../d2l-ui/BungieImage';
import styles from './MetricBanner.m.scss';
import { getMetricTimeScope } from './presentation-nodes';

interface Props {
  metricHash: number;
  className?: string;
  objectiveProgress: DestinyObjectiveProgress;
}

export default function MetricBanner({ metricHash, objectiveProgress, className }: Props) {
  const defs = useD2Definitions()!;
  const metricDef = defs.Metric.get(metricHash);
  if (!metricDef) {
    return null;
  }
  const metricIcon = metricDef.displayProperties.icon;

  const metricScope = getMetricTimeScope(defs, metricDef);

  const parentNodeHash = metricDef.parentNodeHashes.length
    ? metricDef.parentNodeHashes[0]
    : METRICS_ACCOUNT_NODE;
  const parentNode = defs.PresentationNode.get(parentNodeHash);

  const bannerIcon = parentNode?.displayProperties.icon;
  const scopeIcon = metricScope.displayProperties.iconSequences[0].frames[2];

  const masterwork = objectiveProgress.complete;

  return (
    <div className={clsx(className, styles.icon)}>
      {bannerIcon && <BungieImage className={styles.bannerIcon} src={bannerIcon} />}
      {masterwork && <img src={masterworkOverlay} className={styles.bannerIcon} loading="lazy" />}
      <BungieImage className={styles.scopeIcon} src={scopeIcon} />
      <BungieImage className={styles.metricIcon} src={metricIcon} />
    </div>
  );
}

import clsx from 'clsx';
import styles from './PerkRankIndicator.m.scss';

interface PerkRankIndicatorProps {
  rank: number;
  count?: number;
  className?: string;
  size?: 'small' | 'medium' | 'large';
}

/**
 * Displays a perk rank indicator (1-5 with different colors)
 */
export default function PerkRankIndicator({ 
  rank, 
  count, 
  className, 
  size = 'small' 
}: PerkRankIndicatorProps) {
  if (rank < 1) {
    return null;
  }

  const getRankColor = (rank: number) => {
    switch (rank) {
      case 1: return styles.rank1; // Gold/Best
      case 2: return styles.rank2; // Silver/Good
      case 3: return styles.rank3; // Bronze/OK
      case 4: return styles.rank4; // Gray/Poor
      case 5: return styles.rank5; // Red/Worst
      case 6: return styles.rank6; // Dark Red
      case 7: return styles.rank7; // Darker Red
      default: return styles.rankDefault;
    }
  };

  return (
    <div 
      className={clsx(
        styles.rankIndicator, 
        getRankColor(rank),
        styles[size],
        className
      )}
      title={count ? `Rank ${rank} (${count.toLocaleString()} users)` : `Rank ${rank}`}
    >
      {rank}
    </div>
  );
}

import { useD2Definitions } from 'app/manifest/selectors';
import { DimItem } from 'app/inventory/item-types';
import { useRollAppraiserUtils } from './useRollAppraiserData';
import styles from './TopTraitCombos.m.scss';
import { useMemo, useState } from 'react';
import BungieImage from 'app/d2l-ui/BungieImage';
import clsx from 'clsx';
import { AppIcon, faCaretDown, faCaretUp } from 'app/shell/icons';

interface TraitComboEntry {
  rank: number;
  perk4Hash: number;
  perk4EnhancedHash: number | null;
  perk5Hash: number;
  perk5EnhancedHash: number | null;
  count: number;
  show: boolean;
}

interface TopTraitCombosProps {
  item: DimItem;
  className?: string;
}

export default function TopTraitCombos({ item, className }: TopTraitCombosProps) {
  const defs = useD2Definitions();
  const { utils } = useRollAppraiserUtils();
  const [isExpanded, setIsExpanded] = useState(false);

  const topCombos = useMemo(() => {
    if (!utils || !item.hash) {
      return [];
    }

    // Access the TraitStats data directly from utils
    const weaponTraits = (utils as any).data?.TraitStats?.[item.hash.toString()];
    if (!weaponTraits) {
      return [];
    }

    // Convert trait entries to a more usable format
    const traitEntries: TraitComboEntry[] = Object.values(weaponTraits)
      .filter((trait: any) => trait.Show) // Only show visible entries
      .map((trait: any) => ({
        rank: trait.comboRank,
        perk4Hash: trait.Perk4Hash,
        perk4EnhancedHash: trait.Perk4EnhancedHash,
        perk5Hash: trait.Perk5Hash,
        perk5EnhancedHash: trait.Perk5EnhancedHash,
        count: trait.Count,
        show: trait.Show,
      }));

    // Sort by rank (lower rank = better) and take top 5
    return traitEntries
      .sort((a, b) => a.rank - b.rank)
      .slice(0, 5);
  }, [utils, item.hash]);

  const getRankColor = (rank: number) => {
    switch (rank) {
      case 1: return styles.rank1;
      case 2: return styles.rank2;
      case 3: return styles.rank3;
      case 4: return styles.rank4;
      default: return styles.rank5;
    }
  };

  if (!defs || topCombos.length === 0) {
    return null;
  }

  return (
    <div className={clsx(styles.topTraitCombos, className)}>
      <button 
        className={styles.header}
        onClick={() => setIsExpanded(!isExpanded)}
        type="button"
        aria-expanded={isExpanded}
      >
        <AppIcon 
          icon={isExpanded ? faCaretDown : faCaretUp} 
          className={styles.chevron}
        />
        <h3 className={styles.title}>Top 5 Trait Combinations</h3>
        <span className={styles.count}>({topCombos.length} combos)</span>
      </button>

      {isExpanded && (
        <div className={styles.combos}>
          {topCombos.map((combo) => {
            const perk4Def = defs.InventoryItem.get(combo.perk4Hash);
            const perk5Def = defs.InventoryItem.get(combo.perk5Hash);

            if (!perk4Def || !perk5Def) {
              return null;
            }

            return (
              <div key={`${combo.perk4Hash}-${combo.perk5Hash}`} className={clsx(styles.combo, getRankColor(combo.rank))}>
                <div className={styles.rank}>#{combo.rank}</div>
                <div className={styles.perks}>
                  <div className={styles.perk}>
                    <BungieImage 
                      src={perk4Def.displayProperties.icon} 
                      width={24} 
                      height={24}
                      alt={perk4Def.displayProperties.name}
                    />
                    <span className={styles.perkName}>{perk4Def.displayProperties.name}</span>
                  </div>
                  <div className={styles.separator}>+</div>
                  <div className={styles.perk}>
                    <BungieImage 
                      src={perk5Def.displayProperties.icon} 
                      width={24} 
                      height={24}
                      alt={perk5Def.displayProperties.name}
                    />
                    <span className={styles.perkName}>{perk5Def.displayProperties.name}</span>
                  </div>
                </div>
                <div className={styles.comboCount}>{combo.count.toLocaleString()} users</div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
}

import { RollAppraiserData, RollAppraiserUtils } from 'app/utils/rollAppraiserUtils';

let rollAppraiserUtils: RollAppraiserUtils | null = null;
let loadingPromise: Promise<RollAppraiserUtils> | null = null;

/**
 * Load roll appraiser data from the processed light file
 */
async function loadRollAppraiserData(): Promise<RollAppraiserUtils> {
  if (rollAppraiserUtils) {
    return rollAppraiserUtils;
  }

  if (loadingPromise) {
    return loadingPromise;
  }

  loadingPromise = (async () => {
    try {
      // Load the processed light data
      const response = await fetch('/backend/light/rollAppraiserData.json');
      if (!response.ok) {
        throw new Error(`Failed to load roll appraiser data: ${response.statusText}`);
      }

      const data: RollAppraiserData = await response.json();
      rollAppraiserUtils = new RollAppraiserUtils(data);

      console.log('Roll appraiser data loaded successfully');
      return rollAppraiserUtils;
    } catch (error) {
      console.warn('Failed to load roll appraiser data:', error);
      // Create empty utils if loading fails
      rollAppraiserUtils = new RollAppraiserUtils({
        PerkStats: {},
        TraitStats: {},
        MWStats: {},
        ReviewSummary: {},
      });
      return rollAppraiserUtils;
    } finally {
      loadingPromise = null;
    }
  })();

  return loadingPromise;
}

/**
 * Get the roll appraiser utils instance (load if not already loaded)
 */
export async function getRollAppraiserUtils(): Promise<RollAppraiserUtils> {
  return loadRollAppraiserData();
}

/**
 * Get the roll appraiser utils instance if already loaded (synchronous)
 */
export function getRollAppraiserUtilsSync(): RollAppraiserUtils | null {
  return rollAppraiserUtils;
}

/**
 * Check if roll appraiser data is loaded
 */
export function isRollAppraiserDataLoaded(): boolean {
  return rollAppraiserUtils !== null;
}

/**
 * Preload roll appraiser data
 */
export function preloadRollAppraiserData(): Promise<RollAppraiserUtils> {
  return loadRollAppraiserData();
}

import { useEffect, useState } from 'react';
import { RollAppraiserUtils, WeaponRankingData } from 'app/utils/rollAppraiserUtils';
import { DimItem } from 'app/inventory/item-types';
import { getRollAppraiserUtils, getRollAppraiserUtilsSync } from './rollAppraiserService';
import { getWeaponSockets, getSocketsByIndexes } from 'app/utils/socket-utils';

/**
 * Hook to get roll appraiser utils
 */
export function useRollAppraiserUtils() {
  const [utils, setUtils] = useState<RollAppraiserUtils | null>(getRollAppraiserUtilsSync());
  const [loading, setLoading] = useState(!utils);
  const [error, setError] = useState<Error | null>(null);

  useEffect(() => {
    if (!utils) {
      getRollAppraiserUtils()
        .then((loadedUtils) => {
          setUtils(loadedUtils);
          setLoading(false);
        })
        .catch((err) => {
          setError(err);
          setLoading(false);
        });
    }
  }, [utils]);

  return { utils, loading, error };
}

/**
 * Hook to get weapon ranking data for a specific item
 */
export function useWeaponRankingData(item: DimItem | null): WeaponRankingData | null {
  const { utils } = useRollAppraiserUtils();
  const [rankingData, setRankingData] = useState<WeaponRankingData | null>(null);

  useEffect(() => {
    if (!utils || !item || item.destinyVersion !== 2 || !item.bucket.inWeapons) {
      setRankingData(null);
      return;
    }

    // Get perk hashes from the weapon's sockets
    const perkHashes: number[] = [];
    const traitPerkHashes: number[] = [];
    let masterworkHash: number | undefined = undefined;

    if (item.sockets) {
      // Use getWeaponSockets to properly identify weapon perks
      const weaponSockets = getWeaponSockets(item, { excludeEmptySockets: false });

      if (weaponSockets?.perks) {
        // Get all perk sockets from the perks category
        const perkSockets = getSocketsByIndexes(item.sockets, weaponSockets.perks.socketIndexes);

        // Filter to only plugged perks
        const pluggedPerks = perkSockets.filter(s => s.plugged && s.isPerk);

        pluggedPerks.forEach((socket, index) => {
          if (socket.plugged) {
            perkHashes.push(socket.plugged.plugDef.hash);
            // Columns 3 and 4 (indexes 2 and 3) are typically trait perks
            if (index === 2 || index === 3) {
              traitPerkHashes.push(socket.plugged.plugDef.hash);
            }
          }
        });
      }

      // Check for masterwork
      for (const socket of item.sockets.allSockets) {
        if (socket.plugged && socket.plugged.plugDef.plug?.plugCategoryIdentifier === 'intrinsics.stat') {
          masterworkHash = socket.plugged.plugDef.hash;
        }
      }
    }

    const weaponData = utils.getWeaponData(
      item.hash.toString(),
      perkHashes.map(String),
      masterworkHash?.toString()
    );

    // Override trait combo ranking if we have specific trait perks identified
    if (traitPerkHashes.length === 2 && weaponData) {
      const comboRank = utils.getTraitComboRank(
        item.hash.toString(),
        traitPerkHashes[0].toString(),
        traitPerkHashes[1].toString()
      );
      if (!comboRank) {
        // Debug: log what we're looking for
        console.log('No combo rank found for weapon:', item.name, {
          itemHash: item.hash,
          perk4: traitPerkHashes[0],
          perk5: traitPerkHashes[1],
          allPerks: perkHashes,
          hasTraitData: Boolean(utils.hasDataForWeapon(item.hash.toString())),
        });
      } else {
        console.log('Found combo rank for weapon:', item.name, {
          rank: comboRank.rank,
          perk4: comboRank.perk4Hash,
          perk5: comboRank.perk5Hash,
          count: comboRank.count,
        });
      }
      weaponData.traitComboRanking = comboRank;
    }

    setRankingData(weaponData);
  }, [utils, item]);

  return rankingData;
}

/**
 * Hook to check if a weapon has any ranking data
 */
export function useHasRankingData(item: DimItem | null): boolean {
  const { utils } = useRollAppraiserUtils();

  if (!utils || !item || item.destinyVersion !== 2 || !item.bucket.inWeapons) {
    return false;
  }

  return utils.hasDataForWeapon(item.hash.toString());
}

import { Search, SearchType } from '@destinyitemmanager/dim-api-types';
import { saveSearch, searchDeleted } from 'app/d2l-api/basic-actions';
import { recentSearchesSelector } from 'app/d2l-api/selectors';
import RadioButtons, { Option } from 'app/d2l-ui/RadioButtons';
import { ColumnSort, SortDirection, useTableColumnSorts } from 'app/d2l-ui/table-columns';
import { t } from 'app/i18next-t';
import {
  AppIcon,
  closeIcon,
  faCaretDown,
  faCaretUp,
  starIcon,
  starOutlineIcon,
} from 'app/shell/icons';
import { useThunkDispatch } from 'app/store/thunk-dispatch';
import { Comparator, chainComparator, compareBy, reverseComparator } from 'app/utils/comparators';
import { useShiftHeld } from 'app/utils/hooks';
import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import styles from './SearchHistory.m.scss';

function comparatorFor(id: string): Comparator<Search> {
  switch (id) {
    case 'last_used':
      return compareBy((s) => s.lastUsage);
    case 'starred':
      return compareBy((s) => s.saved);
    case 'times_used':
      return compareBy((s) => s.usageCount);
    case 'query':
      return compareBy((s) => s.query);
    default:
      throw new Error(`internal error, unhandled column ${id}`);
  }
}

export default function SearchHistory() {
  const dispatch = useThunkDispatch();
  const [searchType, setSearchType] = useState(SearchType.Item);
  const recentSearches = useSelector(recentSearchesSelector(searchType));

  const [columnSorts, toggleColumnSort] = useTableColumnSorts([
    { columnId: 'starred', sort: SortDirection.DESC },
    { columnId: 'last_used', sort: SortDirection.DESC },
  ]);

  const shiftHeld = useShiftHeld();

  const deleteSearch = (e: React.MouseEvent, item: Search) => {
    e.stopPropagation();
    dispatch(searchDeleted({ query: item.query, type: item.type }));
  };

  const toggleSaved = (item: Search) => {
    dispatch(saveSearch({ query: item.query, saved: !item.saved, type: item.type }));
  };

  const onDeleteAll = () => {
    for (const s of recentSearches.filter((s) => !s.saved)) {
      dispatch(searchDeleted({ query: s.query, type: s.type }));
    }
  };

  const onToggleSort = (columnId: string, defaultDirection: SortDirection) =>
    toggleColumnSort(columnId, shiftHeld, defaultDirection);

  const headers: [string, React.ReactNode, SortDirection][] = [
    ['times_used', t('SearchHistory.UsageCount'), SortDirection.DESC],
    ['starred', <AppIcon key="star" icon={starIcon} />, SortDirection.DESC],
    ['query', t('SearchHistory.Query'), SortDirection.ASC],
  ];

  const searchComparator = chainComparator(
    ...columnSorts.map((sort) =>
      sort.sort === SortDirection.DESC
        ? reverseComparator(comparatorFor(sort.columnId))
        : comparatorFor(sort.columnId),
    ),
  );

  const radioOptions: Option<SearchType>[] = [
    { label: t('SearchHistory.Item'), value: SearchType.Item },
    { label: t('SearchHistory.Loadout'), value: SearchType.Loadout },
  ];

  // TODO: Tabs
  return (
    <div className={styles.searchHistory}>
      <p className={styles.instructions}>
        {t('SearchHistory.Description')}
        <button type="button" className="d2l-button" onClick={onDeleteAll}>
          {t('SearchHistory.DeleteAll')}
        </button>
        <RadioButtons
          className={styles.tabs}
          options={radioOptions}
          value={searchType}
          onChange={setSearchType}
        />
      </p>
      <table>
        <thead>
          <tr>
            <th />
            {headers.map(([id, contents, defaultDirection]) => (
              <ColumnHeader
                columnSorts={columnSorts}
                defaultDirection={defaultDirection}
                toggleColumnSort={onToggleSort}
                columnId={id}
                key={id}
              >
                {contents}
              </ColumnHeader>
            ))}
          </tr>
        </thead>
        <tbody>
          {recentSearches
            .filter((s) => s.usageCount > 0)
            .sort(searchComparator)
            .map((search) => (
              <tr key={search.query}>
                <td>
                  <button
                    type="button"
                    onClick={(e) => deleteSearch(e, search)}
                    title={t('Header.DeleteSearch')}
                    className={styles.iconButton}
                  >
                    <AppIcon icon={closeIcon} />
                  </button>
                </td>
                <td>{search.usageCount}</td>
                <td>
                  <button
                    type="button"
                    className={styles.iconButton}
                    onClick={() => toggleSaved(search)}
                    title={t('Header.SaveSearch')}
                  >
                    <AppIcon icon={search.saved ? starIcon : starOutlineIcon} />
                  </button>
                </td>
                <td>{search.query}</td>
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
}

function ColumnHeader({
  columnId,
  children,
  defaultDirection,
  columnSorts,
  toggleColumnSort,
}: {
  columnId: string;
  children: React.ReactNode;
  defaultDirection: SortDirection;
  columnSorts: ColumnSort[];
  toggleColumnSort: (columnId: string, direction: SortDirection) => () => void;
}) {
  const sort = columnSorts.find((c) => c.columnId === columnId);
  return (
    <th onClick={toggleColumnSort(columnId, defaultDirection)}>
      {children}
      {sort && (
        <AppIcon
          className={styles.sorter}
          icon={sort.sort === SortDirection.DESC ? faCaretDown : faCaretUp}
        />
      )}
    </th>
  );
}

import { CustomStatDef } from '@destinyitemmanager/dim-api-types';
import { stripAdept } from 'app/compare/compare-utils';
import { tl } from 'app/i18next-t';
import { TagValue } from 'app/inventory/d2l-item-info';
import { DimItem } from 'app/inventory/item-types';
import { getRollAppraiserUtilsSync } from 'app/roll-appraiser/rollAppraiserService';
import { DEFAULT_SHADER, armorStats } from 'app/search/d2-known-values';
import { chainComparator, compareBy, reverseComparator } from 'app/utils/comparators';
import { isArtifice, isClassCompatible } from 'app/utils/item-utils';
import { getSocketsByType, getWeaponSockets } from 'app/utils/socket-utils';
import { DestinyClass } from 'bungie-api-ts/destiny2';
import { BucketHashes } from 'data/d2/generated-enums';
import { ItemFilterDefinition } from '../item-filter-types';
import { PerksSet } from './perks-set';
import { StatsSet } from './stats-set';

const notableTags = ['keep'];

/** outputs a string combination of the identifying features of an item, or the hash if classified */
export const makeDupeID = (item: DimItem) =>
  (item.classified && `${item.hash}`) ||
  `${
    // Consider adept versions of weapons to be the same as the normal type
    item.bucket.inWeapons ? stripAdept(item.name) : item.name
  }${
    // Some items have the same name across different classes, e.g. "Kairos Function Boots"
    item.classType
  }${
    // Some items have the same name across different tiers, e.g. "Traveler's Chosen"
    item.rarity
  }${
    // The engram that dispenses the Taraxippos scout rifle is also called Taraxippos
    item.bucket.hash
  }`;

export const sortDupes = (
  dupes: {
    [dupeID: string]: DimItem[];
  },
  getTag: (item: DimItem) => TagValue | undefined,
  sortType: 'power' | 'combo' | 'dupebest' = 'power',
) => {
  // The comparator for sorting dupes - the first item will be the "best" and all others are "dupelower".
  const dupeComparator = sortType === 'dupebest'
    ? chainComparator<DimItem>(
        // 1. Best Combo Rank (for weapons) - lower rank numbers are better
        compareBy((item) => {
          if (item.bucket.inWeapons && item.sockets) {
            const utils = getRollAppraiserUtilsSync();
            if (!utils) {return Number.MAX_SAFE_INTEGER;} // No data = worst

            const traitPerks = getSocketsByType(item, 'traits');
            if (traitPerks.length >= 2) {
              const perk4Hash = traitPerks[0]?.plugged?.plugDef.hash;
              const perk5Hash = traitPerks[1]?.plugged?.plugDef.hash;

              if (perk4Hash && perk5Hash) {
                const comboRank = utils.getTraitComboRank(item.hash.toString(), perk4Hash, perk5Hash);
                if (comboRank) {
                  // Return the rank directly - lower numbers are better
                  return comboRank.rank;
                }
              }
            }
          }
          return Number.MAX_SAFE_INTEGER; // Non-weapons or items without combo data go last
        }),
        // 2. Highest Power - higher is better, so negate to sort descending
        compareBy((item) => -item.power),
        // 3. Tag priority - true (has tag) is better, so negate
        compareBy((item) => {
          const tag = getTag(item);
          return !(tag && notableTags.includes(tag));
        }),
        // 4. Masterwork status - true is better, so negate
        compareBy((item) => !item.masterwork),
        // 5. Lock status - true is better, so negate
        compareBy((item) => !item.locked),
        // 6. Item ID tiebreaker
        compareBy((i) => i.id),
      )
    : reverseComparator(
        chainComparator<DimItem>(
          // primary comparison based on sort type
          sortType === 'combo'
            ? compareBy((item) => {
                // For weapons, use combo rank (lower rank number = better, so higher rank numbers should be "dupelower")
                if (item.bucket.inWeapons && item.sockets) {
                  const utils = getRollAppraiserUtilsSync();
                  if (!utils) {return -item.power;} // Fallback to power if no roll appraiser data

                  const traitPerks = getSocketsByType(item, 'traits');
                  if (traitPerks.length >= 2) {
                    const perk4Hash = traitPerks[0]?.plugged?.plugDef.hash;
                    const perk5Hash = traitPerks[1]?.plugged?.plugDef.hash;

                    if (perk4Hash && perk5Hash) {
                      const comboRank = utils.getTraitComboRank(item.hash.toString(), perk4Hash, perk5Hash);
                      if (comboRank) {
                        // Return negative rank so lower rank numbers (better combos) sort first
                        // This means higher rank numbers (worse combos) will be considered "dupelower"
                        return -comboRank.rank;
                      }
                    }
                  }
                }
                // Fallback to power for non-weapons or items without combo data
                return -item.power;
              })
            : compareBy((item) => item.power),
          compareBy((item) => {
            const tag = getTag(item);
            return Boolean(tag && notableTags.includes(tag));
          }),
          compareBy((item) => item.masterwork),
          compareBy((item) => item.locked),
          compareBy((i) => i.id), // tiebreak by ID
        ),
      );

  for (const dupeList of Object.values(dupes)) {
    if (dupeList.length > 1) {
      dupeList.sort(dupeComparator);
    }
  }

  return dupes;
};

/**
 * Sort duplicates for "dupebest" search - best item first.
 * For weapons: Use combo rank (lower rank number = better)
 * For armor: Use custom stat value (higher value = better) when available, otherwise use power
 */
export const sortDupesBest = (
  dupes: {
    [dupeID: string]: DimItem[];
  },
  getTag: (item: DimItem) => TagValue | undefined,
  customStats: CustomStatDef[],
) => {
  const dupeComparator = chainComparator<DimItem>(
    // 1. For armor: prioritize highest custom stat value if custom stats exist
    compareBy((item) => {
      if (item.bucket.inArmor && customStats.length > 0) {
        // Find the highest custom stat value for this item
        let highestCustomStatValue = 0;
        for (const customStat of customStats) {
          if (isClassCompatible(customStat.class, item.classType)) {
            const stat = item.stats?.find(s => s.statHash === customStat.statHash);
            if (stat && stat.value > highestCustomStatValue) {
              highestCustomStatValue = stat.value;
            }
          }
        }
        // Return negative value so higher custom stat values are sorted first
        return -highestCustomStatValue;
      }
      return 0; // No priority for non-armor or when no custom stats exist
    }),
    // 2. Best Combo Rank (for weapons) - lower rank numbers are better
    compareBy((item) => {
      if (item.bucket.inWeapons && item.sockets) {
        const utils = getRollAppraiserUtilsSync();
        if (!utils) {return Number.MAX_SAFE_INTEGER;}

        const traitPerks = getSocketsByType(item, 'traits');
        if (traitPerks.length >= 2) {
          const perk4Hash = traitPerks[0]?.plugged?.plugDef.hash;
          const perk5Hash = traitPerks[1]?.plugged?.plugDef.hash;

          if (perk4Hash && perk5Hash) {
            const comboRank = utils.getTraitComboRank(item.hash.toString(), perk4Hash, perk5Hash);
            if (comboRank) {
              return comboRank.rank;
            }
          }
        }
      }
      return Number.MAX_SAFE_INTEGER;
    }),
    // 3. Weighted average of perk ranks in first two columns (tie-breaker for combo rank)
    compareBy((item) => {
      if (item.bucket.inWeapons && item.sockets) {
        const utils = getRollAppraiserUtilsSync();
        if (!utils) {return Number.MAX_SAFE_INTEGER;}

        // Get weapon sockets properly categorized
        const weaponSockets = getWeaponSockets(item, { excludeEmptySockets: false });
        if (!weaponSockets) {return 0;}

        // Get the first two perk sockets with multiple options (like Compare.tsx does)
        const allPerkSockets = item.sockets.allSockets
          .filter((s) => {
            // Must be a perk socket with multiple options
            if (!s.isPerk || s.plugOptions.length <= 1) {return false;}

            // Exclude intrinsic socket
            if (weaponSockets.intrinsicSocket && s.socketIndex === weaponSockets.intrinsicSocket.socketIndex) {
              return false;
            }

            return true;
          })
          .sort((a, b) => a.socketIndex - b.socketIndex);

        // Use the first two perk sockets (which should be columns 1 & 2)
        if (allPerkSockets.length >= 2) {
          // Find the best rank among all available perks in each column
          let bestRank1 = Number.MAX_SAFE_INTEGER;
          let bestRank2 = Number.MAX_SAFE_INTEGER;

          // Check all plug options in first column
          for (const plug of allPerkSockets[0].plugOptions) {
            const perkRank = utils.getPerkRank(item.hash.toString(), plug.plugDef.hash);
            if (perkRank && perkRank.rank < bestRank1) {
              bestRank1 = perkRank.rank;
            }
          }

          // Check all plug options in second column
          for (const plug of allPerkSockets[1].plugOptions) {
            const perkRank = utils.getPerkRank(item.hash.toString(), plug.plugDef.hash);
            if (perkRank && perkRank.rank < bestRank2) {
              bestRank2 = perkRank.rank;
            }
          }

          if (bestRank1 !== Number.MAX_SAFE_INTEGER && bestRank2 !== Number.MAX_SAFE_INTEGER) {
            // Weight calculation: 1=2.5, 2=2.0, 3=1.5, 4+=1.0
            const getWeight = (rank: number) => {
              switch (rank) {
                case 1: return 2.5;
                case 2: return 2.0;
                case 3: return 1.5;
                default: return 1.0;
              }
            };

            const weight1 = getWeight(bestRank1);
            const weight2 = getWeight(bestRank2);
            const weightedAverage = (weight1 + weight2) / 2;

            // Return negative so higher averages (better) sort first
            return -weightedAverage;
          }
        }
      }
      return 0; // No penalty for non-weapons or items without perk data
    }),
    // 4. Highest Power
    compareBy((item) => -item.power),
    // 5. Tag priority
    compareBy((item) => {
      const tag = getTag(item);
      return !(tag && notableTags.includes(tag));
    }),
    // 6. Masterwork status
    compareBy((item) => !item.masterwork),
    // 7. Lock status
    compareBy((item) => !item.locked),
    // 8. Item ID tiebreaker
    compareBy((i) => i.id),
  );

  for (const dupeList of Object.values(dupes)) {
    if (dupeList.length > 1) {
      dupeList.sort(dupeComparator);
    }
  }

  return dupes;
};

const computeDupesByIdFn = (allItems: DimItem[], makeDupeIdFn: (item: DimItem) => string) => {
  // Holds a map from item hash to count of occurrences of that hash
  const duplicates: { [dupeID: string]: DimItem[] } = {};

  for (const i of allItems) {
    if (!i.comparable) {
      continue;
    }
    const dupeID = makeDupeIdFn(i);
    if (!duplicates[dupeID]) {
      duplicates[dupeID] = [];
    }
    duplicates[dupeID].push(i);
  }

  return duplicates;
};

/**
 * Find a map of duplicate items using the makeDupeID function.
 */
export const computeDupes = (allItems: DimItem[]) => computeDupesByIdFn(allItems, makeDupeID);

/**
 * Create a dupe ID for armor based on class + type + tier, ignoring specific item name/hash.
 * For exotic armor, also include perks since the perk combination determines uniqueness.
 * This groups all armor pieces of the same class, type, and tier together.
 */
export const makeArmorClassTypeTierDupeID = (item: DimItem) => {
  if (!item.bucket.inArmor) {
    return makeDupeID(item); // Fallback to normal behavior for non-armor
  }

  let dupeId = `${item.classType}-${item.bucket.hash}-${item.rarity}`;

  // For exotic armor, include perks in the dupe ID since perk combinations determine uniqueness
  if (item.isExotic && item.sockets?.allSockets) {
    const perkHashes: number[] = [];

    // Get all perk sockets (intrinsic and regular perks)
    for (const socket of item.sockets.allSockets) {
      if (socket.isPerk && socket.plugged?.plugDef.hash) {
        // Include perk hash to differentiate exotic armor by their perk combinations
        perkHashes.push(socket.plugged.plugDef.hash);
      }
    }

    // Sort perk hashes for consistent ordering
    perkHashes.sort((a, b) => a - b);

    if (perkHashes.length > 0) {
      dupeId += `-perks:${perkHashes.join(',')}`;
    }
  }

  return dupeId;
};

/**
 * Compute armor duplicates grouped by class/type/tier instead of itemHash.
 * This allows finding the best armor piece per class/type/tier combination.
 */
export const computeArmorDupesByClassTypeTier = (allItems: DimItem[]) => {
  const armorItems = allItems.filter(item => item.bucket.inArmor);
  return computeDupesByIdFn(armorItems, makeArmorClassTypeTierDupeID);
};

const dupeFilters: ItemFilterDefinition[] = [
  {
    keywords: 'dupe',
    description: tl('Filter.Dupe'),
    filter: ({ allItems }) => {
      const duplicates = computeDupes(allItems);
      return (item) => {
        const dupeId = makeDupeID(item);
        return checkIfIsDupe(duplicates, dupeId, item);
      };
    },
  },
  {
    keywords: 'dupelower',
    description: tl('Filter.DupeLower'),
    format: ['simple', 'query'],
    suggestions: ['power', 'combo'],
    filter: ({ filterValue, allItems, getTag }) => {
      // Default to 'power' if no specific value is provided
      const sortType = filterValue === 'combo' ? 'combo' : 'power';
      const duplicates = sortDupes(computeDupes(allItems), getTag, sortType);

      return (item) => {
        if (
          !(
            item.bucket &&
            (item.bucket.sort === 'Weapons' || item.bucket.sort === 'Armor') &&
            !item.notransfer
          )
        ) {
          return false;
        }

        const dupeId = makeDupeID(item);
        const dupes = duplicates[dupeId];
        if (dupes?.length > 1) {
          const bestDupe = dupes[0];
          return item !== bestDupe;
        }

        return false;
      };
    },
  },
  {
    keywords: 'infusionfodder',
    description: tl('Filter.InfusionFodder'),
    destinyVersion: 2,
    filter: ({ allItems }) => {
      const duplicates = computeDupesByIdFn(
        allItems.filter((i) => i.infusionFuel),
        (i) => i.hash.toString(),
      );
      return (item) => {
        if (!item.infusionFuel) {
          return false;
        }

        return duplicates[item.hash.toString()]?.some((i) => i.power < item.power);
      };
    },
  },
  {
    keywords: 'count',
    description: tl('Filter.DupeCount'),
    format: 'range',
    filter: ({ compare, allItems }) => {
      const duplicates = computeDupes(allItems);
      return (item) => {
        const dupeId = makeDupeID(item);
        return compare!(duplicates[dupeId]?.length ?? 0);
      };
    },
  },
  {
    keywords: 'statlower',
    description: tl('Filter.StatLower'),
    filter: ({ allItems }) => {
      const duplicates = computeStatDupeLower(allItems);
      return (item) => item.bucket.inArmor && duplicates.has(item.id);
    },
  },
  {
    keywords: 'customstatlower',
    description: tl('Filter.CustomStatLower'),
    filter: ({ allItems, customStats }) => {
      const duplicateSetsByClass: Partial<Record<DimItem['classType'], Set<string>[]>> = {};

      for (const customStat of customStats) {
        const relevantStatHashes: number[] = [];
        const statWeights = customStat.weights;
        for (const statHash in statWeights) {
          const weight = statWeights[statHash];
          if (weight && weight > 0) {
            relevantStatHashes.push(parseInt(statHash, 10));
          }
        }
        (duplicateSetsByClass[customStat.class] ||= []).push(
          computeStatDupeLower(allItems, relevantStatHashes),
        );
      }

      return (item) =>
        item.bucket.inArmor &&
        // highlight the item if it's statlower for all class-relevant custom stats.
        // this duplicates existing behavior for old style default-named custom stat,
        // but should be extended to also be a stat name-based filter
        // for users with multiple stats per class, a la customstatlower:pve
        duplicateSetsByClass[item.classType]?.every((dupeSet) => dupeSet.has(item.id));
    },
  },
  {
    keywords: ['crafteddupe', 'shapeddupe'],
    description: tl('Filter.CraftedDupe'),
    filter: ({ allItems }) => {
      const duplicates = computeDupes(allItems);
      return (item) => {
        const dupeId = makeDupeID(item);
        if (!checkIfIsDupe(duplicates, dupeId, item)) {
          return false;
        }
        const itemDupes = duplicates?.[dupeId];
        return itemDupes?.some((d) => d.crafted);
      };
    },
  },
  {
    keywords: ['dupeperks'],
    description: tl('Filter.DupePerks'),
    filter: ({ allItems }) => {
      const duplicates = new Map<string, PerksSet>();
      function getDupeId(item: DimItem) {
        // Don't compare across buckets or across types (e.g. Titan armor vs Hunter armor)
        return `${item.bucket.hash}|${item.classType}`;
      }
      for (const i of allItems) {
        if (i.sockets?.allSockets.some((s) => s.isPerk && s.socketDefinition.defaultVisible)) {
          const dupeId = getDupeId(i);
          if (!duplicates.has(dupeId)) {
            duplicates.set(dupeId, new PerksSet());
          }
          duplicates.get(dupeId)!.insert(i);
        }
      }
      return (item) =>
        item.sockets?.allSockets.some((s) => s.isPerk && s.socketDefinition.defaultVisible) &&
        Boolean(duplicates.get(getDupeId(item))?.hasPerkDupes(item));
    },
  },
  {
    keywords: 'dupebest',
    description: tl('Filter.DupeBest'),
    filter: ({ allItems, getTag, customStats }) => {
      // For armor, group by class/type/tier instead of itemHash
      const armorDupes = computeArmorDupesByClassTypeTier(allItems);
      const armorDupesSorted = sortDupesBest(armorDupes, getTag, customStats);

      // For non-armor, use the original itemHash-based grouping
      const nonArmorItems = allItems.filter(item => !item.bucket.inArmor);
      const nonArmorDupes = computeDupes(nonArmorItems);
      const nonArmorDupesSorted = sortDupesBest(nonArmorDupes, getTag, customStats);

      return (item) => {
        if (item.bucket.inArmor) {
          // Use class/type/tier grouping for armor
          const dupeId = makeArmorClassTypeTierDupeID(item);
          const dupes = armorDupesSorted[dupeId];
          if (dupes?.length > 1) {
            const bestDupe = dupes[0];
            return item === bestDupe;
          }
        } else {
          // Use itemHash grouping for non-armor
          const dupeId = makeDupeID(item);
          const dupes = nonArmorDupesSorted[dupeId];
          if (dupes?.length > 1) {
            const bestDupe = dupes[0];
            return item === bestDupe;
          }
        }
        return false;
      };
    },
  },
  {
    keywords: 'bestarmor',
    description: tl('Filter.BestArmor'),
    filter: ({ allItems, getTag, customStats }) => {
      // Only process armor items
      const armorItems = allItems.filter(item => item.bucket.inArmor);
      const armorDupes = computeArmorDupesByClassTypeTier(armorItems);
      const armorDupesSorted = sortDupesBest(armorDupes, getTag, customStats);

      return (item) => {
        // Only match armor items
        if (!item.bucket.inArmor) {
          return false;
        }

        const dupeId = makeArmorClassTypeTierDupeID(item);
        const dupes = armorDupesSorted[dupeId];

        if (dupes && dupes.length >= 1) {
          // Return true if this item is the best in its group (first after sorting)
          // This includes single items (length 1) and best of duplicates (length > 1)
          const bestDupe = dupes[0];
          return item === bestDupe;
        }

        return false;
      };
    },
  },
];

export default dupeFilters;

export function checkIfIsDupe(
  duplicates: {
    [dupeID: string]: DimItem[];
  },
  dupeId: string,
  item: DimItem,
) {
  return (
    duplicates[dupeId]?.length > 1 &&
    item.hash !== DEFAULT_SHADER &&
    item.bucket.hash !== BucketHashes.SeasonalArtifact
  );
}

/**
 * Compute a set of items that are "stat lower" dupes. These are items for which
 * there exists another item with strictly better stats (i.e. better in at least
 * one stat and not worse in any stat).
 */
function computeStatDupeLower(allItems: DimItem[], relevantStatHashes: number[] = armorStats) {
  // disregard no-class armor
  const armor = allItems.filter((i) => i.bucket.inArmor && i.classType !== DestinyClass.Classified);

  // Group by class and armor type. Also, compare exotics with each other, not the general pool.
  const grouped = Object.values(
    Object.groupBy(armor, (i) => `${i.bucket.hash}-${i.classType}-${i.isExotic ? i.hash : ''}`),
  );

  const dupes = new Set<string>();

  // A mapping from an item to a list of all of its stat configurations
  // (Artifice armor can have multiple). This is just a cache to prevent
  // recalculating it.
  const statsCache = new Map<DimItem, number[][]>();
  for (const item of armor) {
    if (item.stats && item.power) {
      const statValues = item.stats
        .filter((s) => relevantStatHashes.includes(s.statHash))
        .sort((a, b) => a.statHash - b.statHash)
        .map((s) => s.base);
      if (isArtifice(item)) {
        statsCache.set(
          item,
          // Artifice armor can be +3 in any one stat, so we compute a separate
          // version of the stats for each stat considered
          relevantStatHashes.map((_s, i) => {
            const modifiedStats = [...statValues];
            // One stat gets +3
            modifiedStats[i] += 3;
            return modifiedStats;
          }),
        );
      } else {
        statsCache.set(item, [statValues]);
      }
    }
  }

  // For each group of items that should be compared against each other
  for (const group of grouped) {
    if (!group) { // Add check for undefined group
      continue;
    }
    const statSet = new StatsSet<DimItem>();
    // Add a mapping from stats => item to the statsSet for each item in the group
    for (const item of group) {
      const stats = statsCache.get(item);
      if (stats) {
        for (const statValues of stats) {
          statSet.insert(statValues, item);
        }
      }
    }

    // Now run through the items in the group again, checking against the fully
    // populated stats set to see if there's something better
    for (const item of group) {
      const stats = statsCache.get(item);
      // All configurations must have a better version somewhere for this to count as statlower
      if (stats?.every((statValues) => statSet.doBetterStatsExist(statValues))) {
        dupes.add(item.id);
      }
    }
  }

  return dupes;
}

import { tl } from 'app/i18next-t';
import { DimItem } from 'app/inventory/item-types';
import { testStringsFromAllSockets } from 'app/search/items/search-filters/freeform';
import { matchText } from 'app/search/text-utils';
import { ItemFilterDefinition } from '../item-filter-types';
import { perkStatMappings, getPerkStatHashes, isValidPerkName, PerkName, displayStatToHashMap } from './perk-stat-mappings';

/**
 * Filter that finds the best armor piece for a given perk based on optimized primary/secondary stats
 */
const perkOptimizationFilter: ItemFilterDefinition = {
  keywords: 'bestforperk',
  description: tl('Filter.BestForPerk'),
  format: 'query',
  suggestions: Object.keys(perkStatMappings),
  destinyVersion: 2,
  filter: ({ filterValue, allItems, language, d2Definitions }) => {
    console.log('ðŸ”¥ bestforperk filter called with:', filterValue);
    console.log('ðŸ”¥ allItems count:', allItems.length);
    console.log('ðŸ”¥ d2Definitions:', !!d2Definitions);
    
    // Get the stat hashes for this perk, or use defaults if not in our mapping
    let primaryStatHash: number;
    let secondaryStatHash: number;
    
    if (isValidPerkName(filterValue)) {
      const statHashes = getPerkStatHashes(filterValue);
      primaryStatHash = statHashes.primary;
      secondaryStatHash = statHashes.secondary;
    } else {
      // For perks not in our mapping, we need to determine stats based on the perk name
      // You can expand this logic based on actual perk names you discover
      if (filterValue.toLowerCase().includes('brawler')) {
        // Brawler perks should optimize for Melee (primary) and Health (secondary)
        primaryStatHash = displayStatToHashMap['Melee'];
        secondaryStatHash = displayStatToHashMap['Health'];
      } else {
        // Default fallback - optimize for Health and Melee
        primaryStatHash = displayStatToHashMap['Health'];
        secondaryStatHash = displayStatToHashMap['Melee'];
      }
    }

    // Find all items that have the specified perk
    const perkTest = matchText(filterValue, language, /* exact */ false);
    const itemsWithPerk = allItems.filter(item => 
      item.bucket.inArmor && 
      item.stats &&
      testStringsFromAllSockets(perkTest, item, d2Definitions, /* includeDescription */ false)
    );

    // Debug logging
    if ($D2L_FLAVOR === 'dev') {
      console.log(`Looking for perk: "${filterValue}"`);
      console.log(`Found ${itemsWithPerk.length} items with this perk`);
      if (itemsWithPerk.length > 0) {
        console.log('Sample items:', itemsWithPerk.slice(0, 3).map(i => i.name));
      }
    }

    if (itemsWithPerk.length === 0) {
      return () => false;
    }

    // Group items by slot and class
    const itemsBySlotClass: Record<string, DimItem[]> = {};
    for (const item of itemsWithPerk) {
      const key = `${item.bucket.hash}-${item.classType}`;
      if (!itemsBySlotClass[key]) {
        itemsBySlotClass[key] = [];
      }
      itemsBySlotClass[key].push(item);
    }

    // For each slot+class group, find the best item(s) based on primary stat, then secondary stat
    const bestItemIds = new Set<string>();

    for (const [slotClass, items] of Object.entries(itemsBySlotClass)) {
      if (items.length === 0) continue;

      // Sort by primary stat (descending), then by secondary stat (descending)
      const sortedItems = items.sort((a, b) => {
        const aPrimaryStat = a.stats?.find(s => s.statHash === primaryStatHash)?.base || 0;
        const bPrimaryStat = b.stats?.find(s => s.statHash === primaryStatHash)?.base || 0;
        
        if (aPrimaryStat !== bPrimaryStat) {
          return bPrimaryStat - aPrimaryStat; // Higher primary stat wins
        }
        
        // If primary stats are tied, compare secondary stats
        const aSecondaryStat = a.stats?.find(s => s.statHash === secondaryStatHash)?.base || 0;
        const bSecondaryStat = b.stats?.find(s => s.statHash === secondaryStatHash)?.base || 0;
        
        return bSecondaryStat - aSecondaryStat; // Higher secondary stat wins
      });

      // Add all items that tie for the best stats
      const bestItem = sortedItems[0];
      const bestPrimaryStat = bestItem.stats?.find(s => s.statHash === primaryStatHash)?.base || 0;
      const bestSecondaryStat = bestItem.stats?.find(s => s.statHash === secondaryStatHash)?.base || 0;

      for (const item of sortedItems) {
        const itemPrimaryStat = item.stats?.find(s => s.statHash === primaryStatHash)?.base || 0;
        const itemSecondaryStat = item.stats?.find(s => s.statHash === secondaryStatHash)?.base || 0;
        
        if (itemPrimaryStat === bestPrimaryStat && itemSecondaryStat === bestSecondaryStat) {
          bestItemIds.add(item.id);
        } else {
          break; // No more ties
        }
      }
    }

    return (item: DimItem) => bestItemIds.has(item.id);
  },
};

export default [perkOptimizationFilter];

