    filter: ({ compare }) => (item) => {
      const info = getWeaponRankingInfo(item);
      return info?.reviewSummary ? compare!(info.reviewSummary.pvpAverage) : false;
    },
  },

  {
    keywords: 'overallscore',
    description: undefined as any,
