import { languageSelector } from 'app/d2l-api/selectors';
import ExternalLink from 'app/d2l-ui/ExternalLink';
import { DimItem } from 'app/inventory/item-types';
import { compact } from 'app/utils/collections';
import { isKillTrackerSocket } from 'app/utils/item-utils';
import { getSocketsWithStyle, isWeaponMasterworkSocket } from 'app/utils/socket-utils';
import { DestinySocketCategoryStyle } from 'bungie-api-ts/destiny2';
import { ItemCategoryHashes, PlugCategoryHashes } from 'data/d2/generated-enums';
import logo from 'images/d2llogo.svg';
import lightgg from 'images/lightgg.png';
import { useSelector } from 'react-redux';
import styles from './Links.m.scss';

export default function Links({ item }: { item: DimItem }) {
  const language = useSelector(languageSelector);

  const links = [
    {
      name: 'D2L',
      icon: logo as string,
      link: `/armory/${item.hash}?perks=${buildSocketParam(item)}`,
    },
    {
      name: 'Light.gg',
      icon: lightgg as string,
      link: `https://www.light.gg/db/${language}/items/${item.hash}${buildLightGGSockets(item)}`,
    },
  ] as const;

  return (
    <ul className={styles.links}>
      {compact(links).map(({ link, name, icon }) => (
        <li key={name}>
          <ExternalLink href={link}>
            <img src={icon} height={16} width={16} />
            {name}
          </ExternalLink>
        </li>
      ))}
    </ul>
  );
}

/**
 * Build a comma-separated list of perks where each entry in the list corresponds to a socket ID and the value is the plugged item hash. A zero corresponds to "no choice".
 */
function buildSocketParam(item: DimItem): string {
  const perkValues: number[] = [];

  if (item.sockets) {
    for (const socket of item.sockets.allSockets) {
      perkValues[socket.socketIndex] = socket.plugged?.plugDef.hash ?? 0;
    }
  }

  // Fill in those empty array elements
  for (let i = 0; i < perkValues.length; i++) {
    perkValues[i] ||= 0;
  }

  return perkValues.join(',');
}

/**
 * Light.gg's socket format is highly similar to that of D2Gunsmith: [...base perks, masterwork, weapon mod].join(',')
 */
function buildLightGGSockets(item: DimItem) {
  const perkValues = getWeaponSocketInfo(item);

  if (perkValues) {
    return `?p=${[...perkValues.largePerks, ...perkValues.traits, perkValues.masterwork, perkValues.weaponMod].map((s) => s || '').join(',')}`;
  }

  return '';
}

/**
 * Gathers general socket information for link generation in D2Gunsmith and Light.gg.
 */
function getWeaponSocketInfo(item: DimItem): null | {
  traits: number[];
  masterwork: number;
  weaponMod: number;
  largePerks: number[];
} {
  if (item.sockets && item.bucket?.inWeapons) {
    // TODO: Map enhanced intrinsic frames with their corresponding stat masterworks
    const masterworkSocket = item.sockets.allSockets.find(
      (s) => isWeaponMasterworkSocket(s) && !s.isReusable,
    );
    const masterwork =
      masterworkSocket?.plugged?.plugDef.plug.plugCategoryHash ===
      PlugCategoryHashes.CraftingPlugsFrameIdentifiers
        ? 0
        : (masterworkSocket?.plugged?.plugDef.hash ?? 0);

    const weaponModSocket = item.sockets.allSockets.find((s) =>
      s.plugged?.plugDef.itemCategoryHashes?.includes(ItemCategoryHashes.WeaponModsDamage),
    );
    const weaponMod = weaponModSocket?.plugged?.plugDef.hash ?? 0;

    const trackerSocket = item.sockets.allSockets.find(isKillTrackerSocket);
    const largePerks = getSocketsWithStyle(item.sockets, DestinySocketCategoryStyle.LargePerk)
      .filter((s) => s.hasRandomizedPlugItems)
      .map((s) => s.plugged?.plugDef.hash ?? 0);
    const perkSockets = getSocketsWithStyle(item.sockets, DestinySocketCategoryStyle.Reusable);
    const traits = perkSockets
      .filter(
        (s) => ![trackerSocket?.socketIndex, weaponModSocket?.socketIndex].includes(s.socketIndex),
      )
      .map((s) => s.plugged?.plugDef.hash ?? 0);

    return { traits, masterwork, weaponMod, largePerks };
  }

  return null;
}

import { t } from 'app/i18next-t';
import { storesSelector } from 'app/inventory/selectors';
import {
  LoadoutUpdateFunction,
  setClassType,
  setClearSpace,
  setName,
  setNotes,
} from 'app/loadout-drawer/loadout-drawer-reducer';
import { Loadout } from 'app/loadout/loadout-types';
import { uniqBy } from 'app/utils/collections';
import { DestinyClass } from 'bungie-api-ts/destiny2';
import clsx from 'clsx';
import React from 'react';
import { useSelector } from 'react-redux';
import { createSelector } from 'reselect';
import styles from './LoadoutDrawerOptions.m.scss';

const classTypeOptionsSelector = createSelector(storesSelector, (stores) => {
  const classTypeValues: {
    label: string;
    value: DestinyClass;
  }[] = uniqBy(
    stores.filter((s) => !s.isVault),
    (store) => store.classType,
  ).map((store) => ({ label: store.className, value: store.classType }));
  return [{ label: t('Loadouts.Any'), value: DestinyClass.Unknown }, ...classTypeValues];
});

export default function LoadoutDrawerOptions({
  loadout,
  showClass,
  setLoadout,
}: {
  loadout: Readonly<Loadout>;
  showClass: boolean;
  setLoadout: (updater: LoadoutUpdateFunction) => void;
}) {
  const classTypeOptions = useSelector(classTypeOptionsSelector);

  const handleSetName = (e: React.ChangeEvent<HTMLInputElement>) =>
    setLoadout(setName(e.target.value));

  const handleSetClassType = (e: React.ChangeEvent<HTMLSelectElement>) =>
    setLoadout(setClassType(parseInt(e.target.value, 10)));

  const handleSetClearSpace = (
    e: React.ChangeEvent<HTMLInputElement>,
    category: 'Weapons' | 'Armor',
  ) => setLoadout(setClearSpace(e.target.checked, category));

  const addNotes = () => setLoadout(setNotes(''));

  return (
    <div className={styles.loadoutOptions}>
      <div className={clsx(styles.inputGroup, styles.loadoutName)}>
        <input
          className={styles.d2lInput}
          name="name"
          onChange={handleSetName}
          minLength={1}
          maxLength={50}
          required={true}
          type="text"
          value={loadout.name}
          placeholder={t('Loadouts.LoadoutName')}
        />
        {showClass && (
          <select name="classType" onChange={handleSetClassType} value={loadout.classType}>
            {classTypeOptions.map((option) => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        )}
      </div>
      {loadout.notes === undefined && (
        <div className={styles.inputGroup}>
          <button
            className="d2l-button"
            onClick={addNotes}
            type="button"
            title={t('Loadouts.AddNotes')}
          >
            {t('Loadouts.AddNotes')}
          </button>
        </div>
      )}
      <div className={styles.inputGroup}>
        <label>
          <input
            type="checkbox"
            checked={Boolean(loadout.parameters?.clearWeapons)}
            onChange={(e) => handleSetClearSpace(e, 'Weapons')}
          />{' '}
          {t('Loadouts.ClearSpaceWeapons')}
        </label>
        <label>
          <input
            type="checkbox"
            checked={Boolean(loadout.parameters?.clearArmor)}
            onChange={(e) => handleSetClearSpace(e, 'Armor')}
          />{' '}
          {t('Loadouts.ClearSpaceArmor')}
        </label>
      </div>
    </div>
  );
}

import {
  TagValue as ApiTagValue,
  ItemAnnotation,
  ItemHashTag,
} from '@destinyitemmanager/dim-api-types';
import { IconDefinition } from '@fortawesome/fontawesome-svg-core';
import { I18nKey, tl } from 'app/i18next-t';
import { ThunkResult } from 'app/store/types';
import { filterMap, isEmpty } from 'app/utils/collections';
import { infoLog, warnLog } from 'app/utils/log';
import { keyBy } from 'es-toolkit';
import { banIcon, tagIcon } from '../shell/icons';
import { setItemNote, setItemTag, tagCleanup } from './actions';
import { DimItem } from './item-types';
import { itemInfosSelector } from './selectors';
import { DimStore } from './store-types';

// sortOrder: orders items within a bucket, ascending
export const tagConfig = {
  keep: {
    type: 'keep' as const,
    label: tl('Tags.Keep'),
    sortOrder: 1,
    hotkey: 'shift+1',
    icon: tagIcon,
  },
  junk: {
    type: 'junk' as const,
    label: tl('Tags.Junk'),
    sortOrder: 2,
    hotkey: 'shift+2',
    icon: banIcon,
  },
  hotperk: {
    type: 'hotperk' as const,
    label: tl('Tags.Favorite'),
    sortOrder: 0,
    hotkey: 'shift+3',
    icon: tagIcon,
  },
};

export type TagValue = keyof typeof tagConfig;
export type TagCommand = TagValue | 'clear';

// Type conversion helpers
export function toApiTagValue(tag: TagValue | undefined): ApiTagValue | undefined {
  // The API expects different values, so we need to map them
  if (!tag) {
    return undefined;
  }
  // For now, we'll cast it as the API might accept our values
  return tag as any;
}

export function fromApiTagValue(tag: ApiTagValue | undefined): TagValue | undefined {
  if (!tag) {
    return undefined;
  }
  // Map API values back to our values
  if (tag === 'keep' || tag === 'junk') {
    return tag as TagValue;
  }
  // Map API tags to our supported tags
  if (tag === 'favorite') {
    return 'keep';
  } // Map favorite -> keep
  if (tag === 'archive') {
    return 'junk';
  }
  if (tag === 'infuse') {
    return 'junk';
  }
  // Handle any remaining old hotperk tags by converting to keep
  if (tag === 'hotperk') {
    return 'keep';
  }
  // Default for unknown tags
  return undefined;
}

/**
 * Priority order for which items should get moved off a character (into the vault or another character)
 * when the character is full and you want to move something new in. Tag values earlier in this list
 * are more likely to be moved.
 */
export const characterDisplacePriority: (TagValue | 'none')[] = [
  // Junk items should move to the vault
  'junk',
  'none',
  'keep',
];

/**
 * Priority order for which items should get moved out of the vault (onto a character)
 * when the vault is full and you want to move something new in. Tag values earlier in this list
 * are more likely to be moved.
 */
export const vaultDisplacePriority: (TagValue | 'none')[] = [
  // Junk should probably bubble towards the character so you remember to delete them!
  'junk',
  'none',
  'keep',
];

/**
 * Priority order for which items should get chosen to replace an equipped item.
 * Tag values earlier in this list are more likely to be chosen.
 */
export const equipReplacePriority: (TagValue | 'none')[] = ['keep', 'none', 'junk'];

export interface ItemInfos {
  [itemId: string]: ItemAnnotation;
}

export interface TagInfo {
  type?: TagValue;
  label: I18nKey;
  sortOrder?: number;
  displacePriority?: number;
  hotkey?: string;
  icon?: string | IconDefinition;
}

// populate tag list from tag config info
export const itemTagList: TagInfo[] = Object.values(tagConfig);

export const vaultGroupTagOrder = filterMap(itemTagList, (tag) => tag.type);

export const itemTagSelectorList: TagInfo[] = [
  { label: tl('Tags.TagItem') },
  ...Object.values(tagConfig),
];

/**
 * Delete items from the loaded items that don't appear in newly-loaded stores
 */
export function cleanInfos(stores: DimStore[]): ThunkResult {
  return async (dispatch, getState) => {
    if (!stores.length || stores.some((s) => s.items.length === 0 || s.hadErrors)) {
      // don't accidentally wipe out notes
      return;
    }

    const infos = itemInfosSelector(getState());

    if (isEmpty(infos)) {
      return;
    }

    const infosWithCraftedDate = Object.values(infos).filter((i) => i.craftedDate);
    const infosByCraftedDate = keyBy(infosWithCraftedDate, (i) => i.craftedDate!);

    let maxItemId = 0n;

    // Tags/notes are stored keyed by instance ID. Start with all the keys of the
    // existing tags and notes and remove the ones that are still here, and the rest
    // should be cleaned up because they refer to deleted items.
    const cleanupIds = new Set(Object.keys(infos));
    for (const store of stores) {
      for (const item of store.items) {
        const itemId = BigInt(item.id);
        if (itemId > maxItemId) {
          maxItemId = itemId;
        }
        const info = infos[item.id];
        if (info && (info.tag !== undefined || info.notes?.length)) {
          cleanupIds.delete(item.id);
        } else if (item.craftedInfo?.craftedDate) {
          // Double-check crafted items - we may have them under a different ID.
          // If so, patch up the data by re-tagging them under the new ID.
          // We'll delete the old item's info, but the new infos will be saved.
          const craftedInfo = infosByCraftedDate[item.craftedInfo.craftedDate];
          if (craftedInfo) {
            if (craftedInfo.tag) {
              // Convert API tag to our local tag type
              const localTag = fromApiTagValue(craftedInfo.tag);
              if (localTag) {
                dispatch(
                  setItemTag({
                    itemId: item.id,
                    tag: localTag,
                    craftedDate: item.craftedInfo.craftedDate,
                  }),
                );
              }
            }
            if (craftedInfo.notes) {
              dispatch(
                setItemNote({
                  itemId: item.id,
                  note: craftedInfo.notes,
                  craftedDate: item.craftedInfo.craftedDate,
                }),
              );
            }
          }
        }
      }
    }

    if (cleanupIds.size > 0) {
      const eligibleCleanupIds = Array.from(cleanupIds).filter((id) => {
        // Validate ID before converting to BigInt
        if (!id || typeof id !== 'string' || id.trim() === '') {
          return false;
        }
        try {
          return BigInt(id) < maxItemId;
        } catch {
          // Skip invalid IDs that can't be converted to BigInt
          return false;
        }
      });
      if (cleanupIds.size > eligibleCleanupIds.length) {
        warnLog(
          'cleanInfos',
          `${cleanupIds.size - eligibleCleanupIds.length} infos have IDs newer than the newest ID in inventory`,
        );
      }
      if (eligibleCleanupIds.length > 0) {
        infoLog('cleanInfos', `Purging tag/notes from ${eligibleCleanupIds.length} deleted items`);
        dispatch(tagCleanup(eligibleCleanupIds));
      }
    }
  };
}

export function getTag(
  item: DimItem,
  itemInfos: ItemInfos,
  itemHashTags?: {
    [itemHash: string]: ItemHashTag;
  },
): TagValue | undefined {
  if (!item.taggable) {
    return undefined;
  }

  const apiTag = item.instanced ? itemInfos[item.id]?.tag : itemHashTags?.[item.hash]?.tag;

  // Convert API tag to our local tag type
  if (apiTag === 'keep' || apiTag === 'junk') {
    return apiTag as TagValue;
  }
  // Map API tags to our supported tags
  if (apiTag === 'favorite') {
    return 'hotperk';
  }
  if (apiTag === 'archive') {
    return 'junk';
  }
  if (apiTag === 'infuse') {
    return 'junk';
  }

  return undefined;
}

export function getNotes(
  item: DimItem,
  itemInfos: ItemInfos,
  itemHashTags?: {
    [itemHash: string]: ItemHashTag;
  },
): string | undefined {
  return item.taggable
    ? (item.instanced ? itemInfos[item.id]?.notes : itemHashTags?.[item.hash]?.notes) || undefined
    : undefined;
}

