import { LocationSwitcher } from 'app/shell/LocationSwitcher';
import { HTML5Backend } from 'react-dnd-html5-backend';
import {
  DndProvider,
  MultiBackendOptions,
  PointerTransition,
  TouchTransition,
} from 'react-dnd-multi-backend';
import { TouchBackend } from 'react-dnd-touch-backend';
import { Provider } from 'react-redux';
import { BrowserRouter as Router } from 'react-router';
import App from './App';
import store from './store/store';
import { isNativeDragAndDropSupported } from './utils/browsers';
import { withProfiler } from './utils/sentry';

// Sentry profiling has been disabled - use stub implementation
const ProfiledApp = withProfiler(App);

function Root() {
  const options: MultiBackendOptions = {
    backends:
      // If we have native DnD then use it. iOS 15+ supports both touch and
      // native dnd, and without this it'd switch to touch.
      isNativeDragAndDropSupported()
        ? [{ id: 'html5', backend: HTML5Backend }]
        : [
            { id: 'html5', backend: HTML5Backend, transition: PointerTransition },
            // We can drop this after we only support iOS 15+ and Chrome 108+
            {
              id: 'touch',
              backend: TouchBackend,
              options: { enableMouseEvents: true, delayTouchStart: 150 },
              preview: true,
              transition: TouchTransition,
            },
          ],
  };
  return (
    <Router basename={$PUBLIC_PATH}>
      <Provider store={store}>
        <LocationSwitcher />
        <DndProvider options={options}>
          <ProfiledApp />
        </DndProvider>
      </Provider>
    </Router>
  );
}

export default Root;

import ItemGrid from 'app/armory/ItemGrid';
import { addCompareItem } from 'app/compare/actions';
import { stripAdept } from 'app/compare/compare-utils';
import { languageSelector } from 'app/d2l-api/selectors';
import BungieImage, { bungieNetPath } from 'app/d2l-ui/BungieImage';
import { DestinyTooltipText } from 'app/d2l-ui/DestinyTooltipText';
import ElementIcon from 'app/d2l-ui/ElementIcon';
import RichDestinyText from 'app/d2l-ui/destiny-symbols/RichDestinyText';
import { D2ManifestDefinitions } from 'app/destiny2/d2-definitions';
import { t } from 'app/i18next-t';
import ItemIcon, { DefItemIcon } from 'app/inventory/ItemIcon';
import { DimItem } from 'app/inventory/item-types';
import { allItemsSelector, createItemContextSelector } from 'app/inventory/selectors';
import { makeFakeItem } from 'app/inventory/store/d2-item-factory';
import {
  SocketOverrides,
  applySocketOverrides,
  useSocketOverrides,
} from 'app/inventory/store/override-sockets';
import { getEvent, getSeason } from 'app/inventory/store/season';
import { AmmoIcon } from 'app/item-popup/AmmoIcon';
import BreakerType from 'app/item-popup/BreakerType';
import EmblemPreview from 'app/item-popup/EmblemPreview';
import ItemSockets from 'app/item-popup/ItemSockets';
import ItemStats from 'app/item-popup/ItemStats';
import MetricCategories from 'app/item-popup/MetricCategories';
import { hideItemPopup } from 'app/item-popup/item-popup';
import { useD2Definitions } from 'app/manifest/selectors';
import Objective from 'app/progress/Objective';
import { Reward } from 'app/progress/Reward';
import { AppIcon, compareIcon, faMinusSquare, faPlusSquare, thumbsUpIcon } from 'app/shell/icons';
import { useIsPhonePortrait } from 'app/shell/selectors';
import { useThunkDispatch } from 'app/store/thunk-dispatch';
import { chainComparator, compareBy, reverseComparator } from 'app/utils/comparators';
