        cell: (value, item) =>
          value && (
            <BungieImage
              className={styles.inlineIcon}
              src={item.breakerType!.displayProperties.icon}
            />
          ),
        filter: (_val, item) =>
          item.breakerType
            ? `breaker:${breakerTypeNames[item.breakerType.hash as BreakerTypeHashes]}`
            : undefined,
      }),
    destinyVersion === 2 &&
      isWeapon &&
      !isSpreadsheet &&
      c({
        id: 'archetype',
        header: t('Organizer.Columns.Archetype'),
