import { isArtifice, isClassCompatible } from 'app/utils/item-utils';
import { getSocketsByType, getWeaponSockets } from 'app/utils/socket-utils';
import { DestinyClass } from 'bungie-api-ts/destiny2';
import { BucketHashes } from 'data/d2/generated-enums';
import { ItemFilterDefinition } from '../item-filter-types';
import { PerksSet } from './perks-set';
import { StatsSet } from './stats-set';

const notableTags = ['keep'];

/** outputs a string combination of the identifying features of an item, or the hash if classified */
export const makeDupeID = (item: DimItem) =>
  (item.classified && `${item.hash}`) ||
  `${
    // Consider adept versions of weapons to be the same as the normal type
    item.bucket.inWeapons ? stripAdept(item.name) : item.name
  }${
    // Some items have the same name across different classes, e.g. "Kairos Function Boots"
    item.classType
  }${
    // Some items have the same name across different tiers, e.g. "Traveler's Chosen"
    item.rarity
  }${
    // The engram that dispenses the Taraxippos scout rifle is also called Taraxippos
    item.bucket.hash
  }`;

export const sortDupes = (
  dupes: {
    [dupeID: string]: DimItem[];
  },
  getTag: (item: DimItem) => TagValue | undefined,
  sortType: 'power' | 'combo' | 'dupebest' = 'power',
) => {
  // The comparator for sorting dupes - the first item will be the "best" and all others are "dupelower".
  const dupeComparator =
    sortType === 'dupebest'
      ? chainComparator<DimItem>(
          // 1. Best Combo Rank (for weapons) - lower rank numbers are better
          compareBy((item) => {
            if (item.bucket.inWeapons && item.sockets) {
              const utils = getRollAppraiserUtilsSync();
              if (!utils) {
                return Number.MAX_SAFE_INTEGER;
              } // No data = worst

              const traitPerks = getSocketsByType(item, 'traits');
              if (traitPerks.length >= 2) {
                const perk4Hash = traitPerks[0]?.plugged?.plugDef.hash;
                const perk5Hash = traitPerks[1]?.plugged?.plugDef.hash;

                if (perk4Hash && perk5Hash) {
                  const comboRank = utils.getTraitComboRank(
                    item.hash.toString(),
                    perk4Hash,
                    perk5Hash,
                  );
                  if (comboRank) {
                    // Return the rank directly - lower numbers are better
                    return comboRank.rank;
                  }
                }
              }
            }
            return Number.MAX_SAFE_INTEGER; // Non-weapons or items without combo data go last
          }),
          // 2. Highest Power - higher is better, so negate to sort descending
          compareBy((item) => -item.power),
          // 3. Tag priority - true (has tag) is better, so negate
          compareBy((item) => {
            const tag = getTag(item);
            return !(tag && notableTags.includes(tag));
          }),
          // 4. Masterwork status - true is better, so negate
          compareBy((item) => !item.masterwork),
          // 5. Lock status - true is better, so negate
          compareBy((item) => !item.locked),
          // 6. Item ID tiebreaker
          compareBy((i) => i.id),
        )
      : reverseComparator(
          chainComparator<DimItem>(
            // primary comparison based on sort type
            sortType === 'combo'
              ? compareBy((item) => {
                  // For weapons, use combo rank (lower rank number = better, so higher rank numbers should be "dupelower")
                  if (item.bucket.inWeapons && item.sockets) {
                    const utils = getRollAppraiserUtilsSync();
                    if (!utils) {
                      return -item.power;
                    } // Fallback to power if no roll appraiser data

                    const traitPerks = getSocketsByType(item, 'traits');
                    if (traitPerks.length >= 2) {
                      const perk4Hash = traitPerks[0]?.plugged?.plugDef.hash;
                      const perk5Hash = traitPerks[1]?.plugged?.plugDef.hash;

                      if (perk4Hash && perk5Hash) {
                        const comboRank = utils.getTraitComboRank(
                          item.hash.toString(),
                          perk4Hash,
                          perk5Hash,
                        );
                        if (comboRank) {
                          // Return negative rank so lower rank numbers (better combos) sort first
                          // This means higher rank numbers (worse combos) will be considered "dupelower"
                          return -comboRank.rank;
                        }
                      }
                    }
                  }
                  // Fallback to power for non-weapons or items without combo data
                  return -item.power;
                })
              : compareBy((item) => item.power),
            compareBy((item) => {
              const tag = getTag(item);
              return Boolean(tag && notableTags.includes(tag));
            }),
            compareBy((item) => item.masterwork),
            compareBy((item) => item.locked),
            compareBy((i) => i.id), // tiebreak by ID
          ),
        );

  for (const dupeList of Object.values(dupes)) {
    if (dupeList.length > 1) {
      dupeList.sort(dupeComparator);
    }
  }

  return dupes;
};

/**
 * Sort duplicates for "dupebest" search - best item first.
 * For weapons: Use combo rank (lower rank number = better)
 * For armor: Use custom stat value (higher value = better) when available, otherwise use power
 */
export const sortDupesBest = (
  dupes: {
    [dupeID: string]: DimItem[];
  },
  getTag: (item: DimItem) => TagValue | undefined,
  customStats: CustomStatDef[],
) => {
  const dupeComparator = chainComparator<DimItem>(
    // 1. For armor: prioritize highest custom stat value if custom stats exist
    compareBy((item) => {
      if (item.bucket.inArmor && customStats.length > 0) {
        // Find the highest custom stat value for this item
        let highestCustomStatValue = 0;
        for (const customStat of customStats) {
          if (isClassCompatible(customStat.class, item.classType)) {
            const stat = item.stats?.find((s) => s.statHash === customStat.statHash);
            if (stat && stat.value > highestCustomStatValue) {
              highestCustomStatValue = stat.value;
            }
          }
        }
        // Return negative value so higher custom stat values are sorted first
        return -highestCustomStatValue;
      }
      return 0; // No priority for non-armor or when no custom stats exist
    }),
    // 2. Best Combo Rank (for weapons) - lower rank numbers are better
    compareBy((item) => {
      if (item.bucket.inWeapons && item.sockets) {
        const utils = getRollAppraiserUtilsSync();
        if (!utils) {
          return Number.MAX_SAFE_INTEGER;
        }

        const traitPerks = getSocketsByType(item, 'traits');
        if (traitPerks.length >= 2) {
          const perk4Hash = traitPerks[0]?.plugged?.plugDef.hash;
          const perk5Hash = traitPerks[1]?.plugged?.plugDef.hash;

          if (perk4Hash && perk5Hash) {
            const comboRank = utils.getTraitComboRank(item.hash.toString(), perk4Hash, perk5Hash);
            if (comboRank) {
              return comboRank.rank;
            }
          }
        }
      }
      return Number.MAX_SAFE_INTEGER;
    }),
    // 3. Weighted average of perk ranks in first two columns (tie-breaker for combo rank)
    compareBy((item) => {
      if (item.bucket.inWeapons && item.sockets) {
        const utils = getRollAppraiserUtilsSync();
        if (!utils) {
          return Number.MAX_SAFE_INTEGER;
        }

        // Get weapon sockets properly categorized
        const weaponSockets = getWeaponSockets(item, { excludeEmptySockets: false });
        if (!weaponSockets) {
          return 0;
        }

        // Get the first two perk sockets with multiple options (like Compare.tsx does)
        const allPerkSockets = item.sockets.allSockets
          .filter((s) => {
            // Must be a perk socket with multiple options
            if (!s.isPerk || s.plugOptions.length <= 1) {
              return false;
            }

            // Exclude intrinsic socket
            if (
              weaponSockets.intrinsicSocket &&
              s.socketIndex === weaponSockets.intrinsicSocket.socketIndex
            ) {
              return false;
            }

            return true;
          })
          .sort((a, b) => a.socketIndex - b.socketIndex);

        // Use the first two perk sockets (which should be columns 1 & 2)
        if (allPerkSockets.length >= 2) {
          // Find the best rank among all available perks in each column
          let bestRank1 = Number.MAX_SAFE_INTEGER;
          let bestRank2 = Number.MAX_SAFE_INTEGER;

          // Check all plug options in first column
          for (const plug of allPerkSockets[0].plugOptions) {
            const perkRank = utils.getPerkRank(item.hash.toString(), plug.plugDef.hash);
            if (perkRank && perkRank.rank < bestRank1) {
              bestRank1 = perkRank.rank;
            }
          }

          // Check all plug options in second column
          for (const plug of allPerkSockets[1].plugOptions) {
            const perkRank = utils.getPerkRank(item.hash.toString(), plug.plugDef.hash);
            if (perkRank && perkRank.rank < bestRank2) {
              bestRank2 = perkRank.rank;
            }
          }

          if (bestRank1 !== Number.MAX_SAFE_INTEGER && bestRank2 !== Number.MAX_SAFE_INTEGER) {
            // Weight calculation: 1=2.5, 2=2.0, 3=1.5, 4+=1.0
            const getWeight = (rank: number) => {
              switch (rank) {
                case 1:
                  return 2.5;
                case 2:
                  return 2.0;
                case 3:
                  return 1.5;
                default:
                  return 1.0;
              }
            };

            const weight1 = getWeight(bestRank1);
            const weight2 = getWeight(bestRank2);
            const weightedAverage = (weight1 + weight2) / 2;

            // Return negative so higher averages (better) sort first
            return -weightedAverage;
          }
        }
      }
      return 0; // No penalty for non-weapons or items without perk data
    }),
    // 4. Highest Power
    compareBy((item) => -item.power),
    // 5. Tag priority
    compareBy((item) => {
      const tag = getTag(item);
      return !(tag && notableTags.includes(tag));
    }),
    // 6. Masterwork status
    compareBy((item) => !item.masterwork),
    // 7. Lock status
    compareBy((item) => !item.locked),
    // 8. Item ID tiebreaker
    compareBy((i) => i.id),
  );

  for (const dupeList of Object.values(dupes)) {
    if (dupeList.length > 1) {
      dupeList.sort(dupeComparator);
    }
  }

  return dupes;
};

const computeDupesByIdFn = (allItems: DimItem[], makeDupeIdFn: (item: DimItem) => string) => {
  // Holds a map from item hash to count of occurrences of that hash
  const duplicates: { [dupeID: string]: DimItem[] } = {};

  for (const i of allItems) {
    if (!i.comparable) {
      continue;
    }
    const dupeID = makeDupeIdFn(i);
    if (!duplicates[dupeID]) {
      duplicates[dupeID] = [];
    }
    duplicates[dupeID].push(i);
  }

  return duplicates;
};

/**
 * Find a map of duplicate items using the makeDupeID function.
 */
export const computeDupes = (allItems: DimItem[]) => computeDupesByIdFn(allItems, makeDupeID);

/**
 * Create a dupe ID for armor based on class + type + tier, ignoring specific item name/hash.
 * For exotic armor, also include perks since the perk combination determines uniqueness.
 * This groups all armor pieces of the same class, type, and tier together.
 */
export const makeArmorClassTypeTierDupeID = (item: DimItem) => {
  if (!item.bucket.inArmor) {
    return makeDupeID(item); // Fallback to normal behavior for non-armor
  }

  let dupeId = `${item.classType}-${item.bucket.hash}-${item.rarity}`;

  // For exotic armor, include perks in the dupe ID since perk combinations determine uniqueness
  if (item.isExotic && item.sockets?.allSockets) {
    const perkHashes: number[] = [];

    // Get all perk sockets (intrinsic and regular perks)
    for (const socket of item.sockets.allSockets) {
      if (socket.isPerk && socket.plugged?.plugDef.hash) {
        // Include perk hash to differentiate exotic armor by their perk combinations
        perkHashes.push(socket.plugged.plugDef.hash);
      }
    }

    // Sort perk hashes for consistent ordering
    perkHashes.sort((a, b) => a - b);

    if (perkHashes.length > 0) {
      dupeId += `-perks:${perkHashes.join(',')}`;
    }
  }

  return dupeId;
};

/**
 * Compute armor duplicates grouped by class/type/tier instead of itemHash.
 * This allows finding the best armor piece per class/type/tier combination.
 */
export const computeArmorDupesByClassTypeTier = (allItems: DimItem[]) => {
  const armorItems = allItems.filter((item) => item.bucket.inArmor);
  return computeDupesByIdFn(armorItems, makeArmorClassTypeTierDupeID);
};

const dupeFilters: ItemFilterDefinition[] = [
  {
    keywords: 'dupe',
    description: tl('Filter.Dupe'),
    filter: ({ allItems }) => {
      const duplicates = computeDupes(allItems);
      return (item) => {
        const dupeId = makeDupeID(item);
        return checkIfIsDupe(duplicates, dupeId, item);
      };
    },
  },
  {
    keywords: 'dupelower',
    description: tl('Filter.DupeLower'),
    format: ['simple', 'query'],
    suggestions: ['power', 'combo'],
    filter: ({ filterValue, allItems, getTag }) => {
      // Default to 'power' if no specific value is provided
      const sortType = filterValue === 'combo' ? 'combo' : 'power';
      const duplicates = sortDupes(computeDupes(allItems), getTag, sortType);

      return (item) => {
        if (
          !(
            item.bucket &&
            (item.bucket.sort === 'Weapons' || item.bucket.sort === 'Armor') &&
            !item.notransfer
          )
        ) {
          return false;
        }

        const dupeId = makeDupeID(item);
        const dupes = duplicates[dupeId];
        if (dupes?.length > 1) {
          const bestDupe = dupes[0];
          return item !== bestDupe;
        }

        return false;
      };
    },
  },
  {
    keywords: 'infusionfodder',
    description: tl('Filter.InfusionFodder'),
    destinyVersion: 2,
    filter: ({ allItems }) => {
      const duplicates = computeDupesByIdFn(
        allItems.filter((i) => i.infusionFuel),
        (i) => i.hash.toString(),
      );
      return (item) => {
        if (!item.infusionFuel) {
          return false;
        }

        return duplicates[item.hash.toString()]?.some((i) => i.power < item.power);
      };
    },
  },
  {
    keywords: 'count',
    description: tl('Filter.DupeCount'),
    format: 'range',
    filter: ({ compare, allItems }) => {
      const duplicates = computeDupes(allItems);
      return (item) => {
        const dupeId = makeDupeID(item);
        return compare!(duplicates[dupeId]?.length ?? 0);
      };
    },
  },
  {
    keywords: 'statlower',
    description: tl('Filter.StatLower'),
    filter: ({ allItems }) => {
      const duplicates = computeStatDupeLower(allItems);
      return (item) => item.bucket.inArmor && duplicates.has(item.id);
    },
  },
  {
    keywords: 'customstatlower',
    description: tl('Filter.CustomStatLower'),
    filter: ({ allItems, customStats }) => {
      const duplicateSetsByClass: Partial<Record<DimItem['classType'], Set<string>[]>> = {};

      for (const customStat of customStats) {
        const relevantStatHashes: number[] = [];
        const statWeights = customStat.weights;
        for (const statHash in statWeights) {
          const weight = statWeights[statHash];
          if (weight && weight > 0) {
            relevantStatHashes.push(parseInt(statHash, 10));
          }
        }
        (duplicateSetsByClass[customStat.class] ||= []).push(
          computeStatDupeLower(allItems, relevantStatHashes),
        );
      }

      return (item) =>
        item.bucket.inArmor &&
        // highlight the item if it's statlower for all class-relevant custom stats.
        // this duplicates existing behavior for old style default-named custom stat,
        // but should be extended to also be a stat name-based filter
        // for users with multiple stats per class, a la customstatlower:pve
        duplicateSetsByClass[item.classType]?.every((dupeSet) => dupeSet.has(item.id));
    },
  },
  {
    keywords: ['crafteddupe', 'shapeddupe'],
    description: tl('Filter.CraftedDupe'),
    filter: ({ allItems }) => {
      const duplicates = computeDupes(allItems);
      return (item) => {
        const dupeId = makeDupeID(item);
        if (!checkIfIsDupe(duplicates, dupeId, item)) {
          return false;
        }
        const itemDupes = duplicates?.[dupeId];
        return itemDupes?.some((d) => d.crafted);
      };
    },
  },
  {
    keywords: ['dupeperks'],
    description: tl('Filter.DupePerks'),
    filter: ({ allItems }) => {
      const duplicates = new Map<string, PerksSet>();
      function getDupeId(item: DimItem) {
        // Don't compare across buckets or across types (e.g. Titan armor vs Hunter armor)
        return `${item.bucket.hash}|${item.classType}`;
      }
      for (const i of allItems) {
        if (i.sockets?.allSockets.some((s) => s.isPerk && s.socketDefinition.defaultVisible)) {
          const dupeId = getDupeId(i);
          if (!duplicates.has(dupeId)) {
            duplicates.set(dupeId, new PerksSet());
          }
          duplicates.get(dupeId)!.insert(i);
        }
      }
      return (item) =>
        item.sockets?.allSockets.some((s) => s.isPerk && s.socketDefinition.defaultVisible) &&
        Boolean(duplicates.get(getDupeId(item))?.hasPerkDupes(item));
    },
  },
  {
    keywords: 'dupebest',
    description: tl('Filter.DupeBest'),
    filter: ({ allItems, getTag, customStats }) => {
      // For armor, group by class/type/tier instead of itemHash
      const armorDupes = computeArmorDupesByClassTypeTier(allItems);
      const armorDupesSorted = sortDupesBest(armorDupes, getTag, customStats);

      // For non-armor, use the original itemHash-based grouping
      const nonArmorItems = allItems.filter((item) => !item.bucket.inArmor);
      const nonArmorDupes = computeDupes(nonArmorItems);
      const nonArmorDupesSorted = sortDupesBest(nonArmorDupes, getTag, customStats);

      return (item) => {
        if (item.bucket.inArmor) {
          // Use class/type/tier grouping for armor
          const dupeId = makeArmorClassTypeTierDupeID(item);
          const dupes = armorDupesSorted[dupeId];
          if (dupes?.length > 1) {
            const bestDupe = dupes[0];
            return item === bestDupe;
          }
        } else {
          // Use itemHash grouping for non-armor
          const dupeId = makeDupeID(item);
          const dupes = nonArmorDupesSorted[dupeId];
          if (dupes?.length > 1) {
            const bestDupe = dupes[0];
            return item === bestDupe;
          }
        }
        return false;
      };
    },
  },
  {
    keywords: 'bestarmor',
    description: tl('Filter.BestArmor'),
    filter: ({ allItems, getTag, customStats }) => {
      // Only process armor items
      const armorItems = allItems.filter((item) => item.bucket.inArmor);
      const armorDupes = computeArmorDupesByClassTypeTier(armorItems);
      const armorDupesSorted = sortDupesBest(armorDupes, getTag, customStats);

      return (item) => {
        // Only match armor items
        if (!item.bucket.inArmor) {
          return false;
        }

        const dupeId = makeArmorClassTypeTierDupeID(item);
        const dupes = armorDupesSorted[dupeId];

        if (dupes && dupes.length >= 1) {
          // Return true if this item is the best in its group (first after sorting)
          // This includes single items (length 1) and best of duplicates (length > 1)
          const bestDupe = dupes[0];
          return item === bestDupe;
        }

        return false;
      };
    },
  },
];

export default dupeFilters;

export function checkIfIsDupe(
  duplicates: {
    [dupeID: string]: DimItem[];
  },
  dupeId: string,
  item: DimItem,
) {
  return (
    duplicates[dupeId]?.length > 1 &&
    item.hash !== DEFAULT_SHADER &&
    item.bucket.hash !== BucketHashes.SeasonalArtifact
  );
}

/**
 * Compute a set of items that are "stat lower" dupes. These are items for which
 * there exists another item with strictly better stats (i.e. better in at least
 * one stat and not worse in any stat).
 */
function computeStatDupeLower(allItems: DimItem[], relevantStatHashes: number[] = armorStats) {
  // disregard no-class armor
  const armor = allItems.filter((i) => i.bucket.inArmor && i.classType !== DestinyClass.Classified);

  // Group by class and armor type. Also, compare exotics with each other, not the general pool.
  const grouped = Object.values(
    Object.groupBy(armor, (i) => `${i.bucket.hash}-${i.classType}-${i.isExotic ? i.hash : ''}`),
  );

  const dupes = new Set<string>();

  // A mapping from an item to a list of all of its stat configurations
  // (Artifice armor can have multiple). This is just a cache to prevent
  // recalculating it.
  const statsCache = new Map<DimItem, number[][]>();
  for (const item of armor) {
    if (item.stats && item.power) {
      const statValues = item.stats
        .filter((s) => relevantStatHashes.includes(s.statHash))
        .sort((a, b) => a.statHash - b.statHash)
        .map((s) => s.base);
      if (isArtifice(item)) {
        statsCache.set(
          item,
          // Artifice armor can be +3 in any one stat, so we compute a separate
          // version of the stats for each stat considered
          relevantStatHashes.map((_s, i) => {
            const modifiedStats = [...statValues];
            // One stat gets +3
            modifiedStats[i] += 3;
            return modifiedStats;
          }),
        );
      } else {
        statsCache.set(item, [statValues]);
      }
    }
  }

  // For each group of items that should be compared against each other
  for (const group of grouped) {
    if (!group) {
      // Add check for undefined group
      continue;
    }
    const statSet = new StatsSet<DimItem>();
    // Add a mapping from stats => item to the statsSet for each item in the group
    for (const item of group) {
      const stats = statsCache.get(item);
      if (stats) {
        for (const statValues of stats) {
          statSet.insert(statValues, item);
        }
      }
    }

    // Now run through the items in the group again, checking against the fully
    // populated stats set to see if there's something better
    for (const item of group) {
      const stats = statsCache.get(item);
      // All configurations must have a better version somewhere for this to count as statlower
      if (stats?.every((statValues) => statSet.doBetterStatsExist(statValues))) {
        dupes.add(item.id);
      }
    }
  }

  return dupes;
}

import { tl } from 'app/i18next-t';
import { DimItem } from 'app/inventory/item-types';
import { testStringsFromAllSockets } from 'app/search/items/search-filters/freeform';
import { matchText } from 'app/search/text-utils';
import { ItemFilterDefinition } from '../item-filter-types';
import {
  displayStatToHashMap,
  getPerkStatHashes,
  isValidPerkName,
  perkStatMappings,
} from './perk-stat-mappings';

/**
 * Filter that finds the best armor piece for a given perk based on optimized primary/secondary stats
 */
const perkOptimizationFilter: ItemFilterDefinition = {
  keywords: 'bestforperk',
  description: tl('Filter.BestForPerk'),
  format: 'query',
  suggestions: Object.keys(perkStatMappings),
  destinyVersion: 2,
  filter: ({ filterValue, allItems, language, d2Definitions }) => {
    console.log('🔥 bestforperk filter called with:', filterValue);
    console.log('🔥 allItems count:', allItems.length);
    console.log('🔥 d2Definitions:', Boolean(d2Definitions));

    // Get the stat hashes for this perk, or use defaults if not in our mapping
    let primaryStatHash: number;
    let secondaryStatHash: number;

    if (isValidPerkName(filterValue)) {
      const statHashes = getPerkStatHashes(filterValue);
      primaryStatHash = statHashes.primary;
      secondaryStatHash = statHashes.secondary;
    } else {
      // For perks not in our mapping, we need to determine stats based on the perk name
      // You can expand this logic based on actual perk names you discover
      if (filterValue.toLowerCase().includes('brawler')) {
        // Brawler perks should optimize for Melee (primary) and Health (secondary)
        primaryStatHash = displayStatToHashMap.Melee;
        secondaryStatHash = displayStatToHashMap.Health;
      } else {
        // Default fallback - optimize for Health and Melee
        primaryStatHash = displayStatToHashMap.Health;
        secondaryStatHash = displayStatToHashMap.Melee;
      }
    }

    // Find all items that have the specified perk
    const perkTest = matchText(filterValue, language, /* exact */ false);
    const itemsWithPerk = allItems.filter(
      (item) =>
        item.bucket.inArmor &&
        item.stats &&
        testStringsFromAllSockets(perkTest, item, d2Definitions, /* includeDescription */ false),
    );

    // Debug logging
    if ($D2L_FLAVOR === 'dev') {
      console.log(`Looking for perk: "${filterValue}"`);
      console.log(`Found ${itemsWithPerk.length} items with this perk`);
      if (itemsWithPerk.length > 0) {
        console.log(
          'Sample items:',
          itemsWithPerk.slice(0, 3).map((i) => i.name),
        );
      }
    }

    if (itemsWithPerk.length === 0) {
      return () => false;
    }

    // Group items by slot and class
    const itemsBySlotClass: Record<string, DimItem[]> = {};
    for (const item of itemsWithPerk) {
      const key = `${item.bucket.hash}-${item.classType}`;
      if (!itemsBySlotClass[key]) {
        itemsBySlotClass[key] = [];
      }
      itemsBySlotClass[key].push(item);
    }

    // For each slot+class group, find the best item(s) based on primary stat, then secondary stat
    const bestItemIds = new Set<string>();

    for (const [slotClass, items] of Object.entries(itemsBySlotClass)) {
      if (items.length === 0) {
        continue;
      }

      // Sort by primary stat (descending), then by secondary stat (descending)
      const sortedItems = items.sort((a, b) => {
        const aPrimaryStat = a.stats?.find((s) => s.statHash === primaryStatHash)?.base || 0;
        const bPrimaryStat = b.stats?.find((s) => s.statHash === primaryStatHash)?.base || 0;

        if (aPrimaryStat !== bPrimaryStat) {
          return bPrimaryStat - aPrimaryStat; // Higher primary stat wins
        }

        // If primary stats are tied, compare secondary stats
        const aSecondaryStat = a.stats?.find((s) => s.statHash === secondaryStatHash)?.base || 0;
        const bSecondaryStat = b.stats?.find((s) => s.statHash === secondaryStatHash)?.base || 0;

        return bSecondaryStat - aSecondaryStat; // Higher secondary stat wins
      });

      // Add all items that tie for the best stats
      const bestItem = sortedItems[0];
      const bestPrimaryStat =
        bestItem.stats?.find((s) => s.statHash === primaryStatHash)?.base || 0;
      const bestSecondaryStat =
        bestItem.stats?.find((s) => s.statHash === secondaryStatHash)?.base || 0;

      for (const item of sortedItems) {
        const itemPrimaryStat = item.stats?.find((s) => s.statHash === primaryStatHash)?.base || 0;
        const itemSecondaryStat =
          item.stats?.find((s) => s.statHash === secondaryStatHash)?.base || 0;

        if (itemPrimaryStat === bestPrimaryStat && itemSecondaryStat === bestSecondaryStat) {
          bestItemIds.add(item.id);
        } else {
          break; // No more ties
        }
      }
    }

    return (item: DimItem) => bestItemIds.has(item.id);
  },
};

