import styles from './EnergyIncrements.m.scss';
import { PressTip } from './PressTip';

// TODO special display for T10 -> T10 + exotic artifice?

/** this accepts either an item, or a partial DimItem.energy */
function EnergyIncrements({
  item,
  energy,
}:
  | { item: DimItem; energy?: undefined }
  | {
      item?: undefined;
      energy: {
        energyCapacity: number;
        energyUsed: number;
      };
    }) {
  const { energyCapacity, energyUsed } = item?.energy ?? energy!;
  return (
    <EnergyMeterIncrements
      energyCapacity={energyCapacity}
      energyUsed={energyUsed}
      variant="small"
    />
  );
}

export function EnergyMeterIncrements({
  energyCapacity,
  energyUsed,
  minCapacity,
  previewUpgrade,
  variant,
}: {
  energyCapacity: number;
  energyUsed: number;
  minCapacity?: number;
  previewUpgrade?: (i: number) => void;
  variant: 'medium' | 'small';
}) {
  // This works because Tier 5 armor with 11 energy drops with all energy unlocked.
  const maxEnergyCapacity = Math.max(10, energyCapacity);
  // layer in possible total slots, then earned slots, then currently used slots
  const meterIncrements = Array<string | undefined>(maxEnergyCapacity)
    .fill(styles.unavailable)
    .fill(undefined, 0, energyCapacity)
    .fill(styles.used, 0, energyUsed);
  return (
    <div className={clsx(styles.energyMeterIncrements, { [styles.medium]: variant === 'medium' })}>
      {meterIncrements.map((incrementStyle, i) => (
        <div
          key={i}
          className={incrementStyle}
          role={minCapacity !== undefined && i + 1 > minCapacity ? 'button' : undefined}
          onClick={previewUpgrade ? () => previewUpgrade(i + 1) : undefined}
        />
      ))}
    </div>
  );
}

export function EnergyIncrementsWithPresstip({
  energy,
  wrapperClass,
  item,
}: {
  energy: {
    energyCapacity: number;
    energyUsed: number;
  };
  wrapperClass?: string | undefined;
  item: DimItem;
}) {
  const { energyCapacity, energyUsed } = energy;
  const energyUnused = Math.max(energyCapacity - energyUsed, 0);

  const defs = useD2Definitions()!;
  if (!item.energy) {
    return null;
  }

  const energyModHashes = getEnergyUpgradeHashes(item, energyUsed || 0);
  const costs = sumModCosts(
    defs,
    energyModHashes.map((h) => defs.InventoryItem.get(h)),
  ).sort(compareBy((c) => c.quantity));

  return (
    <PressTip
      tooltip={
        <>
          {t('EnergyMeter.Energy')}
          <hr />
          {t('EnergyMeter.Used')}: {energyUsed}
          <br />
          {t('EnergyMeter.Unused')}: {energyUnused}
          {energyUsed > energyCapacity && (
            <>
              <hr />
              {t('EnergyMeter.UpgradeNeeded', energy)}
            </>
          )}
          {costs.length > 0 && (
            <>
              <hr />
              <div className={styles.costs}>
                <span>{t('Loadouts.ModPlacement.UpgradeCosts')}</span>
                {costs.map((cost) => (
                  <Cost key={cost.itemHash} cost={cost} className={styles.cost} />
                ))}
              </div>
            </>
          )}
        </>
      }
      className={wrapperClass}
    >
      <EnergyIncrements
        energy={{
          energyCapacity,
          energyUsed,
        }}
      />
      {energyUsed > energyCapacity && <EnergySwap energy={energy} />}
    </PressTip>
  );
}

import clsx from 'clsx';
import React, { useEffect, useLayoutEffect, useRef, useState } from 'react';
import styles from './ExpandableTextBlock.m.scss';

/**
 * wrapped around some inline content, this crops to a specified number of lines
 * (with a fadeout) and allows the user to click it and show the rest
 *
 * @param linesWhenClosed an integer please. controls how many lines to collapse to
 * @param alreadyOpen allows a parent component to force it open
 */
export function ExpandableTextBlock({
  children,
  linesWhenClosed,
  alreadyOpen,
  className,
}: {
  children: React.ReactNode;
  linesWhenClosed: number;
  alreadyOpen?: boolean;
  className?: string;
}) {
  const [isOpen, setOpen] = useState(alreadyOpen);
  const [closedHeight, setClosedHeight] = useState<number>();
  const contentRef = useRef<HTMLDivElement>(null);
  const wrapperRef = useRef<HTMLDivElement>(null);

  // measure the element height, with lines clamped, the first time this component exists
  useLayoutEffect(() => {
    setClosedHeight(contentRef.current!.clientHeight);
  }, []);
  // after the element has been measured, if the unclamped text still fits inside clamped height,
  // then clamping wasn't necessary. set isOpen to mark it as, effectively, already opened
  useEffect(() => {
    if (closedHeight && wrapperRef.current!.clientHeight >= contentRef.current!.clientHeight) {
      setOpen(true);
    }
  }, [closedHeight]);

  return (
    <div
      className={clsx(className, styles.textBlockWrapper, { [styles.open]: isOpen })}
      ref={wrapperRef}
      onClick={() => setOpen(true)}
      style={{ height: isOpen ? 'max-content' : closedHeight, overflow: 'hidden' }}
    >
      <div
        ref={contentRef}
        style={
          closedHeight
            ? undefined
            : {
                WebkitLineClamp: linesWhenClosed,
                WebkitBoxOrient: 'vertical',
                display: '-webkit-box',
              }
        }
      >
        {children}
      </div>
    </div>
  );
}

import { expandDownIcon, expandUpIcon } from 'app/shell/icons';
import AppIcon from 'app/shell/icons/AppIcon';
import clsx from 'clsx';
import { useSelect } from 'downshift';

import { useHeightFromViewportBottom } from 'app/utils/hooks';
import { CSSProperties, ReactNode, useEffect, useRef, useState } from 'react';
import styles from './Select.m.scss';
import { usePopper } from './usePopper';

export interface Option<T> {
  key: string;
  content: ReactNode;
  disabled?: boolean;
  value?: T;
}

interface Props<T> {
  className?: string;
  /** Hide the selected option from the dropdown */
  hideSelected?: boolean;
  disabled?: boolean;
  /** Sets the max width for the button. */
  maxButtonWidth?: number;
  /**
   * Sets the max width for the dropdown.
   *
   * If 'button' is used the two things can happen:
   * 1. If maxButtonWidth is set it will use that as the max width.
   * 2. If maxButtonWidth is undefined it will calculate the width
   * of the button dynamically and use that to set the max width.
   */
  maxDropdownWidth?: number | 'button';
  value?: T;
  options: Option<T>[];
  /** Optional override for the button content */
  children?: ReactNode;
  onChange: (value?: T) => void;
}

/**
 * A Select menu, which maintains a current value and a dropdown to choose
 * another value. A replacement for HTML's <select> element. This is a
 * controlled component.
 *
 * @see Dropdown for a menu of commands
 * @see MultiSelect for multiple-item selector
 */
export default function Select<T>({
  className,
  disabled,
  maxButtonWidth,
  maxDropdownWidth,
  options: items,
  onChange,
  value,
  hideSelected,
  children,
}: Props<T>) {
  const {
    isOpen,
    getToggleButtonProps,
    getMenuProps,
    highlightedIndex,
    getItemProps,
    selectedItem,
  } = useSelect({
    items,
    selectedItem: items.find((o) => o.value === value) || null,
    itemToString: (i) => i?.key || 'none',
    onSelectedItemChange: ({ selectedItem }) => onChange(selectedItem?.value),
    isItemDisabled: (item) => Boolean(item.disabled),
  });

  const buttonRef = useRef<HTMLButtonElement>(null);
  const menuRef = useRef<HTMLDivElement>(null);
  const [dropdownWidth, setDropdownWidth] = useState<number | undefined>(() =>
    typeof maxDropdownWidth === 'number' ? maxDropdownWidth : undefined,
  );
  const [dropdownHeight, setDropdownHeight] = useState<number | undefined>();

  usePopper(
    {
      contents: menuRef,
      reference: buttonRef,
      placement: 'bottom-start',
      offset: 2,
    },
    [isOpen, items],
  );

  // Only throw error if value is defined but doesn't match any option
  if (value !== undefined && !selectedItem) {
    throw new Error('value must correspond to one of the provided options');
  }

  useEffect(() => {
    if (maxDropdownWidth === 'button' && dropdownWidth === undefined && buttonRef.current) {
      // Minus 2 because the menu has a thicker outline than the button border (2px vs 1px)
      const width =
        maxButtonWidth !== undefined
          ? maxButtonWidth
          : buttonRef.current.getBoundingClientRect().width - 2;
      setDropdownWidth(width);
    }
  }, [dropdownWidth, maxButtonWidth, maxDropdownWidth]);

  useHeightFromViewportBottom(buttonRef, setDropdownHeight, 28, true);

  let buttonStyle: CSSProperties | undefined;

  const dropdownStyle: CSSProperties = {
    overflowY: 'auto',
    overscrollBehaviorY: 'contain',
    maxHeight: dropdownHeight,
  };

  if (maxButtonWidth !== undefined) {
    buttonStyle = {
      maxWidth: maxButtonWidth,
    };
  }

  return (
    <div className={className}>
      <button
        type="button"
        style={buttonStyle}
        className={styles.button}
        {...getToggleButtonProps({
          ref: buttonRef,
          disabled,
        })}
      >
        {children ?? (
          <>
            {selectedItem?.content}{' '}
            <AppIcon icon={isOpen ? expandUpIcon : expandDownIcon} className={styles.arrow} />
          </>
        )}
      </button>
      <div
        {...getMenuProps({ ref: menuRef, className: clsx(styles.menu, { [styles.open]: isOpen }) })}
      >
        <div style={dropdownStyle}>
          {isOpen &&
            items.map(
              (item, index) =>
                !(hideSelected && item.value === value) && (
                  <div
                    className={clsx(styles.menuItem, {
                      [styles.highlighted]: highlightedIndex === index,
                      [styles.disabled]: item.disabled,
                    })}
                    key={item.key}
                    {...getItemProps({
                      item,
                      index,
                    })}
                  >
                    {item.content}
                  </div>
                ),
            )}
        </div>
      </div>
    </div>
  );
}

