// used in displaying the modded segments on item stats
const modItemCategoryHashes = new Set([
  ItemCategoryHashes.WeaponModsDamage,
  ItemCategoryHashes.ArmorModsGameplay, // armor mods (pre-2.0)
  ItemCategoryHashes.ArmorMods, // armor 2.0 mods
]);

// used in displaying the component segments on item stats
const weaponParts = new Set([
  ItemCategoryHashes.WeaponModsBowstring,
  ItemCategoryHashes.WeaponModsBatteries,
  ItemCategoryHashes.WeaponModsSwordBlades,
  ItemCategoryHashes.WeaponModsLaunchTubes,
  ItemCategoryHashes.WeaponModsScopes,
  ItemCategoryHashes.WeaponModsHafts,
  ItemCategoryHashes.WeaponModsStocks,
  ItemCategoryHashes.WeaponModsSwordGuards,
  ItemCategoryHashes.WeaponModsBarrels,
  ItemCategoryHashes.WeaponModsArrows,
  ItemCategoryHashes.WeaponModsGrips,
  ItemCategoryHashes.WeaponModsSights,
  ItemCategoryHashes.WeaponModsMagazines,
]);

// Some stat labels are long. This lets us replace them with i18n
const statLabels: LookupTable<StatHashes, I18nKey> = {
  [StatHashes.RoundsPerMinute]: tl('Organizer.Stats.RPM'),
  [StatHashes.AirborneEffectiveness]: tl('Organizer.Stats.Airborne'),
  [StatHashes.AmmoGeneration]: tl('Organizer.Stats.AmmoGeneration'),
};

type StatSegmentType = 'base' | 'parts' | 'mod' | 'masterwork';
const statStyles: Record<StatSegmentType, [style: string, label: I18nKey]> = {
  base: [styles.base, tl('Organizer.Columns.BaseStats')],
  parts: [styles.parts, tl('Stats.WeaponPart')],
  mod: [styles.mod, tl('Loadouts.Mods')],
  masterwork: [styles.masterwork, tl('Organizer.Columns.MasterworkStat')],
};
type StatSegments = [value: number, statSegmentType: StatSegmentType, modName?: string][];

/**
 * A single stat line.
 */
export default function ItemStat({ stat, item }: { stat: DimStat; item?: DimItem }) {
  const showQuality = useSelector(settingSelector('itemQuality'));
  const customStatsList = useSelector(customStatsSelector);
  const customStatHashes = customStatsList.map((c) => c.statHash);

  const modEffects =
    item &&
    getModEffects(item, stat.statHash).sort(reverseComparator(compareBy(([value]) => value)));
  const modEffectsTotal = modEffects ? sumBy(modEffects, ([value]) => value) : 0;

  const partEffects =
    item &&
    getPartEffects(item, stat.statHash).sort(reverseComparator(compareBy(([value]) => value)));
  const partEffectsTotal = partEffects ? sumBy(partEffects, ([value]) => value) : 0;

  const armorMasterworkSockets = item!.sockets?.allSockets.filter((s) =>
    s.plugged?.plugDef.plug.plugCategoryIdentifier.startsWith('v460.plugs.armor.masterworks'),
  );
  const armorMasterworkValue =
    armorMasterworkSockets && getTotalPlugEffects(armorMasterworkSockets, [stat.statHash]);

  const masterworkValue =
    item?.masterworkInfo?.stats?.find((s) => s.hash === stat.statHash)?.value ?? 0;
  // This bool controls the stat name being gold
  const isMasterworkedStat = masterworkValue !== 0;
  const masterworkDisplayValue = masterworkValue || armorMasterworkValue;
  let masterworkDisplayWidth = masterworkDisplayValue || 0;
  // baseBar here is the leftmost segment of the stat bar.
  // For armor, this is the "roll," the sum of its invisible stat plugs.
  // For weapons, this is the default base stat in its item definition, before barrels/mags/etc.
  const baseBar = item?.bucket.inArmor
    ? // if it's armor, the base bar length should be
      // the shortest of base or resulting value, but not below 0
      Math.max(Math.min(stat.base, stat.value), 0)
    : // otherwise, for weapons, we just subtract masterwork and
      // consider the "base" to include selected perks but not mods
      stat.value - masterworkValue - modEffectsTotal - partEffectsTotal;

  const segments: StatSegments = [[baseBar, 'base']];

  for (const [effectAmount, modName] of partEffects ?? []) {
    segments.push([effectAmount, 'parts', modName]);
  }

  for (const [effectAmount, modName] of modEffects ?? []) {
    segments.push([effectAmount, 'mod', modName]);
  }

  if (masterworkDisplayWidth) {
    // Account for a masterwork being completely counteracted by a mod penalty.
    // A MW segment cannot be longer than the bar's total.
    // ie: a +6 base, a +2mw, and a -10 mod, results in 0. MW segment width is 0.
    if (modEffectsTotal < 0) {
      masterworkDisplayWidth = clamp(masterworkDisplayWidth, 0, stat.value);
    }
    segments.push([masterworkDisplayWidth, 'masterwork']);
  }

  // Get the values that contribute to the total stat value
  const totalDetails =
    item &&
    stat.statHash === TOTAL_STAT_HASH &&
    breakDownTotalValue(stat.base, item, armorMasterworkSockets || []);

  const modSign =
    (stat.value !== stat.base ? modEffectsTotal : 0) * (stat.smallerIsBetter ? -1 : 1);

  const optionalClasses = {
    [styles.masterworked]: isMasterworkedStat,
    [styles.mod]: modSign > 0,
    [styles.negativeModded]: modSign < 0,
    [styles.totalRow]: Boolean(totalDetails),
    [styles.customTotal]: customStatHashes.includes(stat.statHash),
  };

  return (
    <>
      <div
        className={clsx(styles.statName, optionalClasses)}
        aria-label={stat.displayProperties.name}
        title={stat.displayProperties.description}
      >
        {stat.statHash in statLabels
          ? t(statLabels[stat.statHash as StatHashes]!)
          : stat.displayProperties.name}
      </div>

      <div className={clsx(styles.value, optionalClasses)}>
        {stat.additive && stat.value >= 0 && '+'}
        <AnimatedNumber value={stat.value} />
      </div>

      {item?.destinyVersion === 2 && statsMs.includes(stat.statHash) && (
        <div className={clsx(optionalClasses)}>{t('Stats.Milliseconds')}</div>
      )}

      {stat.displayProperties.hasIcon && (
        <div className={styles.icon}>
          <BungieImage className="stat-icon" src={stat.displayProperties.icon} alt="" />
        </div>
      )}

      {showQuality &&
        item &&
        isD1Stat(item, stat) &&
        stat.qualityPercentage &&
        stat.qualityPercentage.min !== 0 && (
          <div
            className={styles.quality}
            style={getD1QualityColor(stat.qualityPercentage.min, 'color')}
          >
            ({stat.qualityPercentage.range})
          </div>
        )}

      {stat.statHash === StatHashes.RecoilDirection && (
        <div className={styles.statBar}>
          <RecoilStat value={stat.value} />
        </div>
      )}

      {stat.bar && <StatBar stat={stat} segments={segments} />}

      {totalDetails &&
        Boolean(totalDetails.totalModsValue || totalDetails.totalMasterworkValue) && (
          <StatTotal totalDetails={totalDetails} optionalClasses={optionalClasses} stat={stat} />
        )}

      {item && customStatHashes.includes(stat.statHash) && (
        <CustomStatWeightsFromHash
          className={clsx(styles.smallStatToggle, styles.nonDimmedStatIcons)}
          customStatHash={stat.statHash}
        />
      )}
    </>
  );
}

function StatBar({ segments, stat }: { segments: StatSegments; stat: DimStat }) {
  // Make sure the combined "filled"-colored segments never exceed this.
  let remainingFilled = stat.value;
  // Make sure the red bar section never exceeds the blank space,
  // which would increase the total stat bar width.
  let remainingEmpty = Math.max(stat.maximumValue - stat.value, 0);
  return (
    <div className={styles.statBar} aria-label={stat.displayProperties.name} aria-hidden="true">
      <PressTip
        placement="top-start"
        className={styles.barContainer}
        tooltip={<StatBarTooltip segments={segments} stat={stat} />}
      >
        {segments
          // Process base stats last, letting them be the most likely to hit cap and lose display length
          .toSorted(([, statType]) => (statType === 'base' ? 1 : 0))
          .map(([val, statType], index) => {
            let segmentLength = Math.abs(val);
            if (val < 0) {
              segmentLength = Math.min(segmentLength, remainingEmpty);
              remainingEmpty -= segmentLength;
            } else {
              segmentLength = Math.min(segmentLength, remainingFilled);
              remainingFilled -= segmentLength;
            }
            return (
              <div
                key={index}
                className={clsx(
                  styles.statBarSegment,
                  val < 0 && statType !== 'masterwork' ? styles.negative : statStyles[statType][0],
                )}
                style={{ width: percent(segmentLength / stat.maximumValue) }}
              />
            );
          })}
      </PressTip>
    </div>
  );
}

function StatBarTooltip({ segments, stat }: { segments: StatSegments; stat: DimStat }) {
  const showMath = !(segments.length === 1 && segments[0][1] === 'base');
  return (
    <>
      <div className={styles.statBarTooltip}>
        {showMath &&
          segments.map(([val, statType, description], index) => {
            const [typeClassName, i18nLabel] = statStyles[statType];
            const className = clsx(typeClassName, { [styles.negative]: val < 0 });
            return (
              <React.Fragment key={index}>
                <span className={className}>
                  {index > 0 && val >= 0 && '+'}
                  {val}
                </span>
                <span className={className}>{description || t(i18nLabel)}</span>
              </React.Fragment>
            );
          })}
        <span className={clsx({ [styles.tooltipTotalRow]: showMath }, styles.tooltipNetStat)}>
          <span>{stat.value}</span>
          <span>{stat.displayProperties.name}</span>
        </span>
      </div>
    </>
  );
}

function StatTotal({
  totalDetails,
  optionalClasses,
  stat,
}: {
  totalDetails: {
    baseTotalValue: number;
    totalModsValue: number;
    totalMasterworkValue: number;
  };
  optionalClasses: NodeJS.Dict<boolean>;
  stat: DimStat;
}) {
  return (
    <div
      className={clsx(styles.totalStatDetailed, optionalClasses)}
      aria-label={stat.displayProperties.name}
      title={stat.displayProperties.description}
    >
      <span>{totalDetails.baseTotalValue}</span>
      {Boolean(totalDetails.totalModsValue > 0) && (
        <span className={styles.totalStatModded}>{` + ${totalDetails.totalModsValue}`}</span>
      )}
      {Boolean(totalDetails.totalModsValue < 0) && (
        <span
          className={styles.totalStatNegativeModded}
        >{` - ${-totalDetails.totalModsValue}`}</span>
      )}
      {Boolean(totalDetails.totalMasterworkValue) && (
        <span className={styles.totalStatMasterwork}>
          {` + ${totalDetails.totalMasterworkValue}`}
        </span>
      )}
    </div>
  );
}

/**
 * A special stat row for D1 items that have item quality calculations
 */
export function D1QualitySummaryStat({ item }: { item: D1Item }) {
  if (!item.quality) {
    return null;
  }
  return (
    <>
      <div className={styles.statName}>{t('Stats.Quality')}</div>
      <div className={styles.qualitySummary} style={getD1QualityColor(item.quality.min, 'color')}>
        {t('Stats.OfMaxRoll', { range: item.quality.range })}
        <ExternalLink
          href={userGuideUrl('View-how-good-the-stat-(Int-Dis-Str)-roll-on-your-armor-is')}
          title={t('Stats.PercentHelp')}
        >
          <AppIcon icon={helpIcon} />
        </ExternalLink>
      </div>
    </>
  );
}

/**
 * Gets sockets that are considered "mods", like "Mobility Mod" in armor,
 * or "Adept Range" on weapons. These are marked blue on stat bars.
 */
function getNonReusableModSockets(item: DimItem) {
  if (!item.sockets) {
    return [];
  }

  return item.sockets.allSockets.filter(
    (s) =>
      s.plugged &&
      !s.isPerk && // excludes armor 1.0 perks and stats?
      !socketContainsIntrinsicPlug(s) &&
      !s.plugged.plugDef.plug.plugCategoryIdentifier.includes('masterwork') &&
      (s.plugged.plugDef.itemCategoryHashes?.some((h) => modItemCategoryHashes.has(h)) ||
        statfulOrnaments.includes(s.plugged.plugDef.hash)),
  );
}
/**
 * Gets weapon parts, like barrels, mags, etc.
 * Sockets that contribute to the item's stats, but aren't its base stats or mods.
 */
function getWeaponPartSockets(item: DimItem) {
  return (item.sockets?.allSockets ?? []).filter((s) =>
    s.plugged?.plugDef.itemCategoryHashes?.some((h) => weaponParts.has(h)),
  );
}

/**
 * Looks through the item sockets to find any weapon/armor mods that modify this stat.
 * Returns the value the stat is modified by, or 0 if it is not being modified.
 */
function getModEffects(item: DimItem, statHash: number) {
  const modSockets = getNonReusableModSockets(item);
  return getPlugEffects(modSockets, [statHash]);
}
/**
 * Looks through the item sockets to find any weapon/armor mods that modify this stat.
 * Returns the value the stat is modified by, or 0 if it is not being modified.
 */
function getPartEffects(item: DimItem, statHash: number) {
  const modSockets = getWeaponPartSockets(item);
  return getPlugEffects(modSockets, [statHash]);
}

export function isD1Stat(item: DimItem, _stat: DimStat): _stat is D1Stat {
  return item.destinyVersion === 1;
}

/**
 * check all sockets for plug effects upon specified statHashes, and total them
 *
 * includes a check for conditionally active stats.
 * passing the item parameter will make this more accurate
 */
function getTotalPlugEffects(sockets: DimSocket[], armorStatHashes: number[]) {
  return sumBy(getPlugEffects(sockets, armorStatHashes), ([s]) => s);
}

/**
 * check all sockets for plug effects upon specified statHashes
 *
 * includes a check for conditionally active stats.
 * passing the item parameter will make this more accurate
 *
 * returns a list of tuples of
 * [ the mod's name, its numeric effect upon selected stats ]
 */
function getPlugEffects(sockets: DimSocket[], statHashes: number[]) {
  const modEffects: [value: number, name: string][] = [];

  for (const socket of sockets) {
    if (!socket.plugged?.enabled || !socket.plugged.stats || socketContainsIntrinsicPlug(socket)) {
      continue;
    }

    for (const [statHash_, modificationAmount] of Object.entries(socket.plugged.stats)) {
      const statHash = Number(statHash_);
      if (!statHashes.includes(statHash)) {
        continue;
      }

      modEffects.push([modificationAmount.value, socket.plugged.plugDef.displayProperties.name]);
    }
  }
  return modEffects;
}

function breakDownTotalValue(
  baseTotalValue: number,
  item: DimItem,
  masterworkSockets: DimSocket[],
) {
  const modSockets = getNonReusableModSockets(item);

  // Armor 1.0 doesn't increase stats when masterworked
  const totalModsValue = getTotalPlugEffects(modSockets, armorStats);
  const totalMasterworkValue = masterworkSockets
    ? getTotalPlugEffects(masterworkSockets, armorStats)
    : 0;
  return { baseTotalValue, totalModsValue, totalMasterworkValue };
}

import { currentAccountSelector } from 'app/accounts/selectors';
import { savedLoStatConstraintsByClassSelector } from 'app/d2l-api/selectors';
import {
  allItemsSelector,
  createItemContextSelector,
  unlockedPlugSetItemsSelector,
} from 'app/inventory/selectors';
import { DimStore } from 'app/inventory/store-types';
import { loVendorItemsSelector } from 'app/loadout-builder/loadout-builder-vendors';
import { getAutoMods } from 'app/loadout-builder/process/mappers';
import { Loadout } from 'app/loadout/loadout-types';
import { d2ManifestSelector } from 'app/manifest/selectors';
import { filterFactorySelector, validateQuerySelector } from 'app/search/items/item-search-filter';
import { noop } from 'app/utils/functions';
import { currySelector } from 'app/utils/selectors';
import { useLoadVendors } from 'app/vendors/hooks';
import {
  ReactNode,
  createContext,
  use,
  useCallback,
  useEffect,
  useId,
  useState,
  useSyncExternalStore,
} from 'react';
import { useSelector } from 'react-redux';
import { createSelector } from 'reselect';
import { LoadoutBackgroundAnalyzer } from './store';
import { LoadoutAnalysisContext, LoadoutAnalysisResult, LoadoutAnalysisSummary } from './types';

const LoadoutAnalyzerReactContext = createContext<LoadoutBackgroundAnalyzer | null>(null);

const autoModSelector = createSelector(
  d2ManifestSelector,
  unlockedPlugSetItemsSelector.selector,
  (defs, unlockedPlugSetItems) => defs && getAutoMods(defs, unlockedPlugSetItems),
);

// It'd be really neat if this one didn't depend on the storeId but
// unlockedPlugSetItemsSelector and as a result, autoModSelector
// do need it.
const autoOptimizationContextSelector = currySelector(
  createSelector(
    createItemContextSelector,
    unlockedPlugSetItemsSelector.selector,
    savedLoStatConstraintsByClassSelector,
    autoModSelector,
    allItemsSelector,
    loVendorItemsSelector.selector,
    filterFactorySelector,
    validateQuerySelector,
    (
      itemCreationContext,
      unlockedPlugs,
      savedLoStatConstraintsByClass,
      autoModDefs,
      inventoryItems,
      vendorItems,
      filterFactory,
      validateQuery,
    ) => {
      const allItems = inventoryItems.concat(vendorItems);
      return (
        itemCreationContext.defs &&
        autoModDefs &&
        ({
          itemCreationContext,
          unlockedPlugs,
          savedLoStatConstraintsByClass,
          autoModDefs,
          allItems,
          filterFactory,
          validateQuery,
        } satisfies LoadoutAnalysisContext)
      );
    },
  ),
);

/** Wrapper component that holds the analyzer task and results. */
export function MakeLoadoutAnalysisAvailable({ children }: { children: ReactNode }) {
  const [analyzer, setAnalyzer] = useState<LoadoutBackgroundAnalyzer | null>(null);
  useEffect(() => {
    setAnalyzer(new LoadoutBackgroundAnalyzer());
    return () => {
      setAnalyzer((oldAnalyzer) => {
        oldAnalyzer?.destroy();
        return null;
      });
    };
  }, []);
  return <LoadoutAnalyzerReactContext value={analyzer}>{children}</LoadoutAnalyzerReactContext>;
}

/**
 * Keep injecting an up-to-date loadout analysis context into the analyzer so that
 * it can keep analyzing loadouts when the user changes items etc.
 */
export function useUpdateLoadoutAnalysisContext(storeId: string) {
  const account = useSelector(currentAccountSelector)!;
  const analyzer = use(LoadoutAnalyzerReactContext);
  const analysisContext = useSelector(autoOptimizationContextSelector(storeId));

  useLoadVendors(account, storeId);

  useEffect(
    () => analysisContext && analyzer?.updateAnalysisContext(storeId, analysisContext),
    [analysisContext, analyzer, storeId],
  );
}

/** Submit a single loadout to analysis. This will return undefined until results are available. */
export function useAnalyzeLoadout(
  loadout: Loadout,
  store: DimStore,
  active: boolean,
):
  | {
      outdated: boolean;
      result: LoadoutAnalysisResult;
    }
  | undefined {
  const id = useId();
  const analyzer = use(LoadoutAnalyzerReactContext);
  const subscribe = useCallback(
    (callback: () => void) =>
      active && analyzer
        ? analyzer.subscribeToLoadoutResult(id, store.id, store.classType, loadout, callback)
        : noop,
    [active, analyzer, id, loadout, store.classType, store.id],
  );
  const getSnapshot = useCallback(
    () => (active ? analyzer?.getLoadoutResults(store.id, loadout) : undefined),
    [active, analyzer, loadout, store.id],
  );
  return useSyncExternalStore(subscribe, getSnapshot);
}

/** Bulk analyze loadouts, returning a summary with loadout IDs grouped by findings */
export function useSummaryLoadoutsAnalysis(
  loadouts: Loadout[],
  store: DimStore,
  active: boolean,
): LoadoutAnalysisSummary | undefined {
  const id = useId();
  const analyzer = use(LoadoutAnalyzerReactContext);
  const subscribe = useCallback(
    (callback: () => void) =>
      active && analyzer
        ? analyzer?.subscribeToSummary(id, store.id, store.classType, loadouts, callback)
        : noop,
    [active, analyzer, id, loadouts, store.classType, store.id],
  );
  const getSnapshot = useCallback(
    () => (active ? analyzer?.getSummary(id) : undefined),
    [active, analyzer, id],
  );
  return useSyncExternalStore(subscribe, getSnapshot);
}

import { AssumeArmorMasterwork, LoadoutParameters } from '@destinyitemmanager/dim-api-types';
import { AlertIcon } from 'app/d2l-ui/AlertIcon';
import { D2ManifestDefinitions } from 'app/destiny2/d2-definitions';
import { t } from 'app/i18next-t';
import { PluggableInventoryItemDefinition } from 'app/inventory/item-types';
import { ResolvedLoadoutMod } from 'app/loadout/loadout-types';
import PlugDef from 'app/loadout/loadout-ui/PlugDef';
import { ModMap } from 'app/loadout/mod-assignment-utils';
import { AppIcon, banIcon } from 'app/shell/icons';
import { compact, filterMap, uniqBy } from 'app/utils/collections';
import { DestinyClass } from 'bungie-api-ts/destiny2';
import { Dispatch } from 'react';
import styles from './NoBuildsFoundExplainer.m.scss';
import ExoticArmorChoice from './filter/ExoticArmorChoice';
import LockedItem from './filter/LockedItem';
import { FilterInfo } from './item-filter';
import { LoadoutBuilderAction } from './loadout-builder-reducer';
import { ProcessStatistics, RejectionRate } from './process-worker/types';
import { ArmorBucketHashes, ArmorEnergyRules, PinnedItems } from './types';

interface ActionableSuggestion {
  id: string;
  contents: React.ReactNode;
}

interface ProblemDescription {
  description: string;
  id: string;
  suggestions: ActionableSuggestion[];
}

/** How many sets can be excluded by upper bounds before we warn. Not too high because upper bounds are rarely useful. */
const UPPER_STAT_BOUNDS_WARN_RATIO = 0.8;
/**
 * How many sets can be excluded by lower bounds before we warn.
 * Quite high because LO's purpose is literally to sift through tons of garbage sets.
 */
const LOWER_STAT_BOUNDS_WARN_RATIO = 0.95;
/**
 * Pretty much only from activity mods or too many expensive mods.
 */
const EARLY_MOD_REJECTION_WARN_RATIO = 0.8;

export default function NoBuildsFoundExplainer({
  defs,
  dispatch,
  classType,
  params,
  resolvedMods,
  lockedModMap,
  alwaysInvalidMods,
  armorEnergyRules,
  pinnedItems,
  filterInfo,
  processInfo,
}: {
  defs: D2ManifestDefinitions;
  dispatch: Dispatch<LoadoutBuilderAction>;
  classType: DestinyClass;
  params: LoadoutParameters;
  resolvedMods: ResolvedLoadoutMod[];
  lockedModMap: ModMap;
  alwaysInvalidMods: PluggableInventoryItemDefinition[];
  armorEnergyRules: ArmorEnergyRules;
  pinnedItems: PinnedItems;
  filterInfo?: FilterInfo;
  processInfo?: ProcessStatistics;
}) {
  const problems: ProblemDescription[] = [];

  // Note: This component looks at what happened to mods when they were assigned, and offers ways to
  // remove them based on that. Unfortunately removal needs ResolvedLoadoutMods, but we don't really want
  // to make all mod assignment code operate on ResolvedLoadoutMods, so instead we find the original mod
  // using a search through `resolvedMods`. We should avoid this, so maybe look at using ResolvedLoadoutMod more?

  const modRow = (mods: PluggableInventoryItemDefinition[]) => (
    <div key="modsDisplay" className={styles.modRow}>
      {mods.map((mod, index) => (
        <PlugDef
          key={index}
          plug={mod}
          onClose={() =>
            dispatch({
              type: 'removeLockedMod',
              mod: resolvedMods.find((resolved) => resolved.resolvedMod === mod)!,
            })
          }
          forClassType={classType}
        />
      ))}
    </div>
  );

  // Easy to diagnose problem -- we have things that aren't
  // armor mods, or deprecated mods. The correct option is
  // to drop them, so offer that.
  if (alwaysInvalidMods.length) {
    problems.push({
      id: 'alwaysInvalidMods',
      description: t('LoadoutBuilder.NoBuildsFoundExplainer.AlwaysInvalidMods'),
      suggestions: [
        {
          id: 'dropInvalidMods',
          contents: (
            <>
              <button
                key="removeAllInvalid"
                type="button"
                className="d2l-button"
                onClick={() =>
                  dispatch({
                    type: 'lockedModsChanged',
                    // Drop all invalid mods by setting current mods to only the valid mods
                    lockedMods: lockedModMap.allMods.map((mod) => mod.hash),
                  })
                }
              >
                <AppIcon icon={banIcon} /> {t('LoadoutBuilder.NoBuildsFoundExplainer.RemoveMods')}
              </button>
              {modRow(alwaysInvalidMods)}
            </>
          ),
        },
      ],
    });
  }

  let failedModsInBucket = false;

  // Also quite easy to diagnose: If a bucket has no valid pieces before we send to the worker,
  // then we should consider removing some item restrictions (such as unpinning/unrestricting items)
  // or removing mods.
  if (filterInfo) {
    const lockedExoticHash = params.exoticArmorHash;
    const lockedExoticBucketHash =
      lockedExoticHash !== undefined &&
      lockedExoticHash > 0 &&
      defs.InventoryItem.get(lockedExoticHash).inventory!.bucketTypeHash;
    for (const bucketHash of ArmorBucketHashes) {
      const bucketInfo = filterInfo.perBucketStats[bucketHash];
      const bucketMods = lockedModMap.bucketSpecificMods[bucketHash];
      if (bucketInfo.totalConsidered > 0 && bucketInfo.finalValid === 0 && bucketMods?.length) {
        failedModsInBucket = true;
        const suggestions: ActionableSuggestion[] = [
          {
            id: 'considerDroppingMods',
            contents: (
              <>
                {t('LoadoutBuilder.NoBuildsFoundExplainer.MaybeRemoveMods')}
                {modRow(bucketMods)}
              </>
            ),
          },
        ];
        const pinnedItem = pinnedItems[bucketHash];
        if (pinnedItem) {
          suggestions.push({
            id: 'considerUnpinningItem',
            contents: (
              <>
                {t('LoadoutBuilder.NoBuildsFoundExplainer.MaybeAllowMoreItems')}
                <div className={styles.modRow}>
                  <LockedItem
                    lockedItem={pinnedItem}
                    onRemove={() => dispatch({ type: 'unpinItem', item: pinnedItem })}
                  />
                </div>
              </>
            ),
          });
        }

        if (bucketHash === lockedExoticBucketHash) {
          suggestions.push({
            id: 'considerRemovingExotic',
            contents: (
              <>
                {t('LoadoutBuilder.NoBuildsFoundExplainer.MaybeAllowMoreItems')}
                <div className={styles.modRow}>
                  <ExoticArmorChoice
                    lockedExoticHash={lockedExoticHash!}
                    onClose={() => dispatch({ type: 'removeLockedExotic' })}
                  />
                </div>
              </>
            ),
          });
        }

        problems.push({
          id: `badBucket-${bucketHash}`,
          description: t('LoadoutBuilder.NoBuildsFoundExplainer.BadSlot', {
            bucketName: defs.InventoryBucket.get(bucketHash).displayProperties.name,
          }),
          suggestions,
        });
      }
    }
  }

  // TODO: Maybe add a "trivially infeasible slot-independent mods" check?
  // E.g. if we have solar mods in helmet, arms and chest but have more than
  // two non-solar combat mods, mod assignment is trivially infeasible and we
  // can point that out directly?

  const anyStatMinimums = params.statConstraints!.some((f) => Boolean(f.minTier || f.minStat));

  const bucketIndependentMods = [...lockedModMap.generalMods, ...lockedModMap.activityMods];

  const capacityMayCauseProblems =
    armorEnergyRules.assumeArmorMasterwork !== AssumeArmorMasterwork.All &&
    armorEnergyRules.assumeArmorMasterwork !== AssumeArmorMasterwork.ArtificeExotic &&
    (processInfo?.statistics.modsStatistics.finalAssignment.modsAssignmentFailed ||
      processInfo?.statistics.modsStatistics.finalAssignment.autoModsAssignmentFailed ||
      failedModsInBucket) &&
    (lockedModMap.allMods.length || anyStatMinimums);

  if ((!alwaysInvalidMods || alwaysInvalidMods.length === 0) && capacityMayCauseProblems) {
    // If we might have problems assigning bucket specific mods or mods in the
    // process worker, offer some advice.
    problems.push({
      id: 'armorEnergyRestrictions',
      description: t('LoadoutBuilder.NoBuildsFoundExplainer.AssumptionsRestricted'),
      suggestions: [
        {
          id: 'assumeMasterworked',
          contents: (
            <button
              key="assumeMasterworked"
              type="button"
              className="d2l-button"
              onClick={() =>
                dispatch({
                  type: 'assumeArmorMasterworkChanged',
                  assumeArmorMasterwork: AssumeArmorMasterwork.All,
                })
              }
            >
              {t('LoadoutBuilder.NoBuildsFoundExplainer.AssumeMasterworked')}
            </button>
          ),
        },
      ],
    });
  }

  // This time we made it to the LO worker process,
  // so time to investigate why LO could not find a single set.
  if (processInfo) {
    if (filterInfo?.searchQueryEffective) {
      problems.push({
        id: 'searchQuery',
        description: t('LoadoutBuilder.NoBuildsFoundExplainer.ActiveSearchQuery'),
        suggestions: [
          {
            id: 'clearQuery',
            contents: t('LoadoutBuilder.NoBuildsFoundExplainer.MaybeRemoveSearchQuery'),
          },
        ],
      });
    }

    const allPinnedItems = filterMap(ArmorBucketHashes, (hash) => pinnedItems[hash]);
    let usedUnpinSuggestion = false;
    const unpinItemsSuggestion = () => {
      if (usedUnpinSuggestion) {
        return undefined;
      }
      usedUnpinSuggestion = true;
      return (
        allPinnedItems.length > 0 && {
          id: 'considerUnpinningItems',
          contents: (
            <>
              {t('LoadoutBuilder.NoBuildsFoundExplainer.MaybeAllowMoreItems')}
              {allPinnedItems.map((pinnedItem) => (
                <div key={pinnedItem.id} className={styles.modRow}>
                  <LockedItem
                    lockedItem={pinnedItem}
                    onRemove={() => dispatch({ type: 'unpinItem', item: pinnedItem })}
                  />
                </div>
              ))}
            </>
          ),
        }
      );
    };

    // Here, we check which parts of the worker process rejected a ton of sets. LO essentially
    // checks upper bounds, lower bounds, mod assignments in that order. We base our reports on the
    // percentage of sets that failed each of the steps. This is, statistically speaking, not really a good
    // way to do it because the "last" step that fails always fails 100% of the sets it sees (otherwise
    // it either wouldn't be the last step or we wouldn't be here). So if we have three filter steps A -> B -> C and no sets,
    // where A, B, C are the events that a set would independently pass A, B and C respectively,
    // we get approximations for P(!A), P(!B | A) (B failed given that A succeeded) and P(!C | Bâˆ©A) (similarly)
    // our numFailed/numChecked rate for C will be either 0/0 or result in P(!C | Bâˆ©A) = 1.
    // Say A is the upper bounds check, B is the lower bounds check, and C is mod assignment, then if A rejected 99.9% of sets
    // and left 1 set through, this set passed lower bounds, and then we failed to assign combat mods to that 1 set in step C,
    // then blaming the selected combat mods is kind of unfair even though 100% of sets failed C. So there's no perfect way to solve this,
    // we just have to make up percentages that work well.
    // As an aside, this particularly interesting when the steps aren't statistically independent -- e.g. if everything that
    // passes A fails C and everything that fails A would pass C.
    // This might happen with stat upper bounds -- non-masterworked armor tends to have lower stats, so may pass more upper bounds
    // checks, but also has less energy capacity for mods. So upper bounds should probably be warned about quite early and often.

    const isInteresting = ({ timesChecked, timesFailed }: RejectionRate, threshold: number) =>
      timesChecked > 0 && timesFailed / timesChecked >= threshold;

    const {
      lowerBoundsExceeded,
      upperBoundsExceeded,
      modsStatistics: modsStats,
    } = processInfo.statistics;

    if (isInteresting(upperBoundsExceeded, UPPER_STAT_BOUNDS_WARN_RATIO)) {
      problems.push({
        id: 'upperBoundsExceeded',
        description: t('LoadoutBuilder.NoBuildsFoundExplainer.UpperBoundsFailed'),
        suggestions: compact([
          {
            id: 'hint',
            contents: t('LoadoutBuilder.NoBuildsFoundExplainer.MaybeIncreaseUpperBounds'),
          },
          unpinItemsSuggestion(),
        ]),
      });
    }

    if (isInteresting(lowerBoundsExceeded, LOWER_STAT_BOUNDS_WARN_RATIO)) {
      problems.push({
        id: 'lowerBoundsExceeded',
        description: t('LoadoutBuilder.NoBuildsFoundExplainer.LowerBoundsFailed'),
        suggestions: compact([
          !params.autoStatMods &&
            $featureFlags.loAutoStatMods && {
              id: 'hint1',
              contents: (
                <button
                  key="allowAutoStatMods"
                  type="button"
                  className="d2l-button"
                  onClick={() =>
                    dispatch({
                      type: 'autoStatModsChanged',
                      autoStatMods: true,
                    })
                  }
                >
                  {t('LoadoutBuilder.NoBuildsFoundExplainer.AllowAutoStatMods')}
                </button>
              ),
            },
          params.autoStatMods &&
            lockedModMap.generalMods.length > 0 && {
              id: 'removeGeneralMods',
              contents: (
                <>
                  {t('LoadoutBuilder.NoBuildsFoundExplainer.MaybeRemoveMods')}
                  {modRow(lockedModMap.generalMods)}
                </>
              ),
            },
          {
            id: 'hint2',
            contents: t('LoadoutBuilder.NoBuildsFoundExplainer.MaybeDecreaseLowerBounds'),
          },
          unpinItemsSuggestion(),
        ]),
      });
    }

    if (modsStats.earlyModsCheck.timesChecked > 0) {
      // If we got here, we took a closer look at a number of sets, but failed to pick/assign mods.

      const suggestions: (ActionableSuggestion | false | undefined)[] = [];

      if (isInteresting(modsStats.earlyModsCheck, EARLY_MOD_REJECTION_WARN_RATIO)) {
        // Early mod rejection is mod tags
        suggestions.push(
          lockedModMap.activityMods.length > 0 && {
            id: 'removeElementOrTagMods',
            contents: (
              <>
                {t('LoadoutBuilder.NoBuildsFoundExplainer.MaybeRemoveMods')}
                {modRow([...lockedModMap.activityMods])}
              </>
            ),
          },
          unpinItemsSuggestion(),
        );
      }

      if (isInteresting(modsStats.autoModsPick, LOWER_STAT_BOUNDS_WARN_RATIO)) {
        // We fail to pick stat mods to hit these stats very often, so consider
        // relaxing stat requirements and dropping general mods so min auto mods
        // has more freedom
        suggestions.push(
          lockedModMap.generalMods.length > 0 && {
            id: 'removeGeneralMods',
            contents: (
              <>
                {t('LoadoutBuilder.NoBuildsFoundExplainer.MaybeRemoveMods')}
                {modRow(lockedModMap.generalMods)}
              </>
            ),
          },
          {
            id: 'decreaseLowerBounds',
            contents: t('LoadoutBuilder.NoBuildsFoundExplainer.MaybeDecreaseLowerBounds'),
          },
          unpinItemsSuggestion(),
        );
      }

      if (modsStats.finalAssignment.modAssignmentAttempted > 0) {
        // We made it to mod assignment, but didn't end up successfully. Definitely worth pointing out.
        suggestions.push(
          bucketIndependentMods.length > 0 && {
            id: 'removeBucketIndependentMods',
            contents: (
              <>
                {t('LoadoutBuilder.NoBuildsFoundExplainer.MaybeRemoveMods')}
                {modRow(bucketIndependentMods)}
              </>
            ),
          },
          modsStats.finalAssignment.autoModsAssignmentFailed > 0 && {
            id: 'decreaseLowerBounds',
            contents: t('LoadoutBuilder.NoBuildsFoundExplainer.MaybeDecreaseLowerBounds'),
          },
          unpinItemsSuggestion(),
        );
      }
      problems.push({
        id: 'modAssignmentFailed',
        description: t('LoadoutBuilder.NoBuildsFoundExplainer.ModAssignmentFailed'),
        suggestions: uniqBy(compact(suggestions), ({ id }) => id),
      });
    }
  }

  return (
    <div className={styles.noBuildsExplainerContainer}>
      <h3 className={styles.noBuildsFoundMsg}>
        <AlertIcon />
        {t('LoadoutBuilder.NoBuildsFoundExplainer.Header')}
      </h3>
      {problems.length > 0 && (
        <ul>
          {problems.map((p) => (
            <li key={p.id}>
              <div className={styles.problemDescription}>
                <h3>{p.description}</h3>
                <ul className={styles.suggestionList}>
                  {p.suggestions.map((suggestion) => (
                    <li key={suggestion.id}>{suggestion.contents}</li>
                  ))}
                </ul>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

import {
  DragDropContext,
  Draggable,
  Droppable,
  DropResult,
  PreDragActions,
  SensorAPI,
  SnapDragActions,
} from '@hello-pangea/dnd';
import BungieImage from 'app/d2l-ui/BungieImage';
import { t } from 'app/i18next-t';
import { DimStore } from 'app/inventory/store-types';
import { MAX_STAT } from 'app/loadout/known-values';
import LoadoutEditSection from 'app/loadout/loadout-edit/LoadoutEditSection';
import { useD2Definitions } from 'app/manifest/selectors';
import { percent } from 'app/shell/formatters';
import {
  AppIcon,
  dragHandleIcon,
  faCheckSquare,
  faSquare,
  moveDownIcon,
  moveUpIcon,
} from 'app/shell/icons';
import { delay } from 'app/utils/promises';
import clsx from 'clsx';
import { Dispatch, useEffect, useId, useRef, useState } from 'react';
import { LoadoutBuilderAction } from '../loadout-builder-reducer';
import { ArmorStatHashes, MinMaxStat, ResolvedStatConstraint, StatRanges } from '../types';
import styles from './TierlessStatConstraintEditor.m.scss';

/**
 * A selector that allows for choosing minimum and maximum stat ranges, plus
 * reordering the stat priority. This does not use tiers, it allows selecting
 * exact stat values and is mean to be used after Edge of Fate releases and
 * makes all stats have an incremental effect.
 */
export default function TierlessStatConstraintEditor({
  store,
  resolvedStatConstraints,
  statRangesFiltered,
  equippedHashes,
  className,
  lbDispatch,
  processing,
}: {
  store: DimStore;
  resolvedStatConstraints: ResolvedStatConstraint[];
  /** The ranges the stats could have gotten to INCLUDING stat filters and mod compatibility */
  statRangesFiltered?: Readonly<StatRanges>;
  equippedHashes: Set<number>;
  className?: string;
  lbDispatch: Dispatch<LoadoutBuilderAction>;
  processing: boolean;
}) {
  // Actually change the stat constraints in the LO state, which triggers recalculation of sets.
  const handleStatChange = (constraint: ResolvedStatConstraint) =>
    lbDispatch({ type: 'statConstraintChanged', constraint });

  const handleClear = () => lbDispatch({ type: 'statConstraintReset' });

  const handleRandomize = () => lbDispatch({ type: 'statConstraintRandomize' });

  const handleSyncFromEquipped = () => {
    const constraints = Object.values(store.stats).map(
      (s): ResolvedStatConstraint => ({
        statHash: s.hash,
        ignored: false,
        maxStat: MAX_STAT,
        minStat: s.value,
      }),
    );
    lbDispatch({ type: 'setStatConstraints', constraints });
  };

  // Handle dropping the stat constraints in a new order
  const handleDragEnd = (result: DropResult) => {
    // dropped outside the list
    if (!result.destination) {
      return;
    }
    const sourceIndex = result.source.index;
    const destinationIndex = result.destination.index;
    if (sourceIndex !== destinationIndex) {
      lbDispatch({
        type: 'statOrderChanged',
        sourceIndex,
        destinationIndex: result.destination.index,
      });
    }
  };

  return (
    <LoadoutEditSection
      title={t('LoadoutBuilder.StatConstraints')}
      className={className}
      onClear={handleClear}
      onSyncFromEquipped={handleSyncFromEquipped}
      onRandomize={handleRandomize}
    >
      <DragDropContext onDragEnd={handleDragEnd} sensors={[useButtonSensor]}>
        <Droppable droppableId="droppable">
          {(provided) => (
            <div ref={provided.innerRef} className={styles.editor}>
              {resolvedStatConstraints.map((c, index) => {
                const statHash = c.statHash as ArmorStatHashes;
                return (
                  <StatRow
                    key={statHash}
                    statConstraint={c}
                    index={index}
                    statRange={statRangesFiltered?.[statHash]}
                    onStatChange={handleStatChange}
                    equippedHashes={equippedHashes}
                    processing={processing}
                  />
                );
              })}

              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </LoadoutEditSection>
  );
}

function StatRow({
  statConstraint,
  statRange,
  index,
  onStatChange,
  equippedHashes,
  processing,
}: {
  statConstraint: ResolvedStatConstraint;
  statRange?: MinMaxStat;
  index: number;
  onStatChange: (constraint: ResolvedStatConstraint) => void;
  equippedHashes: Set<number>;
  processing: boolean;
}) {
  const defs = useD2Definitions()!;
  const statHash = statConstraint.statHash as ArmorStatHashes;
  const statDef = defs.Stat.get(statHash);
  const handleIgnore = () => onStatChange({ ...statConstraint, ignored: !statConstraint.ignored });

  const setMin = (value: number) => {
    if (value !== statConstraint.minStat) {
      onStatChange({
        ...statConstraint,
        minStat: value,
        maxStat: Math.max(value, statConstraint.maxStat),
      });
    }
  };
  const setMax = (value: number) => {
    if (value !== statConstraint.maxStat) {
      onStatChange({
        ...statConstraint,
        minStat: Math.min(value, statConstraint.minStat),
        maxStat: value,
      });
    }
  };

  const min = statConstraint.minStat;
  const max = statConstraint.maxStat;

  return (
    <Draggable draggableId={statHash.toString()} index={index}>
      {(provided, snapshot) => (
        <div
          className={clsx(styles.row, {
            [styles.dragging]: snapshot.isDragging,
            [styles.ignored]: statConstraint.ignored,
          })}
          data-index={index}
          ref={provided.innerRef}
          {...provided.draggableProps}
        >
          <span
            className={styles.grip}
            {...provided.dragHandleProps}
            tabIndex={-1}
            aria-hidden={true}
          >
            <AppIcon icon={dragHandleIcon} />
          </span>
          <div className={styles.name}>
            <button
              type="button"
              role="checkbox"
              aria-checked={!statConstraint.ignored}
              className={styles.rowControl}
              onClick={handleIgnore}
              title={t('LoadoutBuilder.IgnoreStat')}
            >
              <AppIcon icon={statConstraint.ignored ? faSquare : faCheckSquare} />
            </button>
            <div className={styles.label} {...provided.dragHandleProps}>
              <BungieImage
                className={styles.iconStat}
                src={statDef.displayProperties.icon}
                aria-hidden={true}
                alt=""
              />
              {statDef.displayProperties.name}
            </div>
          </div>
          <div className={styles.buttons}>
            <button
              type="button"
              className={styles.rowControl}
              title={t('LoadoutBuilder.IncreaseStatPriority')}
              disabled={index === 0}
              tabIndex={-1 /* Better to use the react-dnd keyboard interactions than this button */}
              data-direction="up"
              data-draggable-id={statHash.toString()}
            >
              <AppIcon icon={moveUpIcon} />
            </button>
            <button
              type="button"
              className={styles.rowControl}
              title={t('LoadoutBuilder.DecreaseStatPriority')}
              disabled={index === 5}
              tabIndex={-1 /* Better to use the react-dnd keyboard interactions than this button */}
              data-direction="down"
              data-draggable-id={statHash.toString()}
            >
              <AppIcon icon={moveDownIcon} />
            </button>
          </div>
          {!statConstraint.ignored && (
            <StatEditBar min={min} max={max} setMin={setMin} setMax={setMax}>
              <StatBar
                range={statRange}
                equippedHashes={equippedHashes}
                min={min}
                max={max}
                setMin={setMin}
                setMax={setMax}
                processing={processing}
              />
            </StatEditBar>
          )}
        </div>
      )}
    </Draggable>
  );
}

function StatEditBar({
  min,
  max,
  setMin,
  setMax,
  children,
}: {
  min: number;
  max: number;
  setMin: (value: number) => void;
  setMax: (value: number) => void;
  children: React.ReactNode;
}) {
  const id = useId();
  const [minText, setMinText] = useState(min.toString());
  const [maxText, setMaxText] = useState(max.toString());
  useEffect(() => {
    setMinText(min.toString());
  }, [min]);
  useEffect(() => {
    setMaxText(max.toString());
  }, [max]);

  const setMinMaxFromText = (text: string, setter: (s: number) => void) => {
    const value = parseInt(text, 10);
    if (isNaN(value) || value < 0 || value > MAX_STAT) {
      return;
    }
    setter(value);
  };
  const setMinFromText = setMinMaxFromText.bind(null, minText, setMin);
  const setMaxFromText = setMinMaxFromText.bind(null, maxText, setMax);

  const handleEnter = (setter: () => void) => (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      setter();
    }
  };

  return (
    <div className={styles.statBar}>
      <input
        id={`${id}-min`}
        type="number"
        min={0}
        max={MAX_STAT}
        value={minText}
        aria-label={t('LoadoutBuilder.StatMin')}
        onChange={(e) => {
          setMinText(e.target.value);
        }}
        onBlur={setMinFromText}
        onKeyDown={handleEnter(setMinFromText)}
      />
      {children}
      <input
        id={`${id}-max`}
        type="number"
        min={0}
        max={MAX_STAT}
        value={maxText}
        aria-label={t('LoadoutBuilder.StatMax')}
        onChange={(e) => {
          setMaxText(e.target.value);
        }}
        onBlur={setMaxFromText}
        onKeyDown={handleEnter(setMaxFromText)}
      />
    </div>
  );
}

function StatBar({
  min,
  max,
  range,
  setMin,
  setMax,
  processing,
}: {
  range?: MinMaxStat;
  equippedHashes: Set<number>;
  min: number;
  max: number;
  setMin: (value: number) => void;
  setMax: (value: number) => void;
  processing: boolean;
}) {
  const [dragging, setDragging] = useState(false);
  const [dragValue, setDragValue] = useState(0);
  // Whether we're dragging the max or min value
  const draggingMax = useRef(false);
  const lastClickTime = useRef(0);

  // Set the live value of min or max based on where the pointer is
  const setValueToPointer = (e: React.PointerEvent<HTMLDivElement>) => {
    const bar = e.currentTarget;
    const rect = bar.getBoundingClientRect();
    const clickX = e.clientX - rect.left;
    const ratio = Math.max(0, Math.min(1, clickX / rect.width));
    const value = Math.round(ratio * MAX_STAT);
    setDragValue(value);
  };

  const handlePointerMove = (e: React.PointerEvent<HTMLDivElement>) => {
    setValueToPointer(e);
  };

  // Commit the value on pointer up
  const handlePointerUp = (e: React.PointerEvent<HTMLDivElement>) => {
    const bar = e.currentTarget;
    bar.releasePointerCapture(e.pointerId);
    // Detect double-click
    if (performance.now() - lastClickTime.current < 200 && !draggingMax.current && range) {
      setMin(range.maxStat);
    } else {
      draggingMax.current ? setMax(dragValue) : setMin(dragValue);
    }
    setDragging(false);
    lastClickTime.current = performance.now();
  };
  const handlePointerDown = (e: React.PointerEvent<HTMLDivElement>) => {
    // If you shift-click you set max, or if you click on the max bar first.
    draggingMax.current = e.shiftKey || (e.target as Element).classList.contains(styles.statBarMax);
    const bar = e.currentTarget;
    bar.setPointerCapture(e.pointerId);
    setValueToPointer(e);
    setDragging(true);
  };

  const effectiveMin = dragging && !draggingMax.current ? dragValue : min;
  const effectiveMax = dragging && draggingMax.current ? dragValue : max;

  return (
    <div
      className={styles.statRange}
      title={
        range &&
        t('LoadoutBuilder.StatRangeTooltip', {
          min: range.minStat,
          max: range.maxStat,
        })
      }
      onPointerDown={handlePointerDown}
      onPointerUp={dragging ? handlePointerUp : undefined}
      onPointerMove={dragging ? handlePointerMove : undefined}
    >
      {range && range.minStat < range.maxStat && (
        <div
          className={clsx(styles.statBarFill, { [styles.processing]: processing })}
          style={{
            left: percent(range.minStat / MAX_STAT),
            width: percent((range.maxStat - range.minStat) / MAX_STAT),
          }}
        />
      )}
      {(!range || range.minStat !== max) && (
        <div
          key="min"
          className={styles.statBarMin}
          style={{ left: percent(effectiveMin / MAX_STAT) }}
        />
      )}
      {(!range || range.maxStat !== max) && (
        <div
          key="max"
          className={styles.statBarMax}
          style={{ left: percent(effectiveMax / MAX_STAT) }}
        />
      )}
    </div>
  );
}

// Listen for button presses on the up and down buttons and turn it into lift+move+drop actions.
function useButtonSensor(api: SensorAPI) {
  useEffect(() => {
    const onClick = (event: MouseEvent) => {
      // Event already used
      if (event.defaultPrevented) {
        return;
      }

      const target = event.target as HTMLButtonElement;
      if (
        target.tagName !== 'BUTTON' ||
        target.disabled ||
        !target.dataset.direction ||
        !target.dataset.draggableId
      ) {
        return;
      }

      const draggableId = target.dataset.draggableId;
      if (!draggableId) {
        return;
      }

      const preDrag: PreDragActions | null = api.tryGetLock(draggableId);
      if (!preDrag) {
        return;
      }

      // we are consuming the event
      event.preventDefault();

      (async () => {
        const actions: SnapDragActions = preDrag.snapLift();
        if (target.dataset.direction === 'down') {
          actions.moveDown();
        } else if (target.dataset.direction === 'up') {
          actions.moveUp();
        }
        await delay(300);
        actions.drop();
      })();
    };
    document.addEventListener('click', onClick);
    return () => document.removeEventListener('click', onClick);
  }, [api]);
}

import { DestinyAccount } from 'app/accounts/destiny-account';
import { t } from 'app/i18next-t';
import { DimItem } from 'app/inventory/item-types';
import { storesSelector } from 'app/inventory/selectors';
import { getCurrentStore } from 'app/inventory/stores-helpers';
import { warnMissingClass } from 'app/loadout-builder/loadout-builder-reducer';
import { decodeUrlLoadout } from 'app/loadout/loadout-share/loadout-import';
import { useD2Definitions } from 'app/manifest/selectors';
import { showNotification } from 'app/notifications/notifications';
import { errorMessage } from 'app/utils/errors';
import { useEventBusListener } from 'app/utils/hooks';
import { DestinyClass } from 'bungie-api-ts/destiny2';
import { Suspense, lazy, useCallback, useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { useLocation, useNavigate } from 'react-router';
import { EditLoadoutState, addItem$, editLoadout$ } from './loadout-events';
import { convertToLoadoutItem, newLoadout, pickBackingStore } from './loadout-utils';

const LoadoutDrawer = lazy(
  () => import(/* webpackChunkName: "loadout-drawer" */ './LoadoutDrawer'),
);
const D1LoadoutDrawer = lazy(
  () =>
    import(
      /* webpackChunkName: "d1-loadout-drawer" */ 'app/destiny1/loadout-drawer/D1LoadoutDrawer'
    ),
);

/**
 * A launcher for the LoadoutDrawer. This is used both so we can lazy-load the
 * LoadoutDrawer, and so we can make sure defs are defined when the loadout
 * drawer is rendered.
 */
export default function LoadoutDrawerContainer({ account }: { account: DestinyAccount }) {
  const defs = useD2Definitions();
  const navigate = useNavigate();
  const { search: queryString, pathname } = useLocation();

  // This state only holds the initial version of the loadout that launches the
  // drawer - after that, the loadout drawer itself manages edits to the
  // loadout.
  // TODO: Alternately we could come up with the concept of a
  // `useControlledReducer` that applied a reducer to mutate an object whose
  // state is handled outside the component.
  const [initialLoadout, setInitialLoadout] = useState<EditLoadoutState>();

  const handleDrawerClose = useCallback(() => {
    setInitialLoadout(undefined);
  }, []);

  const stores = useSelector(storesSelector);

  // The loadout to edit comes in from the editLoadout$ observable
  useEventBusListener(
    editLoadout$,
    useCallback(
      (state) => {
        const { storeId, loadout } = state;
        // Fall back to current store because otherwise there's no way to delete loadouts
        // the user doesn't have a class for.
        const editingStore =
          pickBackingStore(stores, storeId, loadout.classType) ?? getCurrentStore(stores);

        if (!editingStore) {
          if (defs) {
            warnMissingClass(loadout.classType, defs);
          }
          return;
        }

        setInitialLoadout({ ...state, storeId: editingStore.id });
      },
      [stores, defs],
    ),
  );

  const hasInitialLoadout = Boolean(initialLoadout);

  // Only react to add item if there's not a loadout open (otherwise it'll be handled in the loadout drawer!)
  useEventBusListener(
    addItem$,
    useCallback(
      (item: DimItem) => {
        if (!hasInitialLoadout) {
          // If we don't have a loadout, this action was invoked via the "+ Loadout" button
          // in item actions, so pick the best store to back this loadout with
          const owner = pickBackingStore(stores, item.owner, item.classType);

          if (!owner) {
            showNotification({
              type: 'warning',
              title: t('Loadouts.ClassTypeMissing', { className: item.classTypeNameLocalized }),
            });
            return;
          }

          const classType =
            item.classType === DestinyClass.Unknown ? owner.classType : item.classType;
          const draftLoadout = newLoadout('', [], classType);
          draftLoadout.items.push(convertToLoadoutItem(item, true));
          setInitialLoadout({
            loadout: draftLoadout,
            storeId: owner.id,
            showClass: true,
            fromExternal: true,
          });
        }
      },
      [hasInitialLoadout, stores],
    ),
  );

  // Load in a full loadout specified in the URL
  useEffect(() => {
    if (!stores.length || !defs?.isDestiny2) {
      return;
    }
    try {
      const parsedLoadout = decodeUrlLoadout(queryString);
      if (parsedLoadout) {
        const storeId = pickBackingStore(stores, undefined, parsedLoadout.classType)?.id;

        if (!storeId) {
          warnMissingClass(parsedLoadout.classType, defs);
          return;
        }

        setInitialLoadout({
          loadout: parsedLoadout,
          storeId,
          showClass: false,
          fromExternal: true,
        });
        // Clear the loadout from params if the URL contained one...
        navigate(pathname, { replace: true });
      }
    } catch (e) {
      showNotification({
        type: 'error',
        title: t('Loadouts.BadLoadoutShare'),
        body: t('Loadouts.BadLoadoutShareBody', { error: errorMessage(e) }),
      });
      // ... or if it contained errors
      navigate(pathname, { replace: true });
    }
  }, [defs, queryString, navigate, pathname, stores]);

  // Close the loadout on navigation
  // TODO: prompt for saving?
  useEffect(() => {
    // Don't close if moving to the inventory or loadouts screen
    if (!pathname.endsWith('inventory') && !pathname.endsWith('loadouts')) {
      handleDrawerClose();
    }
  }, [handleDrawerClose, pathname]);

  if (initialLoadout) {
    return (
      <Suspense fallback={null}>
        {account.destinyVersion === 2 ? (
          <LoadoutDrawer
            initialLoadout={initialLoadout.loadout}
            storeId={initialLoadout.storeId}
            onClose={handleDrawerClose}
            fromExternal={initialLoadout.fromExternal}
          />
        ) : (
          <D1LoadoutDrawer
            initialLoadout={initialLoadout.loadout}
            storeId={initialLoadout.storeId}
            showClass={initialLoadout.showClass}
            onClose={handleDrawerClose}
          />
        )}
      </Suspense>
    );
  }
  return null;
}

import { LoadoutParameters } from '@destinyitemmanager/dim-api-types';
import ClosableContainer from 'app/d2l-ui/ClosableContainer';
import { PressTip } from 'app/d2l-ui/PressTip';
import Sheet from 'app/d2l-ui/Sheet';
import { D2ManifestDefinitions } from 'app/destiny2/d2-definitions';
import { t } from 'app/i18next-t';
import ConnectedInventoryItem from 'app/inventory/ConnectedInventoryItem';
import { DefItemIcon } from 'app/inventory/ItemIcon';
import { DimItem, DimSocket, PluggableInventoryItemDefinition } from 'app/inventory/item-types';
import { allItemsSelector, unlockedPlugSetItemsSelector } from 'app/inventory/selectors';
import SocketDetails from 'app/item-popup/SocketDetails';
import { ArmorBucketHashes } from 'app/loadout-builder/types';
import { Loadout, ResolvedLoadoutItem } from 'app/loadout/loadout-types';
import { useD2Definitions } from 'app/manifest/selectors';
import { DEFAULT_ORNAMENTS, DEFAULT_SHADER } from 'app/search/d2-known-values';
import { AppIcon, clearIcon, rightArrowIcon } from 'app/shell/icons';
import { useIsPhonePortrait } from 'app/shell/selectors';
import { filterMap, isEmpty } from 'app/utils/collections';
import { getSocketsByCategoryHash, plugFitsIntoSocket } from 'app/utils/socket-utils';
import { HashLookup } from 'app/utils/util-types';
import {
  DestinyCollectibleDefinition,
  DestinyInventoryItemDefinition,
} from 'bungie-api-ts/destiny2';
import clsx from 'clsx';
import { PlugCategoryHashes, SocketCategoryHashes } from 'data/d2/generated-enums';
import { keyBy, maxBy } from 'es-toolkit';
import { produce } from 'immer';
import { useState } from 'react';
import { useSelector } from 'react-redux';
import { BucketPlaceholder } from '../loadout-ui/BucketPlaceholder';
import PlugDef from '../loadout-ui/PlugDef';
import styles from './FashionDrawer.m.scss';

interface PickPlugState {
  item: DimItem;
  socket: DimSocket;
}

/** An editor for "Fashion" (shaders and ornaments) in a loadout */
export default function FashionDrawer({
  loadout,
  storeId,
  items,
  onModsByBucketUpdated,
  onClose,
}: {
  loadout: Loadout;
  storeId?: string;
  items: ResolvedLoadoutItem[];
  onModsByBucketUpdated: (modsByBucket: LoadoutParameters['modsByBucket']) => void;
  onClose: () => void;
}) {
  const defs = useD2Definitions()!;
  const unlockedPlugs = useSelector(unlockedPlugSetItemsSelector(storeId));
  const isPhonePortrait = useIsPhonePortrait();
  const [pickPlug, setPickPlug] = useState<PickPlugState>();
  const allItems = useSelector(allItemsSelector);
  const armor = items.filter(
    (li) => li.loadoutItem.equip && ArmorBucketHashes.includes(li.item.bucket.hash),
  );

  const classType = loadout.classType;

  const armorItemsByBucketHash: HashLookup<ResolvedLoadoutItem> = keyBy(
    armor,
    (li) => li.item.bucket.hash,
  );

  // The items we'll use to determine what sockets are available on each item. Either the equipped item from
  // the loadout, or a suitable "example" item from inventory.
  const exampleItemsByBucketHash = Object.fromEntries(
    ArmorBucketHashes.map((bucketHash) => {
      const looksFashionable = (i: DimItem) =>
        i.bucket.hash === bucketHash && i.power && i.energy && i.classType === classType;
      const getFashionSockets = (i: DimItem) =>
        getSocketsByCategoryHash(i.sockets, SocketCategoryHashes.ArmorCosmetics);

      // TODO: is this really the best way to do this? we just default to the equipped item, but that may be an exotic
      const exampleItem =
        armorItemsByBucketHash[bucketHash]?.item ??
        // Try to find a legendary example
        allItems.find(
          (i) => i.rarity === 'Legendary' && looksFashionable(i) && getFashionSockets(i).length > 1,
        ) ??
        // Fall back to any non-exotic example
        allItems.find(
          (i) => !i.isExotic && looksFashionable(i) && getFashionSockets(i).length > 1,
        ) ??
        // Finally, find something shaderable at least. This user must have only rud2lentary armor.
        allItems.find(
          (i) =>
            !i.isExotic &&
            i.bucket.hash === bucketHash &&
            i.classType === classType &&
            getFashionSockets(i).length,
        );
      return [bucketHash, exampleItem];
    }),
  );

  const [modsByBucket, setModsByBucket] = useState(loadout.parameters?.modsByBucket ?? {});
  const isShader = (h: number) =>
    defs.InventoryItem.get(h)?.plug?.plugCategoryHash === PlugCategoryHashes.Shader;
  const modHashes = Object.values(modsByBucket).flat();
  const shaders = modHashes.filter(isShader);
  const ornaments = modHashes.filter((h) => !isShader(h));

  const header = (
    <>
      <h1>{t('FashionDrawer.Title')}</h1>
    </>
  );

  const footer = ({ onClose }: { onClose: () => void }) => (
    <>
      <button
        type="button"
        className="d2l-button"
        onClick={() => {
          onModsByBucketUpdated(modsByBucket);
          onClose();
        }}
      >
        {t('FashionDrawer.Accept')}
      </button>
    </>
  );

  const handlePlugSelected = ({
    item,
    socket,
    plugHash,
  }: {
    item: DimItem;
    socket: DimSocket;
    plugHash: number;
  }) => {
    setModsByBucket((modsByBucket) => {
      // Clear out existing selections for this socket.
      const existingMods = (modsByBucket[item.bucket.hash] ?? []).filter(
        (mod) => !plugFitsIntoSocket(socket, mod),
      );

      return {
        ...modsByBucket,
        // Add in the new mod
        [item.bucket.hash]: [...existingMods, plugHash],
      };
    });
  };

  const handleUseEquipped = () => {
    const newModsByBucket = Object.fromEntries(
      ArmorBucketHashes.map((bucketHash) => {
        // Either the item that's in the loadout, or whatever's equipped
        const item =
          armorItemsByBucketHash[bucketHash]?.item ??
          allItems.find(
            (i) =>
              i.bucket.hash === bucketHash &&
              i.equipped &&
              (storeId ? i.owner === storeId : i.classType === classType),
          );
        const cosmeticSockets = item?.sockets
          ? getSocketsByCategoryHash(item.sockets, SocketCategoryHashes.ArmorCosmetics)
          : [];
        return [
          bucketHash,
          filterMap(cosmeticSockets, (s) => (s.actuallyPlugged || s.plugged)?.plugDef.hash),
        ];
      }),
    );

    setModsByBucket(newModsByBucket);
  };

  const handleSyncShader = () => {
    const shaders = Object.values(modsByBucket).flat().filter(isShader);

    const groupedShaders = Object.groupBy(shaders, (h) => h);
    const mostCommonShaders = maxBy(
      Object.values(groupedShaders),
      (shaders) => shaders?.length || 0,
    );
    if (!mostCommonShaders) {
      return;
    }

    setModsByBucket((modsByBucket) =>
      Object.fromEntries(
        ArmorBucketHashes.map((bucketHash) => [
          bucketHash,
          [...(modsByBucket[bucketHash] ?? []).filter((h) => !isShader(h)), mostCommonShaders[0]],
        ]),
      ),
    );
  };

  const handleSyncOrnament = () => {
    const isShader = (h: number) =>
      defs.InventoryItem.get(h)?.plug?.plugCategoryHash === PlugCategoryHashes.Shader;
    const ornaments = Object.values(modsByBucket)
      .flat()
      .filter((h) => !isShader(h));

    // Make it easy to spread default ornament
    if (ornaments.every((h) => DEFAULT_ORNAMENTS.includes(h))) {
      setModsByBucket((modsByBucket) =>
        Object.fromEntries(
          ArmorBucketHashes.map((bucketHash) => [
            bucketHash,
            [...(modsByBucket[bucketHash] ?? []).filter((h) => isShader(h)), ornaments[0]],
          ]),
        ),
      );
      return;
    }

    const groupedOrnaments = Object.groupBy(ornaments, (h) => {
      const collectibleHash =
        defs.InventoryItem.get(h)?.collectibleHash ??
        // if the item has no collectible hash, try to find an "identical" item with one
        findOtherCopies(defs, h).find((i) => i.collectibleHash)?.collectibleHash;

      return (collectibleHash && defs.Collectible.get(collectibleHash)?.parentNodeHashes[0]) ?? -1;
    });
    delete groupedOrnaments[-1];
    const mostCommonOrnamentSet = maxBy(
      Object.entries(groupedOrnaments),
      ([_presentationHash, ornaments]) => ornaments?.length || 0,
    );
    if (!mostCommonOrnamentSet) {
      return;
    }

    const mostCommon = parseInt(mostCommonOrnamentSet[0], 10);
    const set = filterMap(
      defs.PresentationNode.get(mostCommon).children.collectibles,
      (c) =>
        defs.Collectible.get(c.collectibleHash)?.itemHash ??
        manuallyFindItemForCollectible(defs, c.collectibleHash)?.hash,
    );

    if (set.length !== 5) {
      return;
    }

    setModsByBucket((modsByBucket) =>
      Object.fromEntries(
        ArmorBucketHashes.map((bucketHash, i) => {
          let ornamentHash = set[i];

          // if we picked a parent node that doesn't point to ornaments,
          // try to find an "identical" item that *is* an unlocked plug
          if (!unlockedPlugs.has(ornamentHash)) {
            const ornamentVersion = findOtherCopies(defs, ornamentHash).find((i) =>
              unlockedPlugs.has(i.hash),
            );
            ornamentHash = ornamentVersion?.hash ?? ornamentHash;
          }

          if (unlockedPlugs.has(ornamentHash)) {
            const modsWithoutShaders = (modsByBucket[bucketHash] ?? []).filter((h) => isShader(h));
            const mods = [...modsWithoutShaders, ornamentHash];
            return [bucketHash, mods];
          }

          return [bucketHash, modsByBucket[bucketHash] ?? []];
        }),
      ),
    );
  };

  const handleClearType = (shaders: boolean) => {
    setModsByBucket(
      produce((modsByBucket) => {
        for (const bucket in modsByBucket) {
          const mods = modsByBucket[bucket];
          const modsWithoutShaders = mods.filter((h) => (shaders ? !isShader(h) : isShader(h)));
          if (modsWithoutShaders.length === 0) {
            delete modsByBucket[bucket];
          } else {
            modsByBucket[bucket] = modsWithoutShaders;
          }
        }
      }),
    );
  };

  const handleRemovePlug = (bucketHash: number, plugHash: number) => {
    setModsByBucket((modsByBucket) => ({
      ...modsByBucket,
      [bucketHash]: modsByBucket[bucketHash].filter((m) => m !== plugHash),
    }));
  };

  const leftButtons = (
    <>
      <div>
        <button type="button" className="d2l-button" onClick={handleUseEquipped}>
          {t('FashionDrawer.UseEquipped')}
        </button>
      </div>
      <div>
        <button
          type="button"
          className="d2l-button"
          onClick={handleSyncShader}
          disabled={shaders.length === 0}
          title={t('FashionDrawer.SyncShadersTitle')}
        >
          {isPhonePortrait ? t('FashionDrawer.SyncShaders') : t('FashionDrawer.Sync')}{' '}
          <AppIcon icon={rightArrowIcon} />
        </button>
      </div>
      <div>
        <button
          type="button"
          className="d2l-button"
          onClick={handleSyncOrnament}
          disabled={ornaments.length === 0}
          title={t('FashionDrawer.SyncOrnamentsTitle')}
        >
          {isPhonePortrait ? t('FashionDrawer.SyncOrnaments') : t('FashionDrawer.Sync')}{' '}
          <AppIcon icon={rightArrowIcon} />
        </button>
      </div>
    </>
  );

  const rightButtons = (
    <>
      <div>
        <button
          type="button"
          className="d2l-button"
          onClick={() => setModsByBucket({})}
          disabled={isEmpty(modsByBucket)}
        >
          {t('FashionDrawer.Reset')}
        </button>
      </div>
      <div>
        <button
          type="button"
          className="d2l-button"
          onClick={() => handleClearType(true)}
          disabled={shaders.length === 0}
          title={t('FashionDrawer.ClearShadersTitle')}
        >
          <AppIcon icon={clearIcon} /> {isPhonePortrait && t('FashionDrawer.ClearShaders')}
        </button>
      </div>
      <div>
        <button
          type="button"
          className="d2l-button"
          onClick={() => handleClearType(false)}
          disabled={ornaments.length === 0}
          title={t('FashionDrawer.ClearOrnamentsTitle')}
        >
          <AppIcon icon={clearIcon} /> {isPhonePortrait && t('FashionDrawer.ClearOrnaments')}
        </button>
      </div>
    </>
  );

  return (
    <Sheet onClose={onClose} header={header} footer={footer} sheetClassName={styles.sheet}>
      <div className={styles.items}>
        {!isPhonePortrait && <div className={styles.verticalButtons}>{leftButtons}</div>}
        {ArmorBucketHashes.map((bucketHash) => (
          <FashionItem
            key={bucketHash}
            bucketHash={bucketHash}
            item={armorItemsByBucketHash[bucketHash]}
            exampleItem={exampleItemsByBucketHash[bucketHash]}
            mods={modsByBucket[bucketHash]}
            storeId={storeId}
            onPickPlug={setPickPlug}
            onRemovePlug={handleRemovePlug}
          />
        ))}
        {!isPhonePortrait && (
          <div className={clsx(styles.verticalButtons, styles.right)}>{rightButtons}</div>
        )}
      </div>
      {isPhonePortrait && (
        <div className={styles.buttons}>
          {leftButtons}
          {rightButtons}
        </div>
      )}
      {pickPlug && (
        <SocketDetails
          item={pickPlug.item}
          allowInsertPlug={false}
          socket={pickPlug.socket}
          onClose={() => setPickPlug(undefined)}
          onPlugSelected={handlePlugSelected}
        />
      )}
    </Sheet>
  );
}

function FashionItem({
  item,
  exampleItem,
  bucketHash,
  mods = [],
  storeId,
  onPickPlug,
  onRemovePlug,
}: {
  item?: ResolvedLoadoutItem;
  exampleItem?: DimItem;
  bucketHash: number;
  mods?: number[];
  storeId?: string;
  onPickPlug: (params: PickPlugState) => void;
  onRemovePlug: (bucketHash: number, modHash: number) => void;
}) {
  const defs = useD2Definitions()!;
  const isShader = (m: number) =>
    defs.InventoryItem.get(m)?.plug?.plugCategoryHash === PlugCategoryHashes.Shader;
  const shader = mods.find(isShader);
  const ornament = mods.find((m) => !isShader(m));

  const shaderItem = shader ? defs.InventoryItem.get(shader) : undefined;
  const ornamentItem = ornament ? defs.InventoryItem.get(ornament) : undefined;

  if (!exampleItem) {
    return null;
  }

  // Divide valid sockets (with something plugged) into a shader socket and
  // an ornament socket -- all items have two cosmetic sockets
  // (really old Solstice armor has a third one for the glow at the end, and
  // the exotic Loreley Splendor helm has a dummy socket with nothing plugged)
  const isShaderSocket = (s: DimSocket) =>
    defs.SocketType.get(s.socketDefinition.socketTypeHash)?.plugWhitelist.some(
      (pw) => pw.categoryHash === PlugCategoryHashes.Shader,
    );
  const cosmeticSockets = getSocketsByCategoryHash(
    exampleItem.sockets,
    SocketCategoryHashes.ArmorCosmetics,
  ).filter((s) => s.plugged);
  const shaderSocket = cosmeticSockets.find(isShaderSocket);
  const ornamentSocket = cosmeticSockets.find((s) => !isShaderSocket(s));

  const defaultShader = defs.InventoryItem.get(DEFAULT_SHADER);
  const defaultOrnament = defs.InventoryItem.get(DEFAULT_ORNAMENTS[2]);

  return (
    <div className={styles.item}>
      {item ? (
        <ConnectedInventoryItem item={exampleItem} />
      ) : (
        <BucketPlaceholder bucketHash={bucketHash} />
      )}
      <FashionSocket
        bucketHash={bucketHash}
        socket={shaderSocket}
        plug={shaderItem}
        exampleItem={exampleItem}
        defaultPlug={defaultShader}
        storeId={storeId}
        onPickPlug={onPickPlug}
        onRemovePlug={onRemovePlug}
      />
      <FashionSocket
        bucketHash={bucketHash}
        socket={ornamentSocket}
        plug={ornamentItem}
        exampleItem={exampleItem}
        storeId={storeId}
        defaultPlug={defaultOrnament}
        onPickPlug={onPickPlug}
        onRemovePlug={onRemovePlug}
      />
    </div>
  );
}

function FashionSocket({
  bucketHash,
  socket,
  plug,
  exampleItem,
  storeId,
  defaultPlug,
  onPickPlug,
  onRemovePlug,
}: {
  bucketHash: number;
  socket: DimSocket | undefined;
  plug: DestinyInventoryItemDefinition | undefined;
  exampleItem: DimItem;
  storeId?: string;
  defaultPlug: DestinyInventoryItemDefinition;
  onPickPlug: (params: PickPlugState) => void;
  onRemovePlug: (bucketHash: number, modHash: number) => void;
}) {
  const unlockedPlugSetItems = useSelector(unlockedPlugSetItemsSelector(storeId));
  const handleOrnamentClick = socket && (() => onPickPlug({ item: exampleItem, socket }));

  const unlockedPlugsWithoutTheDefault = new Set(
    Array.from(unlockedPlugSetItems).filter((plugHash) => plugHash !== defaultPlug.hash),
  );

  const canSlotOrnament =
    socket?.plugSet?.plugs.some((plug) => unlockedPlugsWithoutTheDefault.has(plug.plugDef.hash)) ||
    socket?.reusablePlugItems?.some(
      (plug) => plug.enabled && unlockedPlugsWithoutTheDefault.has(plug.plugItemHash),
    );

  return (
    <ClosableContainer onClose={plug ? () => onRemovePlug(bucketHash, plug.hash) : undefined}>
      {plug && canSlotOrnament ? (
        <PlugDef
          onClick={handleOrnamentClick}
          plug={plug as PluggableInventoryItemDefinition}
          forClassType={undefined}
        />
      ) : (
        <PressTip
          tooltip={
            <div>
              {canSlotOrnament
                ? t('FashionDrawer.NoPreference')
                : plug?.plug?.plugCategoryHash === PlugCategoryHashes.Shader
                  ? t('FashionDrawer.CannotFitShader')
                  : t('FashionDrawer.CannotFitOrnament')}
            </div>
          }
        >
          <div
            role="button"
            tabIndex={0}
            className={clsx('item', styles.missingItem, { [styles.noOrnament]: !canSlotOrnament })}
            onClick={canSlotOrnament ? handleOrnamentClick : undefined}
          >
            <DefItemIcon itemDef={defaultPlug} />
          </div>
        </PressTip>
      )}
    </ClosableContainer>
  );
}

function findOtherCopies(
  defs: D2ManifestDefinitions,
  item: DestinyInventoryItemDefinition | DimItem | number,
) {
  const itemDef = defs.InventoryItem.get(typeof item === 'number' ? item : item.hash);
  const results: DestinyInventoryItemDefinition[] = [];
  const invItemTable = defs.InventoryItem.getAll();
  for (const h in invItemTable) {
    const i = invItemTable[h];
    if (
      i.displayProperties.name === itemDef.displayProperties.name &&
      i.displayProperties.icon === itemDef.displayProperties.icon
    ) {
      results.push(i);
    }
  }
  return results;
}

function manuallyFindItemForCollectible(
  defs: D2ManifestDefinitions,
  collectible: DestinyCollectibleDefinition | number,
) {
  const collectibleHash = typeof collectible === 'number' ? collectible : collectible.hash;
  const invItemTable = defs.InventoryItem.getAll();
  for (const h in invItemTable) {
    const i = invItemTable[h];
    if (i.collectibleHash === collectibleHash) {
      return i;
    }
  }
}

import Sheet from 'app/d2l-ui/Sheet';
import UserGuideLink from 'app/d2l-ui/UserGuideLink';
import { t } from 'app/i18next-t';
import { editLoadout } from 'app/loadout-drawer/loadout-events';
import { AppIcon, refreshIcon } from 'app/shell/icons';
import { useIsPhonePortrait } from 'app/shell/selectors';
import { isiOSBrowser } from 'app/utils/browsers';
import { errorMessage } from 'app/utils/errors';
import { useEffect, useState } from 'react';
import styles from './LoadoutImportSheet.m.scss';
import { decodeShareUrl, getDecodedLoadout } from './loadout-import';

const placeHolder = `https://d2l.gg/bwipb2a/, https://app.destinyitemmanager.com/loadouts?loadout=...`;

export default function LoadoutImportSheet({
  currentStoreId,
  onClose,
}: {
  currentStoreId: string;
  onClose: () => void;
}) {
  const [shareUrl, setShareUrl] = useState<string>('');
  const [state, setState] = useState<'ok' | 'fetching' | string>('ok');
  const isPhonePortrait = useIsPhonePortrait();
  // On iOS at least, focusing the keyboard pushes the content off the screen
  const nativeAutoFocus = !isPhonePortrait && !isiOSBrowser();

  useEffect(() => {
    if (!shareUrl) {
      setState('ok');
      return;
    }
    const decodedUrl = decodeShareUrl(shareUrl);
    if (!decodedUrl) {
      setState(t('Loadouts.Import.BadURL'));
      return;
    }
    setState('fetching');
    let canceled = false;
    (async () => {
      try {
        const loadout = await getDecodedLoadout(decodedUrl);
        if (!canceled) {
          setState('ok');
          editLoadout(loadout, currentStoreId, { fromExternal: true });
          onClose();
        }
      } catch (e) {
        if (!canceled) {
          setState(`${t('Loadouts.Import.Error')} ${errorMessage(e)}`);
        }
      }
    })();
    return () => {
      canceled = true;
    };
  }, [currentStoreId, onClose, shareUrl]);

  return (
    <Sheet
      onClose={onClose}
      header={
        <>
          <h1>{t('Loadouts.ImportLoadout')}</h1>
          <UserGuideLink topic="Share-Loadouts" />
        </>
      }
      sheetClassName={styles.sheet}
    >
      <div className={styles.body}>
        <span>{t('Loadouts.Import.PasteHere')}</span>
        <div className={styles.fields}>
          <input
            value={shareUrl}
            onChange={(e) => setShareUrl(e.target.value)}
            placeholder={placeHolder}
            autoFocus={nativeAutoFocus}
          />
          {state !== 'ok' &&
            (state === 'fetching' ? (
              <span>
                <AppIcon icon={refreshIcon} spinning={true} />
              </span>
            ) : state.includes('404') ? (
              t('Loadouts.Import.Error404')
            ) : (
              state
            ))}
        </div>
      </div>
    </Sheet>
  );
}

