const categoryStyles: LookupTable<D2BucketCategory, string> = {
  Weapons: styles.categoryWeapons,
  Armor: styles.categoryArmor,
  General: styles.categoryGeneral,
};

export default function LoadoutItemCategorySection({
  category,
  subclass,
  store,
  items,
  allMods,
  modsByBucket,
  loadout,
  hideOptimizeArmor,
}: {
  category: D2BucketCategory;
  subclass?: ResolvedLoadoutItem;
  store: DimStore;
  items?: ResolvedLoadoutItem[];
  allMods: PluggableInventoryItemDefinition[];
  modsByBucket: {
    [bucketHash: number]: number[];
  };
  loadout: Loadout;
  hideOptimizeArmor?: boolean;
}) {
  const defs = useD2Definitions()!;
  const buckets = useSelector(bucketsSelector)!;
  const analysis = useAnalyzeLoadout(loadout, store, /* active */ !hideOptimizeArmor);
  const itemsByBucket = Map.groupBy(items ?? [], (li) => li.item.bucket.hash);
  const isPhonePortrait = useIsPhonePortrait();
  const bucketOrder =
    category === 'Weapons' || category === 'Armor'
      ? buckets.byCategory[category]
      : Array.from(itemsByBucket.keys(), (bucketHash) => buckets.byHash[bucketHash]).sort(
          compareBy((bucket) =>
            buckets.byCategory[category].findIndex((b) => b.hash === bucket.hash),
          ),
        );
  const equippedItems =
    items?.filter((li) => li.loadoutItem.equip && !li.missing).map((li) => li.item) ?? [];

  const isArmor = category === 'Armor';
  const hasFashion = isArmor && !isEmpty(modsByBucket);

  const [optimizeLoadout, constraints]: [Loadout, ResolvedStatConstraint[] | undefined] =
    useMemo(() => {
      if (
        analysis?.result.armorResults?.tag === 'done' &&
        analysis.result.armorResults.betterStatsAvailable
      ) {
        return [
          clearBucketCategory(
            defs,
            'Armor',
          )(setLoadoutParameters(analysis.result.armorResults.loadoutParameters)(loadout)),
          analysis.result.armorResults.strictUpgradeStatConstraints,
        ];
      } else {
        return [loadout, undefined];
      }
    }, [defs, analysis?.result.armorResults, loadout]);

  if (isPhonePortrait && !items && !hasFashion) {
    return null;
  }

  return (
    <div key={category} className={clsx(styles.itemCategory, categoryStyles[category])}>
      {items || hasFashion ? (
        <div className={styles.itemsInCategory}>
          {bucketOrder.map((bucket) => (
            <ItemBucket
              key={bucket.hash}
              storeId={store.id}
              bucketHash={bucket.hash}
              items={itemsByBucket.get(bucket.hash) ?? emptyArray()}
              modsForBucket={modsByBucket[bucket.hash] ?? emptyArray()}
            />
          ))}
        </div>
      ) : (
        <>
          <div className={clsx(styles.placeholder, `category-${category}`)}>
            {t(`Bucket.${category}`, { metadata: { keys: 'buckets' } })}
          </div>
        </>
      )}
      {items && isArmor && (
        <>
          {equippedItems.length === 5 && (
            <div className="stat-bars">
              <LoadoutCharacterStats
                loadout={loadout}
                subclass={subclass}
                allMods={allMods}
                items={items}
              />
            </div>
          )}
          {loadout.parameters && <LoadoutParametersDisplay params={loadout.parameters} />}
          {!hideOptimizeArmor && (
            <OptimizerButton
              loadout={optimizeLoadout}
              storeId={store.id}
              missingArmor={armorItemsMissing(items)}
              strictUpgradeStatConstraints={constraints}
            />
          )}
        </>
      )}
    </div>
  );
}

function ItemBucket({
  bucketHash,
  storeId,
  items,
  modsForBucket,
}: {
  bucketHash: number;
  storeId?: string;
  items: ResolvedLoadoutItem[];
  modsForBucket: number[];
}) {
  const [equipped, unequipped] = partition(items, (li) => li.loadoutItem.equip);

  const showFashion = ArmorBucketHashes.includes(bucketHash);

  // TODO: should these be draggable? so you can drag them into other loadouts?

  return (
    <div className={styles.itemBucket}>
      {[equipped, unequipped].map((items, index) =>
        items.length > 0 ? (
          <div
            className={clsx(styles.items, index === 0 ? styles.equipped : styles.unequipped)}
            key={index}
          >
            {items.map(({ item, loadoutItem, missing }) => (
              <DraggableInventoryItem item={item} key={item.id}>
                <ItemPopupTrigger
                  item={item}
                  extraData={{ socketOverrides: loadoutItem.socketOverrides }}
                >
                  {(ref, onClick) => (
                    <div
                      className={clsx({
                        [styles.missingItem]: missing,
                      })}
                    >
                      <ConnectedInventoryItem item={item} ref={ref} onClick={onClick} />
                    </div>
                  )}
                </ItemPopupTrigger>
              </DraggableInventoryItem>
            ))}
            {index === 0 && showFashion && (
              <FashionMods modsForBucket={modsForBucket} storeId={storeId} />
            )}
          </div>
        ) : (
          index === 0 && (
            <div
              className={clsx(styles.items, index === 0 ? styles.equipped : styles.unequipped)}
              key={index}
            >
              <BucketPlaceholder bucketHash={bucketHash} />
              {/* TODO: show empty placeholder for bucket type? */}
              {showFashion && <FashionMods modsForBucket={modsForBucket} storeId={storeId} />}
            </div>
          )
        ),
      )}
    </div>
  );
}

import { PressTip } from 'app/d2l-ui/PressTip';
import { useDynamicStringReplacer } from 'app/d2l-ui/destiny-symbols/RichDestinyText';
import { t } from 'app/i18next-t';
import { DimItem, PluggableInventoryItemDefinition } from 'app/inventory/item-types';
import { isPluggableItem } from 'app/inventory/store/sockets';
import { useD2Definitions } from 'app/manifest/selectors';
import { modTypeTagByPlugCategoryHash } from 'app/search/specialty-modslots';
import { AppIcon, shoppingCart } from 'app/shell/icons';
import { isEventArmorRerollSocket } from 'app/utils/socket-utils';
import { DestinyInventoryItemDefinition } from 'bungie-api-ts/destiny2';
import clsx from 'clsx';
import { PlugCategoryHashes } from 'data/d2/generated-enums';
import { pickPlugPositions } from '../mod-assignment-utils';
import PlugDef from './PlugDef';
import styles from './Sockets.m.scss';

const undesirablePlugs = [
  PlugCategoryHashes.ArmorSkinsEmpty,
  PlugCategoryHashes.Shader,
  PlugCategoryHashes.V460PlugsArmorMasterworksStatResistance1,
  PlugCategoryHashes.V460PlugsArmorMasterworksStatResistance2,
  PlugCategoryHashes.V460PlugsArmorMasterworksStatResistance3,
  PlugCategoryHashes.V460PlugsArmorMasterworksStatResistance4,
  PlugCategoryHashes.EnhancementsArtificeExotic, // the cost socket you pay for by"plug"ging it in-game, to do an artifice upgrade
];

/**
 * Show sockets (mod slots) for a loadout armor item with the specified locked mods slotted into it.
 */
function Sockets({
  item,
  lockedMods,
  size,
  onSocketClick,
  automaticallyPickedMods,
}: {
  item: DimItem;
  lockedMods?: PluggableInventoryItemDefinition[];
  automaticallyPickedMods?: number[];
  size?: 'small';
  onSocketClick?: (
    plugDef: PluggableInventoryItemDefinition,
    /** An allow-list of plug category hashes that can be inserted into this socket */
    // TODO: why not just pass the socketType hash or socket definition?
    plugCategoryHashWhitelist: number[],
  ) => void;
}) {
  const defs = useD2Definitions()!;
  if (!item.sockets) {
    return null;
  }

  // A list of mods to show. If we aren't showing a plug for a socket we show the empty plug.
  const modsAndWhitelist: {
    plugDef: PluggableInventoryItemDefinition;
    whitelist: number[];
    automaticallyPicked: boolean;
  }[] = [];
  const modsToUse = lockedMods ? [...lockedMods] : [];

  const assignments = pickPlugPositions(defs, item, modsToUse);
  const autoMods = automaticallyPickedMods?.slice();

  for (const socket of item.sockets?.allSockets || []) {
    const socketType = defs.SocketType.get(socket.socketDefinition.socketTypeHash);
    let toSave: DestinyInventoryItemDefinition | undefined = assignments.find(
      (a) => a.socketIndex === socket.socketIndex,
    )?.mod;

    const modIdx = (toSave && autoMods?.findIndex((m) => m === toSave!.hash)) ?? -1;
    const automaticallyPicked = modIdx !== -1;
    if (automaticallyPicked) {
      autoMods!.splice(modIdx, 1);
    }

    if (!toSave) {
      const plugHash =
        socket.plugged &&
        (socket.emptyPlugItemHash ||
          (socket.plugged.plugDef.plug.plugCategoryHash === PlugCategoryHashes.Intrinsics &&
            socket.plugged.plugDef.hash));
      if (plugHash) {
        toSave = defs.InventoryItem.get(plugHash);
      }
    }

    if (
      toSave &&
      isPluggableItem(toSave) &&
      !undesirablePlugs.includes(toSave.plug.plugCategoryHash) &&
      !isEventArmorRerollSocket(socket) &&
      // account for plugs that look exotic-ish but are nothings
      // but always include specialty mod slots, Vow mods don't have
      // an itemTypeDisplayName https://github.com/Bungie-net/api/issues/1620
      (toSave.itemTypeDisplayName ||
        modTypeTagByPlugCategoryHash[toSave.plug.plugCategoryHash as PlugCategoryHashes]) &&
      // either it's some other kind of mod-slot, give it a pass, or
      (socket.plugged?.plugDef.plug.plugCategoryHash !== PlugCategoryHashes.EnhancementsArtifice ||
        // if it IS an artifice slot, then we render it
        // if it's already paid for (visibleInGame)
        socket.visibleInGame ||
        // or if LO has placed a mod in it anyway, due to an upgrades assumption
        toSave.hash !== socket.emptyPlugItemHash)
    ) {
      modsAndWhitelist.push({
        plugDef: toSave,
        whitelist: socketType.plugWhitelist.map((plug) => plug.categoryHash),
        automaticallyPicked,
      });
    }
  }

  return (
    <div className={clsx(styles.lockedItems, { [styles.small]: size === 'small' })}>
      {modsAndWhitelist.map(({ plugDef, whitelist, automaticallyPicked }, index) => (
        <PlugDef
          className={clsx({ [styles.automaticallyPicked]: automaticallyPicked })}
          key={index}
          plug={plugDef}
          onClick={onSocketClick ? () => onSocketClick(plugDef, whitelist) : undefined}
          automaticallyPicked={automaticallyPicked}
          forClassType={item.classType}
        />
      ))}
      {item.vendor && <VendorItemPlug item={item} />}
    </div>
  );
}

function VendorItemPlug({ item }: { item: DimItem }) {
  const defs = useD2Definitions()!;
  const replacer = useDynamicStringReplacer(item.owner);
  const vendorDef = defs.Vendor.get(item.vendor!.vendorHash);
  return (
    <PressTip
      elementType="span"
      tooltip={() => {
        const vendorName = replacer(vendorDef?.displayProperties?.name) || '--';
        return (
          <>
            {t('Compare.IsVendorItem', { vendorName })} {t('LoadoutBuilder.ExcludeVendors')}
          </>
        );
      }}
    >
      <div className={clsx('item', styles.vendorItem)}>
        <AppIcon icon={shoppingCart} />
      </div>
    </PressTip>
  );
}

