export const armorTopLevelCatHashes: ItemCategoryHashes[] = [
  ItemCategoryHashes.Hunter,
  ItemCategoryHashes.Titan,
  ItemCategoryHashes.Warlock,
];

/**
 * This component offers a means for narrowing down your selection to a single item type
 * (hunter helmets, hand cannons, etc.) for the Organizer table.
 */
export default function ItemTypeSelector({
  selectionTree,
  selection,
  onSelection,
}: {
  selectionTree: ItemCategoryTreeNode;
  selection: ItemCategoryTreeNode[];
  onSelection: (selection: ItemCategoryTreeNode[]) => void;
}) {
  const defs = useDefinitions()!;
  const filteredItems = useSelector(filteredItemsSelector);
  selection = selection.length ? selection : [selectionTree];

  const handleSelection = (depth: number, subCategory: ItemCategoryTreeNode) =>
    onSelection([...selection.slice(0, depth + 1), subCategory]);

  return (
    <div className={styles.selector}>
      {selection.map((currentSelection, depth) => {
        const upstreamCategories: number[] = [];
        for (let i = 1; i < depth + 1; i++) {
          selection[i].itemCategoryHash && upstreamCategories.push(selection[i].itemCategoryHash);
        }
        return (
          currentSelection.subCategories && (
            <div key={depth} className={styles.level}>
              {currentSelection.subCategories?.map((subCategory) => {
                const categoryHashList = [...upstreamCategories, subCategory.itemCategoryHash];

                // a top level class-specific category implies armor
                if (armorTopLevelCatHashes.some((h) => categoryHashList.includes(h))) {
                  categoryHashList.push(ItemCategoryHashes.Armor);
                }

                const itemCategory = defs.ItemCategory.get(Math.abs(subCategory.itemCategoryHash));
                return (
                  <label
                    key={subCategory.itemCategoryHash}
                    className={clsx(styles.button, {
                      [styles.checked]: selection[depth + 1] === subCategory,
                    })}
                  >
                    <input
                      type="radio"
                      name={subCategory.id}
                      value={subCategory.id}
                      checked={selection[depth + 1] === subCategory}
                      readOnly={true}
                      onClick={(_e) => handleSelection(depth, subCategory)}
                    />
                    {subCategory.itemCategoryHash !== 0 && (
                      <BucketIcon itemCategoryHash={subCategory.itemCategoryHash} />
                    )}
                    {'displayProperties' in itemCategory
                      ? itemCategory.displayProperties.name
                      : itemCategory.title}{' '}
                    <span className={styles.buttonItemCount}>
                      (
                      {count(
                        filteredItems,
                        (i) =>
                          i.comparable &&
                          categoryHashList.every((h) => i.itemCategoryHashes.includes(h)),
                      )}
                      )
                    </span>
                  </label>
                );
              })}
            </div>
          )
        );
      })}
    </div>
  );
}

import { useDynamicStringReplacer } from 'app/d2l-ui/destiny-symbols/RichDestinyText';
import { t, tl } from 'app/i18next-t';
import { useD2Definitions } from 'app/manifest/selectors';
import { unadvertisedResettableVendors } from 'app/search/d2-known-values';
import { sumBy } from 'app/utils/collections';
import { DestinyProgression } from 'bungie-api-ts/destiny2';
import clsx from 'clsx';
import BungieImage, { bungieNetPath } from '../d2l-ui/BungieImage';
import { ObjectiveCheckbox, ObjectiveRow } from './Objective';
import styles from './ReputationRank.m.scss';

/**
 * displays a single reputation rank for the account
 */
export function ReputationRank({
  progress,
  streak,
  isProgressRanks,
}: {
  progress: DestinyProgression;
  streak?: DestinyProgression;
  isProgressRanks?: boolean;
}) {
  const defs = useD2Definitions()!;
  const replacer = useDynamicStringReplacer();
  const progressionDef = defs.Progression.get(progress.progressionHash);

  const step = progressionDef.steps[Math.min(progress.level, progressionDef.steps.length - 1)];

  const canReset =
    typeof progress.currentResetCount === 'number' ||
    unadvertisedResettableVendors.includes(progress.progressionHash);
  const resetLabel = canReset ? tl('Progress.PercentPrestige') : tl('Progress.PercentMax');

  const rankTotal = sumBy(progressionDef.steps, (cur) => cur.progressTotal);
  const rankPercent = Math.floor((progress.currentProgress / rankTotal) * 100);

  const streakCheckboxes =
    streak && Array<boolean>(5).fill(true).fill(false, streak.currentProgress);

  // language-agnostic css class name to identify which rank type we are in
  const factionClass = `faction-${progress.progressionHash}`;

  return (
    <div
      className={clsx(factionClass, styles.activityRank, { [styles.gridLayout]: isProgressRanks })}
      title={replacer(progressionDef.displayProperties.description)}
    >
      <ReputationRankIcon progress={progress} />
      <div className={styles.factionInfo}>
        <div className={styles.factionLevel}>
          {t('Progress.Rank', {
            name: progressionDef.displayProperties.name,
            rank: progress.level + 1,
          })}
        </div>
        <div className={styles.factionName}>{step.stepName}</div>
        <div className={styles.factionLevel}>
          {progressionDef.rankIcon && (
            <BungieImage className={styles.rankIcon} src={progressionDef.rankIcon} />
          )}
          {progress.currentProgress} ({progress.progressToNextLevel} / {progress.nextLevelAt})
        </div>
        {streakCheckboxes && (
          <ObjectiveRow className={styles.winStreak}>
            {streakCheckboxes.map((c, i) => (
              <ObjectiveCheckbox key={i} completed={c} />
            ))}
          </ObjectiveRow>
        )}
        <div className={clsx(styles.factionLevel, rankPercent === 100 && styles.max)}>
          {t(resetLabel, {
            pct: rankPercent,
          })}
        </div>
        {Boolean(progress.currentResetCount) && (
          <div className={styles.factionLevel}>
            {t('Progress.Resets', { count: progress.currentResetCount })}
          </div>
        )}
      </div>
    </div>
  );
}

function ReputationRankIcon({ progress }: { progress: DestinyProgression }) {
  const defs = useD2Definitions()!;

  const progressionDef = defs.Progression.get(progress.progressionHash);

  const step = progressionDef.steps[Math.min(progress.level, progressionDef.steps.length - 1)];

  const canReset = progressionDef.steps.length === progress.levelCap;
  const rankTotal = sumBy(progressionDef.steps, (step) => step.progressTotal);

  const circumference = 2 * 22 * Math.PI;
  const circumference2 = 2 * 25 * Math.PI;

  const strokeColor = progressionDef.color
    ? `rgb(${progressionDef.color.red}, ${progressionDef.color.green},${progressionDef.color.blue})`
    : 'white';

  return (
    <div className={styles.crucibleRankIcon}>
      <svg viewBox="0 0 54 54">
        <circle r="27" cx="27" cy="27" fill="#555" />
        <circle r="21" cx="27" cy="27" fill="#222" />
        {progress.progressToNextLevel > 0 && (
          <circle
            r="22.5"
            cx="-27"
            cy="27"
            transform="rotate(-90)"
            className={styles.crucibleRankProgress}
            strokeWidth="3"
            strokeDasharray={`${
              (circumference * progress.progressToNextLevel) / progress.nextLevelAt
            } ${circumference}`}
            stroke={strokeColor}
          />
        )}
        {canReset && progress.currentProgress > 0 && (
          <circle
            r="25.5"
            cx="-27"
            cy="27"
            transform="rotate(-90)"
            className={styles.crucibleRankTotalProgress}
            strokeWidth="3"
            strokeDasharray={`${
              (circumference2 * progress.currentProgress) / rankTotal
            } ${circumference2}`}
          />
        )}
        <image xlinkHref={bungieNetPath(step.icon)} width="40" height="40" x="7" y="7" />
      </svg>
    </div>
  );
}

import { DimItem } from 'app/inventory/item-types';
import { RollAppraiserUtils, WeaponRankingData } from 'app/utils/rollAppraiserUtils';
import { getSocketsByIndexes, getWeaponSockets } from 'app/utils/socket-utils';
import { useEffect, useState } from 'react';
import { getRollAppraiserUtils, getRollAppraiserUtilsSync } from './rollAppraiserService';

/**
 * Hook to get roll appraiser utils
 */
export function useRollAppraiserUtils() {
  const [utils, setUtils] = useState<RollAppraiserUtils | null>(() => getRollAppraiserUtilsSync());
  const [loading, setLoading] = useState(!utils);
  const [error, setError] = useState<Error | null>(null);

  useEffect(() => {
    if (!utils) {
      getRollAppraiserUtils()
        .then((loadedUtils) => {
          setUtils(loadedUtils);
          setLoading(false);
        })
        .catch((err: any) => {
          setError(err instanceof Error ? err : new Error(String(err)));
          setLoading(false);
        });
    }
  }, [utils]);

  return { utils, loading, error };
}

/**
 * Hook to get weapon ranking data for a specific item
 */
export function useWeaponRankingData(item: DimItem | null): WeaponRankingData | null {
  const { utils } = useRollAppraiserUtils();
  const [rankingData, setRankingData] = useState<WeaponRankingData | null>(null);

  useEffect(() => {
    if (!utils || !item || item.destinyVersion !== 2 || !item.bucket.inWeapons) {
      setRankingData(null);
      return;
    }

    // Get perk hashes from the weapon's sockets
    const perkHashes: number[] = [];
    const traitPerkHashes: number[] = [];
    let masterworkHash: number | undefined = undefined;

    if (item.sockets) {
      // Use getWeaponSockets to properly identify weapon perks
      const weaponSockets = getWeaponSockets(item, { excludeEmptySockets: false });

      if (weaponSockets?.perks) {
        // Get all perk sockets from the perks category
        const perkSockets = getSocketsByIndexes(item.sockets, weaponSockets.perks.socketIndexes);

        // Filter to only plugged perks
        const pluggedPerks = perkSockets.filter((s) => s.plugged && s.isPerk);

        for (let index = 0; index < pluggedPerks.length; index++) {
          const socket = pluggedPerks[index];
          if (socket.plugged) {
            perkHashes.push(socket.plugged.plugDef.hash);
            // Columns 3 and 4 (indexes 2 and 3) are typically trait perks
            if (index === 2 || index === 3) {
              traitPerkHashes.push(socket.plugged.plugDef.hash);
            }
          }
        }
      }

      // Check for masterwork
      for (const socket of item.sockets.allSockets) {
        if (
          socket.plugged &&
          socket.plugged.plugDef.plug?.plugCategoryIdentifier === 'intrinsics.stat'
        ) {
          masterworkHash = socket.plugged.plugDef.hash;
        }
      }
    }

    const weaponData = utils.getWeaponData(
      item.hash.toString(),
      perkHashes.map(String),
      masterworkHash?.toString(),
    );

    // Override trait combo ranking if we have specific trait perks identified
    if (traitPerkHashes.length === 2 && weaponData) {
      const comboRank = utils.getTraitComboRank(
        item.hash.toString(),
        traitPerkHashes[0].toString(),
        traitPerkHashes[1].toString(),
      );
      // Only log in debug mode
      if (!comboRank && $D2L_FLAVOR === 'dev') {
        console.log('No combo rank found for weapon:', item.name, {
          itemHash: item.hash,
          perk4: traitPerkHashes[0],
          perk5: traitPerkHashes[1],
          allPerks: perkHashes,
          hasTraitData: Boolean(utils.hasDataForWeapon(item.hash.toString())),
        });
      } else if (comboRank && $D2L_FLAVOR === 'dev') {
        console.log('Found combo rank for weapon:', item.name, {
          rank: comboRank.rank,
          perk4: comboRank.perk4Hash,
          perk5: comboRank.perk5Hash,
          count: comboRank.count,
        });
      }
      weaponData.traitComboRanking = comboRank;
    }

    setRankingData(weaponData);
  }, [utils, item]);

  return rankingData;
}

/**
 * Hook to check if a weapon has any ranking data
 */
export function useHasRankingData(item: DimItem | null): boolean {
  const { utils } = useRollAppraiserUtils();

  if (!utils || !item || item.destinyVersion !== 2 || !item.bucket.inWeapons) {
    return false;
  }

  return utils.hasDataForWeapon(item.hash.toString());
}

