const MIN_SPIN = 1000; // 1 second

export default function RefreshButton({ className }: { className?: string }) {
  const [disabled, setDisabled] = useState(false);
  const autoRefresh = useSelector(autoRefreshEnabledSelector);

  const handleChanges = useCallback(
    () => setDisabled(!navigator.onLine || document.hidden || isDragging$.getCurrentValue()),
    [],
  );
  const active = useSubscription(loadingTracker.active$);

  // Always show the spinner for at least MIN_SPIN milliseconds
  const [spin, setSpin] = useState(active ? Date.now() : 0);
  useEffect(() => {
    if (active && spin === 0) {
      setSpin(Date.now());
    } else if (!active && spin !== 0) {
      const elapsed = Date.now() - spin;
      const remainingTime = Math.max(0, MIN_SPIN - elapsed);
      if (remainingTime > 0) {
        const timer = window.setTimeout(() => {
          setSpin(0);
        }, remainingTime);
        return () => window.clearTimeout(timer);
      } else {
        setSpin(0);
      }
    }
  }, [active, spin]);

  useEventBusListener(isDragging$, handleChanges);

  useEffect(() => {
    document.addEventListener('visibilitychange', handleChanges);
    document.addEventListener('online', handleChanges);

    return () => {
      document.removeEventListener('visibilitychange', handleChanges);
      document.removeEventListener('online', handleChanges);
    };
  }, [handleChanges]);

  useHotkey('r', t('Hotkey.RefreshInventory'), refresh);

  return (
    <PressTip tooltip={<RefreshButtonTooltip autoRefresh={autoRefresh} />}>
      <button
        type="button"
        className={clsx(styles.refreshButton, className, { disabled })}
        onClick={refresh}
        title={t('Header.Refresh') + (autoRefresh ? `\n${t('Header.AutoRefresh')}` : '')}
        aria-keyshortcuts="R"
      >
        <AppIcon icon={refreshIcon} spinning={spin !== 0} />
        {autoRefresh && <div className={styles.userIsPlaying} />}
      </button>
    </PressTip>
  );
}

function useProfileAge() {
  const profileMintedDate = useSelector(profileMintedSelector);
  const [_tickState, setTickState] = useState(0);
  useEffect(() => {
    const interval = setInterval(() => setTickState((t) => t + 1), 1000);
    return () => clearInterval(interval);
  }, []);

  return profileAge(profileMintedDate);
}

function profileAge(profileMintedDate: Date) {
  return profileMintedDate.getTime() === 0 ? undefined : Date.now() - profileMintedDate.getTime();
}

function RefreshButtonTooltip({ autoRefresh }: { autoRefresh: boolean }) {
  const profileAge = useProfileAge();
  const profileError = useSelector(profileErrorSelector);
  const isManifestError = profileError?.name === 'ManifestError';
  const destinyVersion = useSelector(destinyVersionSelector);

  useTooltipCustomization({
    className: profileError ? styles.errorTooltip : null,
  });

  return (
    <>
      {profileError ? (
        <div className={styles.errorDetails}>
          <ErrorPanel
            title={
              isManifestError
                ? t('Accounts.ErrorLoadManifest')
                : t('Accounts.ErrorLoadInventory', { version: destinyVersion })
            }
            error={profileError}
            frameless
          />
        </div>
      ) : (
        <>
          <b>{t('Header.Refresh') + (autoRefresh ? `\n${t('Header.AutoRefresh')}` : '')}</b>
          {profileAge !== undefined && (
            <div>{t('Header.ProfileAge', { age: i15dDurationFromMsWithSeconds(profileAge) })}</div>
          )}
        </>
      )}
    </>
  );
}

import { ClarityCharacterStats } from 'app/clarity/descriptions/character-stats';
import { clarityCharacterStatsSelector } from 'app/clarity/selectors';
import BungieImage from 'app/d2l-ui/BungieImage';
import { Tooltip } from 'app/d2l-ui/PressTip';
import { t } from 'app/i18next-t';
import { useD2Definitions } from 'app/manifest/selectors';
import { timerDurationFromMsWithDecimal } from 'app/utils/time';
import { DestinyInventoryItemDefinition } from 'bungie-api-ts/destiny2';
import clsx from 'clsx';
import { StatHashes } from 'data/d2/generated-enums';
import { JSX } from 'react';
import { useSelector } from 'react-redux';
import styles from './ClarityCharacterStat.m.scss';

const statHashToClarityName: { [key: number]: keyof ClarityCharacterStats } = {
  [StatHashes.Weapons]: 'Mobility',
  [StatHashes.Health]: 'Resilience',
  [StatHashes.Class]: 'Recovery',
  [StatHashes.Super]: 'Intellect',
  [StatHashes.Grenade]: 'Discipline',
  [StatHashes.Melee]: 'Strength',
};

/**
 * Use Clarity's database of cooldown info to show extended, accurate cooldown info for equipped subclass mods and exotics.
 */
export default function ClarityCharacterStat({
  statHash,
  tier,
  equippedHashes,
}: {
  statHash: number;
  tier: number;
  /**
   * Hashes of equipped/selected items and subclass plugs for this character or loadout. Can be limited to
   * exotic armor + subclass plugs - make sure to include default-selected subclass plugs. This is used to
   * determine which cooldowns to show and also to calculate any overrides from aspects or exotics.
   */
  equippedHashes: Set<number>;
}) {
  const defs = useD2Definitions()!;
  const clarityCharacterStats = useSelector(clarityCharacterStatsSelector);
  const clarityStatData = clarityCharacterStats?.[statHashToClarityName[statHash]];

  if (!clarityStatData) {
    return null;
  }

  const abilityCooldowns: {
    cooldowns: number[];
    item: DestinyInventoryItemDefinition;
    overrides: DestinyInventoryItemDefinition[];
  }[] = [];

  const applicableOverrides = clarityStatData.Overrides.filter((o) => equippedHashes.has(o.Hash));
  const abilitiesList =
    'SuperAbilities' in clarityStatData
      ? clarityStatData.SuperAbilities
      : clarityStatData.Abilities;
  for (const a of abilitiesList) {
    if (!equippedHashes.has(a.Hash)) {
      continue;
    }
    let cooldowns = a.Cooldowns;
    const item = defs.InventoryItem.get(a.Hash);

    const overrides = [];

    // Apply cooldown overrides based on equipped items.
    for (const o of applicableOverrides) {
      const abilityIndex = (() => {
        const abilityIndex = o.Requirements.indexOf(a.Hash);
        if (abilityIndex !== -1) {
          return abilityIndex;
        }
        const subclassIdx = o.Requirements.findIndex((r) => r < 0 && equippedHashes.has(-r));
        if (subclassIdx !== -1) {
          return subclassIdx;
        }
        if (o.Requirements.length === 1 && o.Requirements[0] === 0) {
          return 0;
        }
        return -1;
      })();
      if (abilityIndex !== -1) {
        if (o.CooldownOverride?.some((v) => v > 0)) {
          cooldowns = o.CooldownOverride;
        }
        const scalar = o.Scalar?.[abilityIndex];
        if (scalar) {
          cooldowns = cooldowns.map((v) => scalar * v);
        }
        overrides.push(defs.InventoryItem.get(o.Hash));
      }
    }

    cooldowns = cooldowns.map((c) => Math.round(c));
    abilityCooldowns.push({ cooldowns, item, overrides });
  }

  // Cooldowns that are not about some specific ability
  const intrinsicCooldowns: JSX.Element[] = [];
  if ('TotalRegenTime' in clarityStatData) {
    intrinsicCooldowns.push(
      <StatTableRow
        key="TimeToFullHP"
        name={t('Stats.TimeToFullHP')}
        cooldowns={clarityStatData.TotalRegenTime.Array}
        tier={tier}
        unit="s"
      />,
    );
  } else if ('WalkSpeed' in clarityStatData) {
    intrinsicCooldowns.push(
      <StatTableRow
        key="WalkingSpeed"
        name={t('Stats.WalkingSpeed')}
        cooldowns={clarityStatData.WalkSpeed.Array}
        tier={tier}
        unit={t('Stats.MetersPerSecond')}
      />,
      <StatTableRow
        key="StrafingSpeed"
        name={t('Stats.StrafingSpeed')}
        cooldowns={clarityStatData.StrafeSpeed.Array}
        tier={tier}
        unit={t('Stats.MetersPerSecond')}
      />,
      <StatTableRow
        key="CrouchingSpeed"
        name={t('Stats.CrouchingSpeed')}
        cooldowns={clarityStatData.CrouchSpeed.Array}
        tier={tier}
        unit={t('Stats.MetersPerSecond')}
      />,
    );
  } else if ('ShieldHP' in clarityStatData) {
    intrinsicCooldowns.push(
      <StatTableRow
        key="ShieldHP"
        // t('Stats.TotalHP')
        // keep this around maybe?
        name={t('Stats.ShieldHP')}
        cooldowns={clarityStatData.ShieldHP.Array}
        tier={tier}
        unit={t('Stats.HP')}
      />,
      <StatTableRow
        key="DamageResistance"
        name={t('Stats.DamageResistance')}
        cooldowns={clarityStatData.PvEDamageResistance.Array}
        tier={tier}
        unit={t('Stats.Percentage')}
      />,
      <StatTableRow
        key="FlinchResistance"
        name={t('Stats.FlinchResistance')}
        cooldowns={clarityStatData.FlinchResistance.Array}
        tier={tier}
        unit={t('Stats.Percentage')}
      />,
    );
  }

  if (intrinsicCooldowns.length + abilityCooldowns.length === 0) {
    return null;
  }

  return (
    <Tooltip.Section className={styles.communityInsightSection}>
      <table>
        <thead>
          <tr>
            <th />
            {tier - 1 >= 0 && (
              <>
                <th colSpan={2} className={styles.value}>
                  {t('LoadoutBuilder.TierNumber', { tier: tier - 1 })}
                </th>
              </>
            )}
            <th colSpan={2} className={clsx(styles.value, styles.currentColumn)}>
              {t('LoadoutBuilder.TierNumber', { tier })}
            </th>
            {tier + 1 <= 10 && (
              <>
                <th colSpan={2} className={styles.value}>
                  {t('LoadoutBuilder.TierNumber', { tier: tier + 1 })}
                </th>
              </>
            )}
          </tr>
        </thead>
        <tbody>
          {abilityCooldowns
            .sort((a, b) => a.cooldowns[tier] - b.cooldowns[tier])
            .map(({ cooldowns, item, overrides }) => (
              <StatTableRow
                key={item.hash}
                name={item.displayProperties.name}
                icon={item.displayProperties.icon}
                cooldowns={cooldowns}
                tier={tier}
                overrides={overrides}
                unit="s"
              />
            ))}
          {intrinsicCooldowns}
        </tbody>
      </table>
    </Tooltip.Section>
  );
}

function StatTableRow({
  name,
  icon,
  cooldowns,
  tier,
  unit,
  overrides = [],
}: {
  name: string;
  icon?: string;
  unit: string;
  tier: number;
  cooldowns: number[];
  overrides?: DestinyInventoryItemDefinition[];
}) {
  const unitEl = <td className={styles.unit}>{unit}</td>;
  const seconds = unit === 's';

  const formatValue = (val: number) => {
    if (seconds) {
      return timerDurationFromMsWithDecimal(val * 1000);
    }
    return val.toLocaleString();
  };

  const colspan = seconds ? 2 : 1;

  return (
    <tr>
      <th>
        <span>
          {icon && <BungieImage src={icon} height={16} width={16} />}
          {name}
        </span>
        {overrides.map((o) => (
          <span key={o.hash} className={styles.override}>
            {'+ '}
            {o.displayProperties.icon && (
              <BungieImage src={o.displayProperties.icon} height={12} width={12} />
            )}
            {o.displayProperties.name}
          </span>
        ))}
      </th>
      {tier - 1 >= 0 && (
        <>
          <td className={styles.value} colSpan={colspan}>
            {formatValue(cooldowns[tier - 1])}
          </td>
          {!seconds && unitEl}
        </>
      )}
      <td className={clsx(styles.value, styles.currentColumn)} colSpan={colspan}>
        {formatValue(cooldowns[tier])}
      </td>
      {!seconds && <td className={clsx(styles.unit, styles.currentColumn)}>{unit}</td>}
      {tier + 1 <= 10 && (
        <>
          <td className={styles.value} colSpan={colspan}>
            {formatValue(cooldowns[tier + 1])}
          </td>
          {!seconds && unitEl}
        </>
      )}
    </tr>
  );
}

import BungieImage from 'app/d2l-ui/BungieImage';
import { PressTip } from 'app/d2l-ui/PressTip';
import { D1StatHashes } from 'app/destiny1/d1-manifest-types';
import { t } from 'app/i18next-t';
import type { DimCharacterStat, DimStore } from 'app/inventory/store-types';
import { findItemsByBucket } from 'app/inventory/stores-helpers';
import { percent } from 'app/shell/formatters';
import clsx from 'clsx';
import { BucketHashes } from 'data/d2/generated-enums';
import styles from './D1CharacterStats.m.scss';

export function D1StoreCharacterStats({ store }: { store: DimStore }) {
  const subclass = findItemsByBucket(store, BucketHashes.Subclass).find((i) => i.equipped);
  return <D1CharacterStats stats={store.stats} subclassHash={subclass?.hash} />;
}

export function D1CharacterStats({
  stats,
  subclassHash,
}: {
  stats: DimStore['stats'];
  subclassHash?: number;
}) {
  const statList = Object.values(stats);
  const tooltips = statList.map((stat) => {
    const tier = Math.floor(Math.min(300, stat.value) / 60);
    const next = t('Stats.TierProgress', {
      context: tier === 5 ? 'Max' : '',
      metadata: { context: ['max'] },
      progress: tier === 5 ? stat.value : stat.value % 60,
      tier,
      nextTier: tier + 1,
      statName: stat.displayProperties.name,
    });

    const cooldown = subclassHash ? getAbilityCooldown(subclassHash, stat.hash, tier) : undefined;
    if (cooldown) {
      switch (stat.hash) {
        case D1StatHashes.Intellect:
          return next + t('Cooldown.Super', { cooldown });
        case D1StatHashes.Discipline:
          return next + t('Cooldown.Grenade', { cooldown });
        case D1StatHashes.Strength:
          return next + t('Cooldown.Melee', { cooldown });
      }
    }
    return next;
  });

  return (
    <div className={styles.statBars}>
      {statList.map((stat, index) => (
        <PressTip key={stat.hash} tooltip={tooltips[index]}>
          <div className={styles.stat}>
            <BungieImage src={stat.displayProperties.icon} alt={stat.displayProperties.name} />
            {getD1CharacterStatTiers(stat).map((n, index) => (
              <div key={index} className={styles.bar}>
                <div
                  className={clsx(styles.progress, {
                    [styles.complete]: n / 60 === 1,
                  })}
                  style={{ width: percent(n / 60) }}
                />
              </div>
            ))}
          </div>
        </PressTip>
      ))}
    </div>
  );
}

function getD1CharacterStatTiers(stat: DimCharacterStat) {
  const tiers = new Array<number>(5);
  let remaining = stat.value;
  for (let t = 0; t < 5; t++) {
    remaining -= tiers[t] = remaining > 60 ? 60 : remaining;
  }
  return tiers;
}

// Cooldowns
const cooldownsSuperA = ['5:00', '4:46', '4:31', '4:15', '3:58', '3:40'];
const cooldownsSuperB = ['5:30', '5:14', '4:57', '4:39', '4:20', '4:00'];
const cooldownsGrenade = ['1:00', '0:55', '0:49', '0:42', '0:34', '0:25'];
const cooldownsMelee = ['1:10', '1:04', '0:57', '0:49', '0:40', '0:29'];

// following code is from https://github.com/DestinyTrialsReport
function getAbilityCooldown(subclass: number, statHash: D1StatHashes, tier: number) {
  switch (statHash) {
    case D1StatHashes.Intellect:
      switch (subclass) {
        case 2007186000: // Defender
        case 4143670656: // Nightstalker
        case 2455559914: // Striker
        case 3658182170: // Sunsinger
          return cooldownsSuperA[tier];
        default:
          return cooldownsSuperB[tier];
      }
    case D1StatHashes.Discipline:
      return cooldownsGrenade[tier];
    case D1StatHashes.Strength:
      switch (subclass) {
        case 4143670656: // Nightstalker
        case 1716862031: // Gunslinger
          return cooldownsMelee[tier];
        default:
          return cooldownsGrenade[tier];
      }
    default:
      return '';
  }
}

import { PressTip } from 'app/d2l-ui/PressTip';
import Sheet from 'app/d2l-ui/Sheet';
import { t } from 'app/i18next-t';
import { locateItem } from 'app/inventory/locate-item';
import { destiny2CoreSettingsSelector, useD2Definitions } from 'app/manifest/selectors';
import { filterFactorySelector } from 'app/search/items/item-search-filter';
import { AppIcon, faCheckCircle, refreshIcon } from 'app/shell/icons';
import { useThunkDispatch } from 'app/store/thunk-dispatch';
import { withCancel } from 'app/utils/cancel';
import { DestinyInventoryItemDefinition } from 'bungie-api-ts/destiny2';
import clsx from 'clsx';
import chestArmorItem from 'destiny-icons/armor_types/chest.svg';
import ghostIcon from 'destiny-icons/general/ghost.svg';
import handCannonIcon from 'destiny-icons/weapons/hand_cannon.svg';
import { produce } from 'immer';
import React, { useCallback, useEffect, useMemo, useReducer, useState } from 'react';
import { useSelector } from 'react-redux';
import { useSubscription } from 'use-subscription';
import { DefItemIcon } from '../inventory/ItemIcon';
import { allItemsSelector } from '../inventory/selectors';
import styles from './StripSockets.m.scss';
import { SocketKind, StripAction, collectSocketsToStrip, doStripSockets } from './strip-sockets';
import { stripSocketsQuery$ } from './strip-sockets-actions';

/**
 * Error, OK, or still in our worklist
 */
type SocketState = string | 'ok' | 'todo';

type State =
  | {
      tag: 'selecting';
    }
  | {
      tag: 'processing';
      cancel: () => void;
      cancelling: boolean;
      socketList: StripAction[];
      socketStates: SocketState[];
    }
  | {
      tag: 'done';
      socketList: StripAction[];
      socketStates: SocketState[];
    };

type UIAction =
  | {
      tag: 'cancel_process';
    }
  | {
      tag: 'confirm_process';
      socketList: StripAction[];
      cancel: () => void;
    }
  | {
      tag: 'confirm_results';
    }
  | {
      tag: 'notify_done';
      success: boolean;
    }
  | {
      tag: 'notify_progress';
      idx: number;
      error: string | undefined;
    };

function reducer(state: State, action: UIAction): State {
  switch (action.tag) {
    case 'cancel_process':
      if (state.tag === 'processing') {
        state.cancel();

        return produce(state, (draft) => {
          draft.tag === 'processing' && (draft.cancelling = true);
        });
      } else if (state.tag === 'done') {
        return { tag: 'selecting' };
      }
      break;
    case 'confirm_process':
      if (state.tag === 'selecting') {
        return {
          tag: 'processing',
          cancel: action.cancel,
          cancelling: false,
          socketList: action.socketList,
          socketStates: Array<string>(action.socketList.length).fill('todo'),
        };
      }
      break;
    case 'confirm_results':
      if (state.tag === 'done') {
        return { tag: 'selecting' };
      }
      break;
    case 'notify_done':
      if (state.tag === 'processing') {
        if (action.success) {
          // completed -- show (varying) success
          return {
            tag: 'done',
            socketList: state.socketList,
            socketStates: state.socketStates,
          };
        } else {
          // cancelled -- go back to selection
          return { tag: 'selecting' };
        }
      }
      break;
    case 'notify_progress':
      if (state.tag === 'processing') {
        return produce(state, (draft) => {
          draft.tag === 'processing' && (draft.socketStates[action.idx] = action.error ?? 'ok');
        });
      }
      break;
  }
  return state;
}

export default function StripSockets() {
  const dispatch = useThunkDispatch();
  const [state, stripDispatch] = useReducer(reducer, { tag: 'selecting' });
  const [selectedSockets, setSelectedSockets] = useState<StripAction[]>([]);
  const query = useSubscription(stripSocketsQuery$);

  const isChoosing = state.tag === 'selecting';

  const onCancel = () => {
    stripDispatch({ tag: 'cancel_process' });
  };

  const onConfirmSockets = useCallback(
    async (selectedSockets: StripAction[]) => {
      if (!isChoosing) {
        return;
      }

      const [cancelToken, cancel] = withCancel();

      stripDispatch({ tag: 'confirm_process', socketList: selectedSockets, cancel });

      try {
        await dispatch(
          doStripSockets(selectedSockets, cancelToken, (idx, error) =>
            stripDispatch({ tag: 'notify_progress', idx, error }),
          ),
        );
        stripDispatch({ tag: 'notify_done', success: true });
      } catch {
        stripDispatch({ tag: 'notify_done', success: false });
      }
    },
    [dispatch, isChoosing],
  );

  if (!query) {
    return null;
  }

  const header = (
    <div>
      <h1>
        {isChoosing ? (
          t('StripSockets.Choose')
        ) : state.tag === 'processing' ? (
          <>
            <span>
              <AppIcon icon={refreshIcon} spinning={true} ariaHidden />
            </span>{' '}
            {t('StripSockets.Running')}
          </>
        ) : (
          t('StripSockets.Done')
        )}
      </h1>
    </div>
  );

  let contents, footer;
  if (state.tag === 'selecting') {
    contents = <StripSocketsChoose query={query} reportSockets={setSelectedSockets} />;

    footer = (
      <button
        type="button"
        className={styles.insertButton}
        onClick={() => onConfirmSockets(selectedSockets)}
        disabled={selectedSockets.length === 0}
      >
        <span>
          <AppIcon icon={faCheckCircle} ariaHidden />{' '}
          {t('StripSockets.Button', { numSockets: selectedSockets.length })}
        </span>
      </button>
    );
  } else {
    // state is processing or done, so show the plug list
    contents = (
      <StripSocketsProcess socketList={state.socketList} socketStates={state.socketStates} />
    );
    if (state.tag === 'processing') {
      footer = (
        <button
          type="button"
          className={styles.insertButton}
          onClick={onCancel}
          disabled={state.cancelling}
        >
          <span>{t('StripSockets.Cancel')}</span>
        </button>
      );
    } else {
      footer = (
        <>
          <button
            type="button"
            className={styles.insertButton}
            onClick={() => stripDispatch({ tag: 'confirm_results' })}
          >
            <span>{t('StripSockets.Ok')}</span>
          </button>
        </>
      );
    }
  }

  return (
    <Sheet
      onClose={() => {
        onCancel();
        stripSocketsQuery$.next(undefined);
      }}
      header={header}
      footer={footer}
      sheetClassName={styles.stripSheet}
    >
      {contents}
    </Sheet>
  );
}

function StripSocketsProcess({
  socketList,
  socketStates,
}: {
  socketList: StripAction[];
  socketStates: SocketState[];
}) {
  return (
    <div className={styles.iconList}>
      {socketList.map((socket, idx) => {
        const state = socketStates[idx];
        const icon = (
          <div onClick={() => locateItem(socket.item)}>
            <DefItemIcon itemDef={socket.plugItemDef} />
          </div>
        );
        const failed = state !== 'ok' && state !== 'todo';
        const key = `${socket.item.index}-${socket.socketIndex}`;
        const className = clsx('item', styles.plug, {
          [styles.ok]: state === 'ok',
          [styles.failed]: failed,
        });
        return failed ? (
          <PressTip minimal key={key} className={className} tooltip={state}>
            {icon}
          </PressTip>
        ) : (
          <div key={key} className={className}>
            {icon}
          </div>
        );
      })}
    </div>
  );
}

function StripSocketsChoose({
  query,
  reportSockets,
}: {
  query: string;
  reportSockets: (sockets: StripAction[]) => void;
}) {
  const defs = useD2Definitions()!;
  const destiny2CoreSettings = useSelector(destiny2CoreSettingsSelector)!;
  const allItems = useSelector(allItemsSelector);
  const filterFactory = useSelector(filterFactorySelector);
  const [activeKinds, setActiveKinds] = useState<SocketKind[]>([]);

  const socketKinds = useMemo(() => {
    if (!query) {
      return null;
    }

    const filterFunc = filterFactory(query);
    const filteredItems = allItems.filter((i) => i.sockets && filterFunc(i));
    return collectSocketsToStrip(filteredItems, destiny2CoreSettings, defs);
  }, [allItems, defs, destiny2CoreSettings, filterFactory, query]);

  useEffect(() => {
    reportSockets(
      socketKinds
        ?.filter((k) => k.items && activeKinds.includes(k.kind))
        .flatMap((k) => k.items!) || [],
    );
  }, [reportSockets, socketKinds, activeKinds]);

  return (
    socketKinds && (
      <>
        {socketKinds.length ? (
          socketKinds.map((entry) => {
            const itemCats = [
              { icon: handCannonIcon, num: entry.numWeapons },
              { icon: chestArmorItem, num: entry.numArmor },
              { icon: ghostIcon, num: entry.numOthers },
            ];
            return (
              <SocketKindButton
                key={entry.kind}
                name={t(entry.name, { count: entry.numApplicableSockets })}
                representativeDef={entry.representativePlug}
                itemCategories={itemCats}
                selected={activeKinds.includes(entry.kind)}
                onClick={() => {
                  if (activeKinds.includes(entry.kind)) {
                    setActiveKinds(activeKinds.filter((k) => k !== entry.kind));
                  } else {
                    setActiveKinds([...activeKinds, entry.kind]);
                  }
                }}
              />
            );
          })
        ) : (
          <div className={styles.noSocketsMessage}>{t('StripSockets.NoSockets')}</div>
        )}
      </>
    )
  );
}

function SocketKindButton({
  name,
  representativeDef,
  itemCategories,
  selected,
  onClick,
}: {
  name: string;
  representativeDef: DestinyInventoryItemDefinition;
  itemCategories: { icon: string; num: number }[];
  selected: boolean;
  onClick: () => void;
}) {
  return (
    <div
      className={clsx(styles.socketKindButton, {
        [styles.selectedButton]: selected,
      })}
      onClick={onClick}
      role="button"
      tabIndex={0}
    >
      <div className="item" title={name}>
        <DefItemIcon itemDef={representativeDef} />
      </div>
      <div className={styles.buttonInfo}>
        <div
          className={clsx(styles.buttonTitle, {
            [styles.selectedTitle]: selected,
          })}
        >
          {name}
        </div>
      </div>
      <div>
        {itemCategories.map(
          ({ icon, num }, idx) =>
            num > 0 && (
              <React.Fragment key={idx}>
                <img src={icon} className={styles.itemTypeIcon} /> {num}
                <br />
              </React.Fragment>
            ),
        )}
      </div>
    </div>
  );
}

import { ReactElement, ReactNode, cloneElement } from 'react';

/** places a divider between each element of arr */
export function addDividers<T extends React.ReactNode>(
  arr: T[],
  divider: ReactElement,
): ReactNode[] {
  // eslint-disable-next-line @eslint-react/no-clone-element
  return arr.flatMap((e, i) => [i ? cloneElement(divider, { key: `divider-${i}` }) : null, e]);
}

