interface ActionButtonProps {
  item: DimItem;
  label?: boolean;
}

export function CompareActionButton({ item, label }: ActionButtonProps) {
  const dispatch = useDispatch();
  const allItems = useSelector(allItemsSelector);

  // Calculate duplicate count
  const dupeCount = useMemo(() => {
    if (!item.comparable) {
      return 1;
    }

    // For exotic armor, use perks to determine true duplicates
    // For other items, use the standard dupe ID
    let count = 0;

    if (item.bucket.inArmor && item.isExotic && item.sockets?.allSockets) {
      // For exotic armor, count items with same hash and same perk combination
      const itemPerkHashes = item.sockets.allSockets
        .filter((socket) => socket.isPerk && socket.plugged?.plugDef.hash)
        .map((socket) => socket.plugged!.plugDef.hash)
        .sort((a, b) => a - b);

      for (const i of allItems) {
        if (!i.comparable || i.hash !== item.hash) {
          continue;
        }

        if (i.bucket.inArmor && i.isExotic && i.sockets?.allSockets) {
          const otherPerkHashes = i.sockets.allSockets
            .filter((socket) => socket.isPerk && socket.plugged?.plugDef.hash)
            .map((socket) => socket.plugged!.plugDef.hash)
            .sort((a, b) => a - b);

          // Check if perk arrays are equal
          if (
            itemPerkHashes.length === otherPerkHashes.length &&
            itemPerkHashes.every((hash, index) => hash === otherPerkHashes[index])
          ) {
            count++;
          }
        }
      }
    } else {
      // Standard duplicate counting for non-exotic armor and all other items
      const dupeID = makeDupeID(item);

      for (const i of allItems) {
        if (i.comparable && makeDupeID(i) === dupeID) {
          count++;
        }
      }
    }

    return count;
  }, [allItems, item]);

  const openCompare = () => {
    hideItemPopup();
    dispatch(addCompareItem(item));
  };

  if (!item.comparable) {
    return null;
  }

  return (
    <ActionButton onClick={openCompare} hotkey="c" hotkeyDescription={t('Compare.ButtonHelp')}>
      <span className={styles.dupeCount}>{dupeCount}</span>
      <AppIcon icon={compareIcon} />
      {label && <span className={styles.label}>{t('Compare.Button')}</span>}
    </ActionButton>
  );
}

export function LockActionButton({
  item,
  label,
  noHotkey,
}: ActionButtonProps & { noHotkey?: boolean }) {
  const autoLockTagged = useSelector(settingSelector('autoLockTagged'));
  const tag = useSelector(tagSelector(item));

  if (!item.lockable && !item.trackable) {
    return null;
  }

  const disabled = autoLockTagged && tag !== undefined && canSyncLockState(item);

  const type = item.lockable ? 'lock' : 'track';
  // Let's keep these translations around?
  // t('MovePopup.FavoriteUnFavorite.Favorited')
  // t('MovePopup.FavoriteUnFavorite.Unfavorited')
  const title =
    type === 'lock'
      ? item.locked
        ? t('MovePopup.LockUnlock.Locked')
        : t('MovePopup.LockUnlock.Unlocked')
      : item.tracked
        ? t('MovePopup.TrackUntrack.Tracked')
        : t('MovePopup.TrackUntrack.Untracked');

  return (
    <LockButton item={item} type={type} disabled={disabled} noHotkey={noHotkey}>
      {label && <span className={styles.label}>{title}</span>}
    </LockButton>
  );
}

export function TagActionButton({
  item,
  label,
  hideKeys,
}: ActionButtonProps & { hideKeys?: boolean }) {
  if (!item.taggable) {
