import { installPrompt$ } from './app-install';
import { AppIcon, searchIcon } from './icons';
import { useIsPhonePortrait } from './selectors';

const menuAnimateVariants: Variants = {
  open: { x: 0 },
  collapsed: { x: -250 },
};
const menuAnimateTransition: Transition<number> = { type: 'spring', duration: 0.3, bounce: 0 };

// TODO: finally time to hack apart the header styles!

export default function Header() {
  const dispatch = useThunkDispatch();
  const isPhonePortrait = useIsPhonePortrait();
  const account = useSelector(currentAccountSelector);

  // Hamburger menu
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const dropdownToggler = useRef<HTMLButtonElement>(null);
  const toggleDropdown = useCallback((e: React.MouseEvent | KeyboardEvent) => {
    e.preventDefault();
    setDropdownOpen((dropdownOpen) => !dropdownOpen);
  }, []);

  const hideDropdown = useCallback(() => {
    setDropdownOpen(false);
  }, []);

  // Mobile search bar
  const [showSearch, setShowSearch] = useState(false);
  const toggleSearch = () => setShowSearch((showSearch) => !showSearch);
  const hideSearch = useCallback(() => {
    if (showSearch) {
      setShowSearch(false);
    }
  }, [showSearch]);

  // Install D2L as a PWA
  const [promptIosPwa, setPromptIosPwa] = useState(false);
  const installPromptEvent = useSubscription(installPrompt$);

  const showInstallPrompt = () => {
    setPromptIosPwa(true);
    setDropdownOpen(false);
  };

  const installDim = () => {
    if (installPromptEvent) {
      installPromptEvent.prompt();
      installPromptEvent.userChoice.then((choiceResult) => {
        if (choiceResult.outcome === 'accepted') {
          infoLog('install', 'User installed D2 Locker to desktop/home screen');
        } else {
          infoLog('install', 'User dismissed the install prompt');
        }
        installPrompt$.next(undefined);
      });
    } else {
      showInstallPrompt();
    }
  };

  // Is this running as an installed app?
  const isStandalone =
    window.navigator.standalone === true || window.matchMedia('(display-mode: standalone)').matches;

  const iosPwaAvailable = isiOSBrowser() && !isStandalone;

  const installable = installPromptEvent || iosPwaAvailable;

  // the choice to relaunch in a no-tabs, less-UI window

  // Search filter
  const searchFilter = useRef<SearchFilterRef>(null);

  // Clear filter and close dropdown on path change
  const { pathname } = useLocation();
  useEffect(() => {
    setDropdownOpen(false);
    dispatch(setSearchQuery(''));
  }, [dispatch, pathname]);

  // Focus search when shown
  useEffect(() => {
    if (searchFilter.current && showSearch) {
      searchFilter.current.focusFilterInput();
    }
  }, [showSearch]);

  const dropdownRef = useRef<HTMLDivElement>(null);

  const navLinkClassName = ({ isActive }: { isActive: boolean }) =>
    clsx(styles.menuItem, { [styles.active]: isActive });

  // Generic links about D2L

  let links: {
    to: string;
    text: string;
    badge?: React.ReactNode;
  }[] = [];
  if (account) {
    const path = accountRoute(account);
    links = compact([
      {
        to: `${path}/inventory`,
        text: t('Header.Inventory'),
      },
      {
        to: `${path}/vendors`,
        text: t('Vendors.Vendors'),
      },
      {
        to: `${path}/loadouts`,
        text: t('Loadouts.Loadouts'),
      },
      account.destinyVersion === 2 && {
        to: `${path}/armory-search`,
        text: 'Weapon Database',
      },
    ]);
  }

  const linkNodes = links.map((link) => (
    <NavLink className={navLinkClassName} key={link.to} to={link.to}>
      {link.badge}
      {link.text}
    </NavLink>
  ));

  // Links about the current Destiny version
  const destinyLinks = linkNodes;

  const hotkeys = useMemo(() => {
    const hotkeys: Hotkey[] = [
      {
        combo: 'm',
        description: t('Hotkey.Menu'),
        callback: toggleDropdown,
      },
      {
        combo: 'f',
        description: t('Hotkey.StartSearch'),
        callback: (event) => {
          if (searchFilter.current) {
            searchFilter.current.focusFilterInput();
            if (isPhonePortrait) {
              setShowSearch(true);
            }
          }
          event.preventDefault();
          event.stopPropagation();
        },
      },
      {
        combo: 'shift+f',
        description: t('Hotkey.StartSearchClear'),
        callback: (event) => {
          if (searchFilter.current) {
            searchFilter.current.clearFilter();
            searchFilter.current.focusFilterInput();
            if (isPhonePortrait) {
              setShowSearch(true);
            }
          }
          event.preventDefault();
          event.stopPropagation();
        },
      },
    ];
    return hotkeys;
  }, [isPhonePortrait, toggleDropdown]);
  useHotkeys(hotkeys);

  const showKeyboardHelp = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    showCheatSheet$.next(true);
    setDropdownOpen(false);
  };

  // Calculate the true height of the header, for use in other things
  const headerRef = useRef<HTMLDivElement>(null);
  useSetCSSVarToHeight(headerRef, '--header-height');

  const headerLinksRef = useRef<HTMLDivElement>(null);

  const streamDeckEnabled = $featureFlags.elgatoStreamDeck
    ? // eslint-disable-next-line react-hooks/rules-of-hooks
      useSelector(streamDeckEnabledSelector)
    : false;

  return (
    <PressTipRoot value={headerRef}>
      <header className={styles.container} ref={headerRef}>
        <div className={styles.header}>
          <button
            type="button"
            className={clsx(styles.menuItem, styles.menu)}
            ref={dropdownToggler}
            onClick={toggleDropdown}
            aria-haspopup="menu"
            aria-label={t('Header.Menu')}
            aria-expanded={dropdownOpen}
          >
            <img src="/android-chrome-192x192-6-2018.png" width="24" height="24" alt="Menu" />
            <MenuBadge />
          </button>
          {account && (
            <span style={{ marginLeft: '8px', fontSize: '14px', fontWeight: 'bold' }}>
              Yo {account.displayName}!
            </span>
          )}
          <AnimatePresence>
            {dropdownOpen && (
              <motion.div
                key="dropdown"
                className={styles.dropdown}
                role="menu"
                initial="collapsed"
                animate="open"
                exit="collapsed"
                variants={menuAnimateVariants}
                transition={menuAnimateTransition}
              >
                <ClickOutside
                  ref={dropdownRef}
                  extraRef={dropdownToggler}
                  onClickOutside={hideDropdown}
                >
                  {destinyLinks}
                  <hr />
                  <NavLink className={navLinkClassName} to="/settings">
                    {t('Settings.Settings')}
                  </NavLink>
                  {installable && (
                    <a className={styles.menuItem} onClick={installDim}>
                      Install D2Locker as an App
                    </a>
                  )}
                  {!isPhonePortrait && (
                    <a className={styles.menuItem} onClick={showKeyboardHelp}>
                      {t('Header.KeyboardShortcuts')}
                    </a>
                  )}
                </ClickOutside>
              </motion.div>
            )}
          </AnimatePresence>
          <div className={styles.headerLinks} ref={headerLinksRef}>
            {destinyLinks}
          </div>
          <div className={styles.headerRight}>
            {account && !isPhonePortrait && (
              <span className={styles.searchLink}>
                <SearchFilter onClear={hideSearch} ref={searchFilter} />
              </span>
            )}
            {streamDeckEnabled && <StreamDeckButton />}
            <RefreshButton className={styles.menuItem} />
            <button
              type="button"
              className={clsx(styles.menuItem, styles.searchButton)}
              onClick={toggleSearch}
            >
              <AppIcon icon={searchIcon} />
            </button>
          </div>
        </div>
        {account && isPhonePortrait && showSearch && (
          <span className="mobile-search-link">
            <SearchFilter onClear={hideSearch} ref={searchFilter} />
          </span>
        )}
        <PostmasterWarningBanner />
        {$featureFlags.warnNoSync && <DimApiWarningBanner />}
        {promptIosPwa && (
          <Sheet header={<h1>{t('Header.InstallD2L')}</h1>} onClose={() => setPromptIosPwa(false)}>
            <p className={styles.pwaPrompt}>{t('Header.IosPwaPrompt')}</p>
          </Sheet>
        )}
      </header>
    </PressTipRoot>
  );
}

import { useThunkDispatch } from 'app/store/thunk-dispatch';
import { useEventBusListener } from 'app/utils/hooks';
import { GlobalAlertLevelsToToastLevels } from 'app/whats-new/BungieAlerts';
import clsx from 'clsx';
import { useCallback, useEffect } from 'react';
import { useSelector } from 'react-redux';
import styles from './MenuBadge.m.scss';
import { pollForBungieAlerts } from './alerts';
import { refresh$ } from './refresh-events';
import { bungieAlertsSelector } from './selectors';

/**
 * A badge for the hamburger menu - must be kept in sync with WhatsNewLink, but may also incorporate other sources.
 *
 * Using inheritance to keep better in sync with WhatsNewLink.
 */
export default function MenuBadge() {
  // TODO: Incorporate settings/storage (e.g. d2l sync disabled/busted)
  const alerts = useSelector(bungieAlertsSelector);
  const dispatch = useThunkDispatch();

  const getAlerts = useCallback(() => dispatch(pollForBungieAlerts()), [dispatch]);
  useEventBusListener(refresh$, getAlerts);
  useEffect(() => {
    getAlerts();
  }, [getAlerts]);

  // Auto-reload updates are handled automatically - no UI indicator needed

  if (alerts.length) {
    return (
      <span
        className={clsx(
          styles.badgeNew,
          `bungie-alert-${GlobalAlertLevelsToToastLevels[alerts[0].AlertLevel]}`,
        )}
      />
    );
  }

  return null;
}

import { errorLog, infoLog } from './log';

const TAG = 'VERSION_CACHE';
const VERSION_KEY = 'app-version';
const LAST_CACHE_CLEAR_KEY = 'last-cache-clear';

/**
 * Manages cache clearing when the app version changes.
 * This ensures users get fresh data after updates.
 */
export class VersionCacheManager {
  private currentVersion: string;

  constructor(version: string) {
    this.currentVersion = version;
  }

  /**
   * Check if this is a new version and clear caches if needed
   */
  async checkAndClearIfNeeded(): Promise<boolean> {
    try {
      const storedVersion = localStorage.getItem(VERSION_KEY);

      if (!storedVersion) {
        // First time or no version stored - just save current version
        localStorage.setItem(VERSION_KEY, this.currentVersion);
        infoLog(TAG, 'First app load, storing version:', this.currentVersion);
        return false;
      }

      if (storedVersion !== this.currentVersion) {
        infoLog(
          TAG,
          `Version changed from ${storedVersion} to ${this.currentVersion} - clearing caches`,
        );
        await this.clearAllCaches();
        localStorage.setItem(VERSION_KEY, this.currentVersion);
        localStorage.setItem(LAST_CACHE_CLEAR_KEY, Date.now().toString());
        return true;
      }

      return false;
    } catch (error) {
      errorLog(TAG, 'Error checking version:', error);
      return false;
    }
  }

  /**
   * Force clear all caches and storage
   */
  static async clearAllCaches(): Promise<void> {
    try {
      // 1. Clear browser caches
      if ('caches' in window) {
        const cacheNames = await caches.keys();
        await Promise.all(
          cacheNames.map((cacheName) => {
            infoLog(TAG, 'Clearing cache:', cacheName);
            return caches.delete(cacheName);
          }),
        );
      }

      // 2. Clear localStorage (except essential items)
      VersionCacheManager.clearLocalStorage();

      // 3. Clear sessionStorage
      try {
        sessionStorage.clear();
        infoLog(TAG, 'Cleared sessionStorage');
      } catch (e) {
        errorLog(TAG, 'Failed to clear sessionStorage:', e);
      }

      // 4. Clear IndexedDB (D2L's offline data)
      await VersionCacheManager.clearIndexedDB();

      infoLog(TAG, 'All caches cleared successfully');
    } catch (error) {
      errorLog(TAG, 'Error clearing caches:', error);
      throw error;
    }
  }

  /**
   * Clear localStorage but preserve essential items
   */
  private static clearLocalStorage(): void {
    try {
      // Items to preserve during cache clear
      const preserveKeys = [
        VERSION_KEY,
        LAST_CACHE_CLEAR_KEY,
        'authorizationState', // OAuth state
        'd2l-api-enabled', // D2LSync setting
        'settings', // User preferences
        'language', // Language preference
      ];

      const keysToRemove: string[] = [];

      // Collect keys to remove
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        if (key && !preserveKeys.includes(key)) {
          keysToRemove.push(key);
        }
      }

      // Remove non-essential keys
      for (const key of keysToRemove) {
        localStorage.removeItem(key);
      }

      infoLog(
        TAG,
        `Cleared ${keysToRemove.length} localStorage items, preserved ${preserveKeys.length} essential items`,
      );
    } catch (error) {
      errorLog(TAG, 'Error clearing localStorage:', error);
    }
  }

  /**
   * Clear IndexedDB databases
   */
  private static async clearIndexedDB(): Promise<void> {
    try {
      if ('indexedDB' in window) {
        // D2L typically uses 'keyval-store' and potentially others
        const dbsToDelete = ['keyval-store', 'D2L', 'd2l-data'];

        return await Promise.all(
          dbsToDelete.map(async (dbName) => {
            try {
              // Check if database exists first
              const deleteReq = indexedDB.deleteDatabase(dbName);
              return await new Promise<void>((resolve) => {
                deleteReq.onsuccess = () => {
                  infoLog(TAG, `Cleared IndexedDB: ${dbName}`);
                  resolve();
                };
                deleteReq.onerror = () => resolve(); // Don't fail if DB doesn't exist
                deleteReq.onblocked = () => {
                  errorLog(TAG, `IndexedDB delete blocked for: ${dbName}`);
                  resolve(); // Continue anyway
                };
              });
            } catch {
              // Database might not exist, that's ok
              return Promise.resolve();
            }
          }),
        );
      }
    } catch (error) {
      errorLog(TAG, 'Error clearing IndexedDB:', error);
    }
  }

  /**
   * Get when caches were last cleared
   */
  static getLastCacheClear(): Date | null {
    try {
      const timestamp = localStorage.getItem(LAST_CACHE_CLEAR_KEY);
      return timestamp ? new Date(parseInt(timestamp, 10)) : null;
    } catch {
      return null;
    }
  }
}

// Export singleton instance
export const versionCacheManager = new VersionCacheManager($D2L_VERSION);

/* eslint prefer-template: 0 */
import parser from 'ua-parser-js';
import {
  samsungInternet,
  steamBrowser,
  supportedLanguages,
  unsupported,
} from './browsercheck-utils.js';

// Adapted from 'is-browser-supported' npm package. Separate from index.js so it'll run even if that fails.
// This is also intentionally written in es5 and not TypeScript because it should not use any new features.

/**
 * @param {parser.IResult} agent
 */
function getBrowserName(agent) {
  if (agent.browser.name === 'Chrome' && agent.os.name === 'Android') {
    return 'and_chr';
  } else if (agent.browser.name === 'Firefox' && agent.os.name === 'Android') {
    return 'and_ff';
  } else if (agent.browser.name === 'Mobile Safari' || agent.os.name === 'iOS') {
    return 'ios_saf';
  } else if (agent.browser.name === 'Chromium') {
    return 'chrome';
  } else if (agent.browser.name === 'Opera') {
    return 'opera';
  }
  return agent.browser.name;
}

/**
 * @returns {import('app/i18n.js').DimLanguage}
 */
function getUserLocale() {
  var lang = window.navigator.language.toLowerCase() || 'en';
  if (lang.startsWith('zh-') && lang.length === 5) {
    lang = lang === 'zh-cn' ? 'zh-chs' : 'zh-cht';
  }
  if (!supportedLanguages.includes(lang)) {
    lang = lang.split('-', 1)[0];
  }
  if (!supportedLanguages.includes(lang)) {
    // fallback to 'en' if unsupported language after removing dialect
    lang = 'en';
  }
  return lang;
}

/**
 * @param {parser.IResult} agent
 */
function getBrowserVersionFromUserAgent(agent) {
  var browserName = getBrowserName(agent).toLowerCase();
  var version = (
    browserName === 'ios_saf' ? agent.os.version : agent.browser.version || agent.os.version || ''
  ).split('.');
  while (version.length > 0) {
    try {
      return browserName + ' ' + version.join('.');
    } catch {
      // Ignore unknown browser query error
    }
    version.pop();
  }
  return 'unknown';
}

/**
 * @param {string[]} browsersSupported
 * @param {string} userAgent
 */
export function isSupported(browsersSupported, userAgent) {
  if (navigator.standalone) {
    // Assume support if we're installed as an iOS PWA.
    return true;
  }

  var agent = parser(userAgent);

  // Build a map from browser version to minimum supported version
  /** @type {Record<string, number|undefined>} */
  var minBrowserVersions = {};
  // eslint-disable-next-line @typescript-eslint/prefer-for-of
  for (var i = 0; i < browsersSupported.length; i++) {
    // ios_saf 11.0-11.2 => [ios_saf, 11.0, 11.2]
    var supportedBrowserVersion = browsersSupported[i].split(/[- ]/);
    minBrowserVersions[supportedBrowserVersion[0]] = Math.min(
      minBrowserVersions[supportedBrowserVersion[0]] || 999999,
      parseFloat(supportedBrowserVersion[1]),
    );
  }

  /** @param {string} browser  */
  function isBrowserSupported(browser) {
    var nameAndVersion = browser.split(' ');
    return (
      minBrowserVersions[nameAndVersion[0]] &&
      minBrowserVersions[nameAndVersion[0]] <= parseFloat(nameAndVersion[1])
    );
  }

  var browser = getBrowserVersionFromUserAgent(agent);
  var supported = isBrowserSupported(browser);

  if (!supported) {
    // Detect anything based on chrome as if it were chrome
    var chromeMatch = /Chrome\/(\d+)/.exec(agent.ua);
    if (chromeMatch) {
      browser = 'chrome ' + chromeMatch[1];
      supported = isBrowserSupported(browser);
    }
  }
  if (!supported) {
    console.warn(
      'Browser ' + browser + ' might not be supported.',
      // browsersSupported,
    );
  }
  return supported;
}

var lang = getUserLocale();

if ($BROWSERS.length && lang) {
  var supported = isSupported($BROWSERS, navigator.userAgent);
  if (!supported) {
    // t(`Browsercheck.Unsupported`)
    document.getElementById('browser-warning').textContent = unsupported[lang];
    document.getElementById('browser-warning').style.display = 'block';
  }

  // Steam is never supported
  if (navigator.userAgent.includes('Steam')) {
    // https://shirezaks.com/Figuring-out-why-D2L-doesn't-work-in-Steam
    // t(`Browsercheck.Steam`)
    document.getElementById('browser-warning').textContent = steamBrowser[lang];
    document.getElementById('browser-warning').style.display = 'block';
  }

  // Samsung Internet is not supported because of its weird forced dark mode
  if (
    navigator.userAgent.includes('SamsungBrowser') &&
    // When the "Labs" setting to respect websites' dark mode capabilities is
    // enabled, Samsung Internet will actually set prefers-color-scheme to dark.
    // Otherwise, it's always "light". This *could* be a user who actually
    // prefers a light theme - there's no way to tell.
    window.matchMedia('(prefers-color-scheme: light)').matches
  ) {
    // t(`Browsercheck.Samsung`)
    document.getElementById('browser-warning').textContent = samsungInternet[lang];
    document.getElementById('browser-warning').style.display = 'block';
  }
}

// eslint-disable-next-line spaced-comment
/// <reference lib="webworker" />

import { CacheableResponsePlugin } from 'workbox-cacheable-response';
import { clientsClaim } from 'workbox-core';
import { ExpirationPlugin } from 'workbox-expiration';
import {
  cleanupOutdatedCaches,
  createHandlerBoundToURL,
  precacheAndRoute,
} from 'workbox-precaching';
import { NavigationRoute, registerRoute } from 'workbox-routing';
import { CacheFirst } from 'workbox-strategies';

// Explicitly type `self` as ServiceWorkerGlobalScope for correct type inference
const globalWorker = self as unknown as ServiceWorkerGlobalScope;

// --- Declare the global variables injected by Workbox ---
declare global {
  interface ServiceWorkerGlobalScope {
    __WB_MANIFEST: any[];
    __precacheManifest: any[];
  }
}

// Use globalWorker.__WB_MANIFEST which is injected by Workbox plugin
// The next line contains self.__WB_MANIFEST for Workbox plugin detection
// @ts-expect-error - self.__WB_MANIFEST is injected by Workbox at build time
precacheAndRoute(globalWorker.__WB_MANIFEST || self.__WB_MANIFEST, {});
cleanupOutdatedCaches();

// Once this activates, start handling requests through the service worker immediately.
// No need to wait for a refresh.
clientsClaim();

registerRoute(
  /https:\/\/fonts\.(googleapis|gstatic)\.com\/.*/,
  new CacheFirst({
    cacheName: 'googleapis',
    plugins: [
      new ExpirationPlugin({ maxEntries: 20, purgeOnQuotaError: false }),
      new CacheableResponsePlugin({ statuses: [0, 200] }),
    ],
  }),
  'GET',
);

// Since we're a single page app, route all navigation to /index.html
const handler = createHandlerBoundToURL(`${$PUBLIC_PATH}index.html`);
const navigationRoute = new NavigationRoute(handler, {
  // These have their own pages (return.html)
  // This regex matches on query string too, so no anchors!
  denylist: [
    /return\.html/,
    /backup\.html/,
    /\.well-known/,
    /\.(php|json|wasm|js|css|png|jpg|map)(\.(gz|br))?$/,
    /\/data\/d1\/manifests\//,
  ],
});
registerRoute(navigationRoute);

// Skip waiting automatically when a new service worker is installed
self.addEventListener('install', () => {
  globalWorker.skipWaiting();
});

self.addEventListener('message', (event) => {
  if (!event.data) {
    return;
  }

  switch (event.data) {
    case 'skipWaiting':
      globalWorker.skipWaiting();
      break;
    default:
      // NOOP
      break;
  }
});
